css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mathworld.wolfram.com") {

  body {
    background: #111 !important;
  }
  
  input, select, textarea {
    background-color: #333 !important;
    color: #ccc !important;
    filter: invert(0) !important;
    -webkit-filter: invert(0) !important;
  }
  
  .leftcolumn {
    filter: invert(1);
    -webkit-filter: invert(1);
  }
  
  #logo > img,
  div#sidebar .wawidget,
  div#sidebar .demoWrapper >a > img {
   filter: invert(1);
   -webkit-filter: invert(1);
  }

  .wawidgetWrapper,
  div#sidebar .demoWrapper,
  div#sidebar .alphaWrapper,
  div#resourcesContent {
    background: #000 !important;
    box-shadow:  0 0 2px #000 !important;
    border: 1px solid #000 !important;
  }
  
  /* Main content */
  
  div#mainContent {
    font-size: 125%;
    background: #000 !important;
    color: #ccc !important;
    box-shadow:  0 0 2px #000 !important;
    border: 1px solid #000 !important;
  }
 
  div#mainContent img {
    background-color: #fff;
    border-radius: 5px;
    padding: 5px;
    filter: invert(1);
    -webkit-filter: invert(1);
    opacity: 0.7;
  }

  div#mainContent img:hover {
    opacity: 1.0;
    filter: invert(0);
    -webkit-filter: invert(0);
    z-index: 1;
  }
  
  .displayformula:hover,
  .numberedequation:hover,
  .inlineformula:hover {
    transform:scale(1.25);
    
    /* 
    image-rendering: -moz-crisp-edges;
    image-rendering:   -o-crisp-edges;
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
    */
  }
  
}
