css:
@-moz-document domain("rainbowturtletranslation.com"), domain("jetpack.wordpress.com") {
.site {
    background-color: rgba(0, 0, 0, 0) !important;
}

.entry-content p, .entry-summary p, .comment-content p, .mu_register p {
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    margin: 0 0 24px;
    margin: 0 0 1.714285714rem;
    line-height: 1.714285714;
    color: #ADA5A5;
}

.entry-content a:visited, 
.comment-content a:visited {
        color: #E70C0C;
}

.widget-area .widget p, .widget-area .widget li, .widget-area .widget .textwidget {
    font-size: 13px;
    font-size: 0.928571rem;
    line-height: 1.846153846;
    color: #0EAEE9;
}

.widget-area .widget h3 {
    color: #21759B;
    font-family: times New Roman;
    font-size: 18px;
    margin-bottom: 1.71429rem;
}


.widget-area .widget a:visited {
        color: #E72900;
}

.widget-area .widget a:hover, .comments-link a:hover, .entry-meta a:hover, .template-home .widget-area .widget li a:hover, .main-navigation a:hover, .main-navigation li a:hover, footer[role="contentinfo"] a:hover, a.comment-reply-link:hover {
        color: darkgoldenrod !important;
}

.widget-area .widget a, .comments-link a, .entry-meta a, .template-home .widget-area .widget li a {
        color: #32EC0E;
}

footer.entry-meta {
    /* margin-top: 24px; */
    margin-top: 1.714285714rem;
    /* font-size: 13px; */
    font-size: 0.928571429rem;
    line-height: 1.846153846;
    /* color: #757575; */
    color: #1EA0CA;
}

html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    vertical-align: baseline;
    color: #68c9e8;
}

.entry-content td, .comment-content td {
    border-top: 1px solid #ededed;
    padding: 6px 10px 6px 0;
    background-color: #111;
}

input[type="text"], input[type="password"], input[type="email"], input[type="url"], textarea {
    background-color: rgba(68, 61, 61, 0.48) !important;
    color: #2354FF;
}

.widget-area #s {
    width: 53.66666666666%;
    background-color: #000;
}

img.header-image {
    border-radius: 10px!important;
    display: block;
  margin-left: auto;
  margin-right: auto;
}

ul.nav-menu {
    border-bottom: 2px solid #ededed!important;
    border-top: 2px solid #ededed!important;
    display: inline-block !important;
    text-align: center!important;
    width: 100%!important;
    border-style: outset !Important;
    background: -webkit-linear-gradient(left,red,orange,yellow,green,blue,indigo,violet)!important;
    border-radius:10px!important;
}

#page {
    background-color: rgba(0, 0, 0, 0) !important;
    background: rgba(0, 0, 0, 0.84) !important;
    border-radius: 20px!important;
}


#s {
    margin-top: 100px;
}

#subscribe-submit input, input#submit.submit, input#searchsubmit {
    background: black !important;
    color: Red !important;
    border-style: outset !Important;
  }

#subscribe-submit input:hover, input#submit.submit:hover, input#searchsubmit:hover {
    color: goldenrod !Important;
    transform: translateY(4px)!Important;
}

div#HTML3 div:nth-child(n) > div {
    background: -webkit-linear-gradient(left,red,orange,yellow,green,blue,indigo,violet)!important;
    color: #000000 !important;
    font-weight: bolder;
    }

div.widget-content div {
    background-color: rgba(0, 0, 0, 0.0) !important;
}
    
h1 a, h1.entry-title {
    color: #004cff!important;
}

h1 a:hover, a.url:hover {
    color: goldenrod !important;
}

a.sharing-anchor.sd-button.share-more, div.inner, a.share-facebook.sd-button.share-icon, a.share-google-plus-1.sd-button.share-icon, a.share-tumblr.sd-button.share-icon, a.share-twitter.sd-button.share-icon {
    background-color: black !important;
}

like.sd-button {
    color: #68c9e8!important;
    background: #000000!important;
   }

#respond #comment-form-comment, #comment-form-share-text-padder, #respond .comment-form-service, #respond.js #comment-form-identity #comment-form-nascar, #respond .comment-form-fields div.comment-form-input.active, #respond .comment-form-fields div.comment-form-input {
    background: rgba(0, 0, 0, 0.54);
    font-weight: bold !important;
    }

#respond #comment-form-identity #comment-form-nascar p {
    color: rgba(249, 35, 35, 0.84);
}

#respond .comment-form-fields div.comment-form-input input {
    color: rgb(173, 165, 165);
    }

#respond p.comment-subscription-form label, #respond label, #respond form label {
    color: goldenrod !important;
}

#respond label, #respond form label {
    color: #daa519!important;
}

#respond .comment-form-fields label {
    color: #ada5a5 !important;
}

#comment-form-comment.active {
        background: rgba(0, 0, 0, 0.54) !important;
}

#commentform p input#comment-submit.submit {
    background: black !important;
    color: #68c9e8 !Important; 
    text-shadow: none !important;
}

#commentform p input#comment-submit.submit:hover {
    transform: translateY(4px)!Important;
    text-shadow: none !important;
}

textarea#comment {
    background: rgba(82, 79, 79, 0.54) !important;
    text-shadow: none !important;
}

#respond #comment-form-comment textarea, #comment-form-share-text-padder textarea {
    background: rgba(82, 79, 79, 0.54) !important;
    color: #9b9ba0;
    text-shadow: 0 0px 0 rgba( 255, 255, 255, 0.45 );
        }

p span {
    color: transparent !important;
}

.main-navigation li ul li a {
    background: rgba(0, 0, 0, 0.84) !important;
    color: #b90e0e !important;
    font-weight: bold !important;
    border-bottom-color: darkred;
}
.site-content article, footer[role="contentinfo"] {
    border-image: -webkit-linear-gradient(left, red,orange,yellow,green,blue,indigo,violet) 1%;
}

input#comment-submit.submit {
    border: 1px solid #580404 !important;
}

.menu-item-11831 a, .menu-item-11828 a, .menu-item-11829 a, .menu-item-11830 a, .menu-item-12276 a, .menu-item-12276 a, .menu-item-12277 a {
    color: black !important;    
    font-weight: bold !important;
}
    
a {
    color: #0ac50a !important;
    font-weight: bold !important;
}

a:visited {
    color: rgb(115, 19, 19) !important;
}

a:hover {
    color: goldenrod !important;
}

.bypostauthor cite span {
    color: #fff !important;
    background-color: #21759b;
    }

.collapseomatic.colomat-visited, .collapseomatic, article#post-2547 p:nth-child(n) > strong:nth-child(n) {
    color: #ada5a5 !important;
}



html{background:url(http://i.imgur.com/ypzDMGB.jpg) no-repeat center fixed;background-size:cover;-o-background-size:cover;-webkit-background-size:cover !important;
}

body.custom-background {
    background: none transparent !important;
}
}

@-moz-document domain("rainbowturtletranslation.com"), domain("widgets.wp.com") {
body::-webkit-scrollbar {
    width: 0.5em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px dimgray;
}
 
body::-webkit-scrollbar-thumb {
  background-color: darkred !important;
  outline: 1px solid slategray;
}

::-webkit-scrollbar-button {
      background-color: dimgray;
 color: black;   
}

::-moz-selection {
    background-color: #000000 !important;
    color: #c71e1e !important;
}

/* Works in Safari */

::selection {
    background-color: #000000 !important;
    color: #c71e1e !important;
}

.wpl-button a {
    color: #68c9e8!important;
    background: #000000!important;
   }
}
