css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigazine.net") {
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #aaa !important; 
    border: inset 0px #222742 !important;
    text-shadow: 0px !important;
    text-shadow: none !important;
  }
  body, #latest{
   background:#111 !important;
   color: #bbb !important;
  }
  #subtop, #header, footer, #UFad, .list-1, .sbn, #EndFooter{
    display:none;
  }
  a:link:hover{
    color:#D2691E !important;    
  }
  .header .menu ul {
    padding: 5px 0px 0px;
    text-transform: uppercase;
  }
  .header {
    border-top: 0px;
  }

  .content section h2 a, .date, #nextpage, #nextpage a, .cntimage, #sub, h3, #subNav dl dt, .navi-list-2col dd:nth-child(2n), #subNav .navi-list-2col dd:nth-child(2n+1), .title, #subNav > .list-1, #sub #subNav > .list-2, #sub #subNav > .list-3
  .input, .prev a, .article{
     background:#272727 !important;
     color:#eee !important;
     border: solid 1px #444 !important;
  }
  .submit{
       background:#555 !important;
       border: solid 1px #444 !important;
  }
  #nextpage a:hover, #nextpage:hover, .submit:hover,#subNav .navi-list-2col dd a:link:hover {
    background:#D2691E !important;
  }
  #subNav .navi-list-2col dd a, #latest li a{
    color:#777 !important;       
  }
  #latest li a:hover{
    color:#aaa !important;       
  }
}
