css:
@-moz-document domain("soundcloud.com") {
    
/* custom colors */             /*var(--main-bg)*/
:root {
--main-bg: #1d1e2a !important;               
--dark-bg: #1a1b26 !important;              
--darker-bg: #14151f !important;             
--main-text: #888 !important;                     /* grey */
--secondary-text: #8a7b70 !important;        /* yellow */
--third-text: #74857c !important;              /* green*/
--fourth-text: #556B82 !important;              /* blue*/  
}      
  
    
body, .trackItem:hover, .trackItem.hover:not(.m-disabled) .trackItem__additional, .trackItem.active:not(.m-disabled) .trackItem__additional, .sc-toggle, .sc-toggle-handle, .commentForm__input, .dropbar__content, .trackItem.hover, .trackItem.active, .g-type-shrinkwrap-inline, .searchOptions__navigationItem.active, .searchOptions__navigationItem.active::after, .linkMenu {
    background: var(--main-bg) !important;
  }  
  
  
.sc-button, .sc-toggle::before, .compactTrackListItem.clickToPlay.active, .compactTrackListItem.clickToPlay:hover, .compactTrackListItem.clickToPlay:focus, .compactTrackListItem.clickToPlay.active .compactTrackListItem__additional, .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional, .compactTrackListItem.clickToPlay:focus .compactTrackListItem__additional, .compactTrackList__moreLink:hover, .compactTrackList__moreLink:focus, .header__navMenu > li > a.selected, .header > li > a:focus, .playControls__inner, .volume.expanded .volume__sliderWrapper, select, .g-dark-bg, .profileMenu__link, .userNav__button.selected, .playControls__controls, .playControls__bg, li.notifications__item:hover, .uploadMain__chooserContainer, .audibleEditForm__form, .g-tabs, input, textarea, .tagInput__wrapper, select, .linkMenu__item:hover, .moreActions, .editTrackItem__additional {
    background: var(--dark-bg) !important;
  }    
  
header, .l-container.l-content, .g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .sound__soundActions, .commentForm, .readMoreBadge__countWrapper, .playbackTimeline__progressBackground, .volume__sliderWrapper, .commentForm__wrapper, .fullListenHero__foreground, .sc-label.sc-label-private, .visualSound.listenContext .visualSound__wrapper, .g-dark-list, .notifications__viewAll, .g-dark, .uploadMain__foot, .soundBadge__additional, .l-fixed-top-one-column > .l-top, .searchTitle, .commentFormDisabled, .modal.modalWhiteout, .volume__sliderBackground, .editTrackItem:hover {
    background: var(--darker-bg) !important;
  }  
  
  .g-dark a:hover, .g-dark a:focus {
    background-color: var(--main-bg) !important;
  }  
  
.playbackTimeline__progressBar, .volume__sliderProgress, .volume__sliderHandle {
    background: var(--third-text) !important;
  }  
  
body, input, textarea, select, .sc-button, .sc-button:hover, .sc-button:focus, .sc-button-focus, .sc-tag, .sc-tag:visited, .sc-buylink, .compactTrackListItem__number, .compactTrackListItem__trackTitle, .commentForm__input, .g-tabs-link, select, .g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .g-dark input[type="search"], .g-type-shrinkwrap-large-primary, .fullListenHero__uploadTime {
    color: var(--main-text) !important;
  }  

.playbackSoundBadge__title, .playbackSoundBadge__title:visited, .g-link-user, a {
    color: var(--fourth-text) !important;
  }  
  
a.sc-link-dark, .g-tabs-link.active, .playbackTimeline__timePassed, .playbackTimeline__duration {
    color: var(--third-text) !important;
  }  
  
  
                          /* remove shit */
  
.l-listen-wrapper .l-about-rows, .sc-button, .listenEngagement, .sc-border-light-bottom, .sc-border-light-top, .sc-tag, .sc-tag:visited, .sc-toggle, .sc-border-light-right, .g-tabs, .g-tabs-link.active, .sc-border-light, .commentForm, .commentForm__input, .collection.m-overview .collection__section:not(:last-child), .audibleBadgePlaceholder::before, .header__navMenu > li > a, .playControls__inner, .volume.expanded .volume__sliderWrapper, .ownActivity.large .ownActivity__user, .g-form-section-head, select, .image__whiteOutline .image__full, .commentForm__wrapper, .notifications__viewAll, .playControls__inner, .playControls__bg, .searchTitle__text, .searchPremiumContentFooter, .sc-snippet-badge-grey, .searchOptions__navigationItem.active::after, .g-modal-title-h1, input, textarea, .tagInput__wrapper, select, .moreActions__group, .moreActions__button {
    border: none !important;
  }  
  
  .modal.modalWhiteout {
/*     opacity: 0.9 !important; */
  }  
  
.listenEngagement {
    box-shadow: none !important;
  }  
  
  .g-nav-item.active > .g-nav-link {
    text-shadow: none !important;
  }  
  
.soundBadge.playlist .soundBadge__avatarLink, .truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after, .modal__modal, .soundBadge.playlist .soundBadge__artwork {
    background: transparent !important;
  }  
  
.dashbox__wrapper, .l-footer, .mobileAppsButtons.m-sidebar, .mobileApps .sidebarHeader__title, .paging-eof, .dashbox__box, .truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after, .header__link.header__goUpsell {
    display: none !important;
  }  
  
                             /* add/fix  shit */
    
.g-modal-title-h1 {
    text-align: center !important;
  }  
  
.sc-border-light-bottom, .commentForm__input {
    padding: 5px !important;
  } 
  
  .userBadge__actions {
     padding: 15px !important;
  }  
  
.fullListenHero__artwork, .g-avatar-badge-avatar > div:nth-child(1) > span, .image:not(.m-omitRoundedBorder) .image__rounded, .image__full, .image {
   -webkit-transition: -webkit-transform .8s ease-in-out !important;
    transition: transform .9s ease-in-out !important;    
  }  
  
.fullListenHero__artwork:hover, .g-avatar-badge-avatar > div:nth-child(1) > span:hover, .image:not(.m-omitRoundedBorder) .image__rounded:hover, .image__full, .image:hover {
    transform:rotate(360deg) !important;
    -webkit-transform:rotate(360deg) !important;
    opacity: .8 !important;
  }    
    
  
        /* header */  
  
.header__logoLink {
 /* background: url("https://a-v2.sndcdn.com/assets/images/header/cloud-e365a4.png") var(--darker-bg) no-repeat 12px 11px !important;*/
  background-image: url("https://a-v2.sndcdn.com/assets/images/header/cloud-e365a4.png") !important;
  background-color: var(--dark-bg) !important;
  }  
  
.header {
  height: 60px !important;  
  /*background: url("http://puu.sh/pNEVL/6375546623.jpg") !important;*/
  background-color: var(--dark-bg) !important;
  }  
 
.headerSearch__input {
    height: 60px !important;  
    margin-top: -16px !important;
  margin-left: -15px !important;
  }  
  
.header__inner {
    margin-top: 7px !important;
  }  
  
  .headerSearch__submit {
    display:  none !important;
  }  
  
  .header__mainMenu-collection, .header__navMenu > li > a.selected, .header > li > a:focus {
   padding: 19px !important;
   height: 60px !important;
   margin-top: -7px !important;
   background-color: var(--dark-bg) !important;
  }  
  
  .uploadButton {
   padding: 19px !important;
   margin-top: -6px !important;
   background-color: var(--dark-bg) !important;
    margin-left: -30px !important;
  }  
  
  .userNav, .header__navMenu > li > a.header__moreButton {
    height: 60px !important;
    margin-top: -7px !important;
    background-color: var(--dark-bg) !important;
    opacity: 1 !important;
  }  
  
  .userNav__usernameButton, .notificationIcon {
     margin-top: 6px !important;
  }  
  
  .header__navMenu > li > a.selected, .header > li > a:focus, .headerSearch__input, .g-dark input[type="search"] {
    background-color: var(--main-bg) !important;
  }  
  
/* songs */  
  
.sc-tag, .sc-tag:visited {
  background-color: var(--main-bg) !important;
  border-radius: 0px !important;
  padding: 10px !important;
  }  
  
.sceneLayer, .playbackTimeline {
    filter: grayscale(500%) !important;
  }

 .waveformCommentsNode {
    filter: hue-rotate(0deg) !important;
  }
  
  
  
              /* fix black icons */
  
.sc-button-medium.sc-button-startstation::before, .sc-button-medium.sc-button-message::before, .sc-button-medium.sc-button-more::before, .playControls__control, .playControl, button.skipControl, svg:not(:root), .volume__button, .sc-button-small::before, .sc-button-medium.sc-button-share::before, .sc-button-medium.sc-button-edit::before {
    filter: invert(100) !important;
    opacity: 0.5 !important;
  }  
  .sc-button-pause, .sc-button-play {
/*     filter: invert(100) !important; */
    background: transparent !important;
    opacity: 1 !important;
  }  
  
  
  
.commentPopover__username, a.commentPopover__username:hover, a.commentPopover__username:visited {
  opacity: 0 !important;
  }  
  
 /* replace icons */  
  
.sc-button-medium.sc-button-like::before, .sc-button-medium.sc-button-share::before, .sc-button-medium.sc-button-edit::before, .sc-button-medium.sc-button-delete::before {
 opacity: 0.7 !important;
  }
  
.sc-button-group-medium {
  padding: 0px !important;  
  }  
  

  

  

   
  
}
