css:
@-moz-document domain("3dcenter.org") {

/*
this is needed to be added in Stylish:
@import url(https://fonts.googleapis.com/css?family=Droid+Sans);
@import url(https://fonts.googleapis.com/css?family=Droid+Serif);
*/

/* DIMENSIONS */
#wrapper {
    max-width: 100%;
    min-width: 100%;
    width: 100%;
}

#wrapper > .bg-base-color, 
#header-primary-tabs > .bg-right {
    width: 1100px;
    margin: 0 auto;
}

#wrapper #header-primary-tabs {
    height: 50px;
}

#header-primary-tabs .bg-right, 
#header-primary-tabs .bg-right ul, 
#header-primary-tabs .bg-right ul li, 
#header-primary-tabs .bg-right ul li a {
    height: 100%;
}

#header-primary-tabs ul li {
    float: none;
    display: inline-block;
}

#header-primary-tabs ul li a, 
#header-primary-tabs ul li a:link, 
#header-primary-tabs ul li a:visited {
    display: block;
    width: auto;
    text-align: center;
    color: #333333;
    padding: 0 0 0 30px;
    height: inherit;
}

#header-region #block-views-downloads_front_drivers {
    float: none;
    width: 40%;
}

#header-region #block-views-Artikel_front_newest {
    margin: 0;
    width: 55%;
}

#header-region #block-views-downloads_front_drivers,
#header-region #block-views-Artikel_front_newest {
    display: inline-block;
    vertical-align: top;
}

.content-corner .group-webweite-links .field-item {
    display: block;
}

.content-corner .group-webweite-links .field-item > *:nth-child(2n-1), 
.content-corner .group-webweite-links .field-item > *:nth-child(2n) {
    width: 100%;
}

#wrapper #container .breadcrumb {
    padding: 10px 0px;
    border-bottom: 1px solid #CCC;
    margin: 0 25px;
}

.content-corner h2.headline {
    border: none;
}



/* BACKGROUNDS */
#wrapper #header-primary-tabs, 
#wrapper #header-primary-tabs .bg-right, 
#header-primary-tabs .start, 
#header-primary-tabs .accentuated-accentuated, 
#header-primary-tabs .accentuated-passive, 
#header-primary-tabs .passive-passive, 
#header-primary-tabs .passive-end, 
#header-primary-tabs .passive-accentuated, 
#wrapper > .bg-base-color, 
#wrapper .bg-sidebar-right, 
#wrapper .bg-sidebar-left, 
#wrapper .bg-header, 
#wrapper .bg-sidebar-right-top, 
#wrapper .bg-sidebar-left-top, 
#wrapper .bg-container, 
#wrapper .bg-container-right, 
#wrapper #container, 
#wrapper #container .bg-breadcrumb, 
#wrapper #container .bg-breadcrumb-right, 
#wrapper #container .breadcrumb, 
#wrapper #container #center #squeeze .content-right-corner, 
#wrapper #container #center #squeeze .content-left-corner, 
#wrapper #container #center #squeeze .content-corner, 
#header-region h2 .bg-headline-right, 
#header-region h2 .bg-headline-left, 
#header-region h2 .bg-headline {
    background: none;
}

#wrapper #header-primary-tabs {
    background: rgba(0,0,0,0.75);
}

#wrapper > .bg-base-color {
    background: #FFF;
}



/* HIDE ELEMENTS */
#feed-icon-tabs, 
#header iframe, 
#sidebar-right iframe, 
#header, 
#wrapper #header-primary-tabs .bg-right ul li.start, 
#wrapper #header-primary-tabs .bg-right ul li.passive-end {
    display: none;
}


#wrapper > .bg-base-color {
    -ms-box-shadow: 	0px 0px 200px 0px rgba(0,0,0,0.35);
    -moz-box-shadow: 	0px 0px 200px 0px rgba(0,0,0,0.35);
    -webkit-box-shadow: 0px 0px 200px 0px rgba(0,0,0,0.35);
    -khtml-box-shadow: 	0px 0px 200px 0px rgba(0,0,0,0.35);
    -o-box-shadow: 		0px 0px 200px 0px rgba(0,0,0,0.35);
    box-shadow: 		0px 0px 200px 0px rgba(0,0,0,0.35);
}



/* FONTS */
body {
    -webkit-font-smoothing: antialiased;
    font-family: "Droid Serif", georgia, serif;
    font-weight: 400;
}

h1, h2, h3, h4, h5, h6, 
#sidebar-left, #sidebar-right, 
#header-primary-tabs ul li a, 
.breadcrumb {
    font-family: "Droid Sans", arial, sans-serif;
    -webkit-font-smoothing: antialiased;
    font-style: normal;
    font-variant: normal;
    font-stretch: normal;
}

h1, h2, h3, h4, h5, h6, 
#header-primary-tabs ul li a {
    font-weight: 700;
    text-transform: uppercase;
}

#center .content p, 
#wrapper #container .breadcrumb {
    font-size: 14px;
    line-height: 20px;
}

#header-region #block-views-downloads_front_drivers .view-downloads-front-drivers {
    font-size: inherit;
    line-height: inherit;
}

#header-primary-tabs ul li a {
    font-size: 20px;
    line-height: 50px;
}



/* COLORS */
h1, h2, h3, h4, h5, h6, 
.content-corner h2 a:active, .content-corner h2 a:visited, .content-corner h2 a:hover, .content-corner h2 a:link {
    color: #000;
}

a:link, 
#sidebar-right .more-link a:link {
    color: #b75a00;
}

a:visited, 
#sidebar-right .more-link a:visited {
    color: #a37f5c;
}

.sidebar a:active, .sidebar a.active, .sidebar a:hover, #sidebar-right .more-link a:hover {
    color: #b75a00;
}

.content-corner .content p a {
    text-decoration: none;
}

.content-corner .content p a:hover {
    text-decoration: underline;
}

#header-primary-tabs ul li a, 
#header-primary-tabs ul li a:link, 
#header-primary-tabs ul li a:visited {
    color: #CCC;
}
#header-primary-tabs ul li.accentuated a, 
#header-primary-tabs ul li.accentuated a:link, 
#header-primary-tabs ul li.accentuated a:visited {
    color: #fff;
}



body {
    background-color: #ee8723;
    background-attachment: fixed;
    background-image: url(data:image/png;base64,);
}
}
