css:
@-moz-document regexp(".*socialneko\\.com(?!\\/user(?!\\/spcoxion)).*") {
/**
 * Dark Red Theme
 * by @SPCOxion
 * 
 * You may use this code on your profile if you credit @SPCOxion in your biography.
 */

* {
	font-family: Helvetica, sans-serif;
}
html {
	overflow-x: hidden
}
body {
	width: 100%;
	max-width: 340px;
	overflow: hidden;
	font-size: 11px;
	margin: auto;
	color: #eee;
	/* background-image: url(http://i.imgur.com/Mqma7qk.png); */
	
	background-image: url(http://i.imgur.com/Jbafifm.png);
	background-attachment: fixed;
}
#header {
	width: 100%;
	height: 85px;
	overflow: hidden;
	background: #292929;
}
#iheader,
.logo {
	background: #292929;
	width: 100%;
	height: 75px;
	overflow: hidden;
}
#welcome {
	text-shadow: 1px 0px 0px rgba(0, 0, 0, 1);
}
/* profile stuff */

.cover {
	width: 100%;
	height: 100px
}
.mailuser {
	position: absolute;
	top: 2px;
	left: 1px
}
.onlineoroffline {
	position: absolute;
	top: 1px;
	left: 15px;
	font-size: 10px
}
.usernamecontainer {
	position: absolute;
	top: 85px;
	left: 69px;
	background: #FFF;
	font-size: 12px;
	border: 1px solid gray;
	border-left: none;
	border-bottom: none;
	border-radius: 7px;
	border-top-left-radius: 0;
	border-bottom-left-radius: 0;
	border-bottom-right-radius: 0;
	width: 105px;
	height: 15px;
	overflow: hidden;
	text-align: center;
	color: gray
}
.profilepiccontainer {
	position: absolute;
	top: 33px;
	left: -1px;
	border: solid 1px silver;
	border-radius: 2px
}
/* pms x coins x notifs */

.headericon {
	height: 9px;
	width: 9px;
	margin-top: 2px;
	display: inline-block;
	vertical-align: top;
	background-image: url(http://socialneko.com/image/headericons.png);
	background-repeat: no-repeat
}
.headericon_mail {
	background-position: 0 0
}
.headericon_coin {
	background-position: -10px 0
}
.headericon_notification {
	background-position: -20px 0
}
a,
.headermenu,
.mainmenubutton {
	color: #ED0909;
	text-decoration: none
}
a:hover,
.mainmenubutton:hover {
	color: #FF0A0A;
}
H1 {
	color: white;
	background: #000000;
	background: -moz-linear-gradient(left, #000000 0%, #800000 40%, #800000 60%, #000000 100%);
	background: -webkit-linear-gradient(left, #000000 0%, #800000 40%, #800000 60%, #000000 100%);
	background: linear-gradient(to right, #000000 0%, #800000 40%, #800000 60%, #000000 100%);
	border: 1px solid gray;
	border-radius: 3px;
	font-family: Helvetica;
	font-weight: 100;
	margin: 15px auto 5px auto;
	padding: 3px;
	text-align: center;
	width: 88%;
	-moz-box-shadow: inset 0 0 10px #000000;
	-webkit-box-shadow: inset 0 0 10px #000000;
	box-shadow: inset 0 0 10px #000000;
	text-shadow: 1px 0px 1px rgba(0, 0, 0, 0.5);
}
H2 {
	color: #ED0909;
	background: #333;
	border: 1px solid gray;
	border-radius: 3px;
	padding: 3px;
	margin: 0;
	font-size: 12px;
	font-weight: 100;
	-moz-box-shadow: inset 0 0 8px #000000;
	-webkit-box-shadow: inset 0 0 8px #000000;
	box-shadow: inset 0 0 8px #000000;
	text-align: center;
	text-shadow: 1px 0px 0px rgba(0, 0, 0, 1);
}
#box {
	background: #161616;
	border: 1px solid #515151;
	border-radius: 5px;
	padding: 2px;
	text-align: center;
	word-wrap: break-word;
	overflow: hidden
}
#box2 {
	background: #161616;
	border: 1px solid #515151;
	border-radius: 5px;
	padding: 2px;
	margin: auto;
	margin-bottom: 6px;
	text-align: center;
	width: 95%;
	word-wrap: break-word;
	overflow: hidden
}
.mainmenubutton {
	border: 0;
	background: transparent;
	margin: 0;
	padding-left: 5px;
	padding-right: 5px;
	outline: none
}
.xuonlinenumbah {
	color: #ED0909;
}
form {
	margin: 0;
	padding: 0
}
select {
	font-size: 10px;
	height: 21px;
	border: 1px solid gray
}
.emailicon {
	background: url(http://socialneko.com/image/email.png) no-repeat !important;
	padding-left: 20px !important
}
input[type=text],
input[type=password],
#usermsg {
	color: #eee;
	background: #262626;
	border-radius: 3px;
	border: 1px solid #666;
	padding: 4px;
	font-size: 11px;
	width: 51.5%;
	/* height:19px; */
	outline: 0
}
input[type=password]:focus,
textarea:focus,
input[type=text]:focus {
	background: #333;
	border: 1px solid #ED0909 !important;
	-moz-box-shadow: inset 0 0 8px #000000;
	-webkit-box-shadow: inset 0 0 8px #000000;
	box-shadow: inset 0 0 8px #000000
}
.form_button,
.button,
#submitmsg {
	color: #FFF;
	background: #ed0909;
	background: -moz-linear-gradient(top, #ed0909 0%, #bf0707 100%);
	background: -webkit-linear-gradient(top, #ed0909 0%, #bf0707 100%);
	background: linear-gradient(to bottom, #ed0909 0%, #bf0707 100%);
	border: 1px solid #910000;
	border-radius: 3px;
	font-size: 12px;
	text-align: center;
	text-shadow: -1px -1px 0 #444, 1px -1px 0 #444, -1px 1px 0 #444, 1px 1px 0 #444;
	margin: 2px;
	padding: 4px 10px;
	outline: none;
	box-shadow: none;
}
.form_button:hover,
.button:hover,
#submitmsg:hover {
	background: #ED0909;
}
.form_button:active,
.button:active,
#submitmsg:active {
	background: #bf0707;
	background: -moz-linear-gradient(top, #bf0707 0%, #ed0909 100%);
	background: -webkit-linear-gradient(top, #bf0707 0%, #ed0909 100%);
	background: linear-gradient(to bottom, #bf0707 0%, #ed0909 100%);
	border: 1px solid #910000;
	position: relative;
	top: 1px;
	left: 1px;
}
/* chat stuff */

#chatbox.chatbox {
	background: #161616;
	border: 5px solid #282828
}
#touchscreen {
	background: #282828;
	border-bottom: 1px solid #ED0909;
}
#stuff {
	color: #eee;
	background: #161616
}
textarea {
	color: #eee;
	background: #262626;
	border: 1px solid #666;
	border-radius: 6px;
	font-size: 10px;
	outline: 0;
	margin: auto;
	margin-bottom: 2px;
	padding: 5px;
	height: 85px;
	width: 95%;
}
textarea:focus {
	background: #333;
	border: 1px solid #2ac400;
	-moz-box-shadow: inset 0 0 8px #000000;
	-webkit-box-shadow: inset 0 0 8px #000000;
	box-shadow: inset 0 0 8px #000000
}
.table {
	width: 100%;
	float: left
}
table,
tr,
td,
th {
	padding: 0;
	margin: 0;
	font-size: 12px
}
td {
	background: #303030;
	border-bottom: none;
}
td a {
	color: #ED0909;
}
.spoiler {
	color: #ED0909;
	background: #282828;
	border: 1px solid #666;
	border-radius: 4px;
	padding-left: 3px;
	width: 95%;
	margin: auto
}
.inner {
	color: white;
	background: #333;
	padding: 4px;
	margin: 2px;
	border: 1px solid #797979;
	border-radius: 2px
}
#compose,
#reply {
	display: none
}
div.backbtn {
	color: #ED0909;
	background: #262626;
	border: 1px solid #888;
	border-radius: 10px;
	margin: auto;
	padding: 2px;
	text-align: center;
	font: 12px Tahoma;
	width: 95%;
	transition: border .5s ease-in-out;
}
div.backbtn:hover {
	border: 1px solid #666
}
.tinyavatar {
	border: 1px inset #888;
	float: left;
	height: 30px;
	width: 30px;
	margin: 2px
}
.commenttd {
	background: #282828;
	border-top: 1px solid #666;
	border-bottom: 1px solid #666;
}
.comment {
	width: 97.2%;
	overflow: hidden;
	word-wrap: break-word;
	text-align: left;
	background: #161616;
	/* border:1px solid #888; */
	clear: both;
	padding: 3px
}
#status {
	width: 93%;
	background: #181818;
	border: 1px solid #666;
	border-radius: 5px;
	margin: auto;
	box-shadow: none;
	overflow: hidden
}
#status a {
	color: #ED0909;
}
#status a:hover {
	color: #FF0A0A;
}
#status_text {
	margin: 2px;
	word-wrap: break-word
}
#status table {
	margin-top: 1px;
	margin-left: 1px;
}
.ava {
	width: 60px;
	height: 60px;
	overflow: hidden;
	padding: 0;
	margin: 0;
	border-top-left-radius: 3px;
	border: 1px solid gray;
	border-bottom: 2px solid #ED0909;
}
#userinfo {
	width: 100%;
	overflow: hidden;
	background: #282828;
	border: 1px solid gray;
	border-bottom: 2px solid #ED0909;
	text-align: center;
	font-weight: bold;
	border-top-right-radius: 3px;
}
#userinfo a {
	color: #ED0909;
	font-family: Myriad Pro;
	font-size: 1.2em;
	text-shadow: 1px 1px 0 #000;
}
#userinfo a:hover {
	color: #FF0A0A;
}
hr {
	border-top: none !important;
	height: 0;
	border-bottom: 1px solid gray !important;
}
input[type=checkbox] {
	border: 1px solid silver;
	border-radius: 2px;
	box-shadow: inset 0 2px 1px -1px rgba(0, 0, 0, .2)
}
#topicname {
	color: #fff;
	font-weight: 100;
	font-family: Helvetica;
	font-size: 12px;
	border: none;
	border-bottom: 1px solid #666;
	border-top-right-radius: 4px;
	border-top-left-radius: 4px;
	width: 98%;
	padding: 3px;
}
#forums {
	background: #262626;
	border: 1px solid grey;
	width: 95%;
	border-radius: 5px;
	margin: auto;
	text-align: center;
	word-wrap: break-word;
}
.forum1, .forum2 {
	font-size: 12px;
	padding: 0px;
}
.forum1 .spacing,
.forum2 .spacing {
	margin: 0px !important;
	padding: 3px;
	padding-top: 2px;
}
.forum1 .spacing div div,
.forum2 .spacing div div {
	font-size: 11px !important;
	border-left: 1px solid gray !important;
}
.forum1,
.askme_a {
	/* brighter color */
	
	background: #303030;
}
.forum2,
.askme_q {
	/* darker color */
	
	background: #262626;
	border-bottom: 1px solid #666;
}
/* Better AskMe! */

.askme_q {
	margin-top: 5px;
	margin-left: 3px;
	margin-right: 3px;
	padding: 3px;
	border: 1px solid gray;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
}
.askme_a {
	margin-bottom: 5px;
	margin-left: 3px;
	margin-right: 3px;
	padding: 3px;
	border: 1px solid gray;
	border-top: none;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}
#askmeshowmore hr {
	border: none !important;
}
.leaderboardavatar {
	border: 1px solid #007cdb
}
.leaderboardavatar:hover {
	border: 1px solid #9EC100
}
.spacing {
	margin-top: 5px
}
.yolo {
	color: white;
	background: #282828;
	border-top: 1px solid #777;
	border-bottom: none;
	text-align: center
}
.yolo:hover a {
	color: #FF0A0A;
}
.yolo:hover {
	color: white;
	background: #333;
	border-bottom: 1px solid #ED0909;
}
/* Validation Messages */

.successmsg,
.errormsg,
.infomsg,
.warnmsg {
	width: 91%;
	text-align: center;
	margin: -8px auto 8px auto;
	padding: 6px;
	border-radius: 3px
}
.closemsg {
	float: right;
	margin-right: 2px;
	margin-top: -4px
}
.successmsg {
	color: #CCFFCC;
	background: #34bc0f;
	background: -moz-linear-gradient(top, #34bc0f 0%, #1f870a 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #34bc0f), color-stop(100%, #1f870a));
	background: -webkit-linear-gradient(top, #34bc0f 0%, #1f870a 100%);
	background: -o-linear-gradient(top, #34bc0f 0%, #1f870a 100%);
	background: -ms-linear-gradient(top, #34bc0f 0%, #1f870a 100%);
	background: linear-gradient(to bottom, #34bc0f 0%, #1f870a 100%);
	border: 1px solid #00bc00;
	text-shadow: 0 1px 0 #144400
}
.warnmsg {
	color: #9F6000;
	background: #feefb3;
	background: -moz-linear-gradient(top, #feefb3 0%, #ccc090 100%);
	background: -webkit-linear-gradient(top, #feefb3 0%, #ccc090 100%);
	background: linear-gradient(to bottom, #feefb3 0%, #ccc090 100%);
	border: 1px solid #CEA100
}
.errormsg {
	color: #fcc;
	background: #a90329;
	background: -moz-linear-gradient(top, #a90329 0%, #8f0222 44%, #6d0019 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #a90329), color-stop(44%, #8f0222), color-stop(100%, #6d0019));
	background: -webkit-linear-gradient(top, #a90329 0%, #8f0222 44%, #6d0019 100%);
	background: -o-linear-gradient(top, #a90329 0%, #8f0222 44%, #6d0019 100%);
	background: -ms-linear-gradient(top, #a90329 0%, #8f0222 44%, #6d0019 100%);
	background: linear-gradient(to bottom, #a90329 0%, #8f0222 44%, #6d0019 100%);
	border: 1px solid #a90329;
	text-shadow: 0 1px 0 #300000
}
.infomsg {
	color: white;
	background: #161616;
	background-position: 3px 2px;
	border: 1px solid #ED0909;
}
.emoji {
	height: 16px;
	width: 20px;
	display: inline-block;
	vertical-align: top;
	background-image: url(http://socialneko.com/image/emoji.png);
	background-repeat: no-repeat
}
.emoji_1 {
	background-position: 0 0
}
.emoji_2 {
	background-position: -21px 0
}
.emoji_3 {
	background-position: -42px 0
}
.emoji_4 {
	background-position: -63px 0
}
.emoji_5 {
	background-position: -84px 0
}
.emoji_6 {
	background-position: -105px 0
}
.emoji_7 {
	background-position: 0 -17px
}
.emoji_8 {
	background-position: -21px -17px
}
.emoji_9 {
	background-position: -42px -17px
}
.emoji_10 {
	background-position: -63px -17px
}
.emoji_11 {
	background-position: -84px -17px
}
.emoji_12 {
	background-position: -105px -17px
}
.emoji_13 {
	background-position: 0 -34px
}
.emoji_14 {
	background-position: -21px -34px
}
.emoji_15 {
	background-position: -42px -34px
}
.emoji_16 {
	background-position: -63px -34px
}
.emoji_17 {
	background-position: -84px -34px
}
.emoji_18 {
	background-position: -105px -34px
}
.emoji_19 {
	background-position: 0 -51px
}
.emoji_20 {
	background-position: -21px -51px
}
.emoji_21 {
	background-position: -42px -51px
}
.emoji_22 {
	background-position: -63px -51px
}
.emoji_23 {
	background-position: -84px -51px
}
.emoji_24 {
	background-position: -105px -51px
}
/* --------------------- spritesheet --------------------- */

.icon {
	background: url(http://socialneko.com/image/icons.png) no-repeat;
	display: inline-block
}
.wh9 {
	width: 9px;
	height: 9px
}
.wh8 {
	width: 8px;
	height: 8px
}
.wh12 {
	width: 12px;
	height: 12px
}
.w16h13 {
	width: 16px;
	height: 13px
}
.wh16 {
	width: 16px;
	height: 16px
}
.w11h12 {
	width: 11px;
	height: 12px
}
.w12h11 {
	width: 12px;
	height: 11px
}
.mail {
	background-position: 0 0
}
.coin {
	background-position: -9px 0
}
.notif {
	background-position: -18px 0
}
.offline {
	background-position: -27px 0
}
.online {
	background-position: -36px 0
}
.delete {
	background-position: -45px 0
}
.edit {
	background-position: -54px 0
}
.lock {
	background-position: -63px 0
}
.arrow-left {
	background-position: 0 -9px
}
.arrow-right {
	background-position: -16px -9px
}
.arrow-left-disabled {
	background-position: 0 -22px
}
.arrow-right-disabled {
	background-position: -16px -22px
}
.refresh {
	background-position: -32px -9px
}
.switch {
	background-position: -32px -25px;
	height: 15px
}
.sticky {
	background-position: -48px -9px
}
.refresh2 {
	background-position: -59px -9px
}
.triangle-left {
	background-position: -48px -21px
}
.triangle-right {
	background-position: -60px -21px
}
.g_comment {
	background-position: -48px -32px
}
.g_favorite {
	background-position: -56px -32px
}
.g_star {
	background-position: -64px -32px
}
.g_artist {
	background-position: -49px -40px
}
.g_star2 {
	background-position: -60px -40px
}
.thumbsup {
	background-position: -15px -40px
}
.thumbsdown {
	background-position: -26px -40px
}
.alert {
	background-position: -37px -40px
}
.scroll-top {
	width: 15px;
	height: 14px;
	background-position: 0 -35px
}
/* EXTERNAL LINKS SPRITESHEET */

.external-link {
	background: url(http://socialneko.com/image/external-links) no-repeat;
	width: 148px;
	height: 20px;
	display: inline-block;
	margin-bottom: 2px
}
.facebook {
	background-position: 0 0
}
.tumblr {
	background-position: 0 -20
}
.twitter {
	background-position: 0 -40
}
/* new forum design */
#everything {
	margin-bottom: 5px;
}
#posthead {
	background: #333333;
	width: 94%;
	color: #ED0909;
	padding: 4px;
	position: relative;
	margin: auto;
	border: 1px solid gray;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
}
#postbody {
	background: rgb(8, 8, 8);
	padding: 4px;
	width: 94%;
	position: relative;
	margin: auto;
	height: auto;
	border: 1px solid gray;
	border-top: none;
	border-bottom: none;
	text-align: justify;
}
#postuserinfo {
	position: absolute;
	left: 0px;
	top: 4px;
	width: 30%;
	text-align: center;
}
#posttext {
	margin-left: 30%;
	background: rgb(20, 20, 20);
	width: 70%;
}
#postfooter {
	background: rgb(8, 8, 8);
	width: 94%;
	padding: 4px;
	position: relative;
	margin: auto;
	border: 1px solid gray;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}
}

@-moz-document regexp(".*socialneko\\.com(?=\\/chatroom\\/\\?r).*") {
body {
	margin: 0;
}
}
