css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
  *,*:before,*:after {
    box-sizing:border-box;
  }
  html {
    background:#EAEAEA;
  }
  
  body {
    background:#FFF;
    font-size:14px;
    line-height:1.5;
    max-width:1180px;
    margin:0 auto;
    padding:0;
  }

  
  .extButton,.bottomCtrl, .absBotDisclaimer,#boardNavDesktopFoot,.fileText, #boardNavDesktop, #t3743858, #t3876795, .threadHideButton, .sideArrows, #bannerCnt, .ad-cnt, .ad-plea, .belowLeaderboard, form[name=post], .aboveMidAd, .abovePostForm, #globalMessage, #toggleMsgBtn, hr {
    display:none!important;
  }
  
  #ctrl-top, .navLinks {
    margin:0 auto 2em;
    width:95%;
  }
  
  div.boardBanner{
     clear: both;
    text-align: center;
    background:#01D099;
    color:#FAFEFD;
    padding:2em;
    margin:1em 0;
    box-shadow:0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
      text-decoration:none;
  }
  
  .reply:target, .reply.highlight{
     background:#F5F5F5!important;
    border:none!important;
    padding-bottom:8px!important;
  }
  div.boardBanner a,div.boardBanner:hover{
    text-decoration:none;
    color:#FFF!important;
  }
  
  div.boardBanner > div.boardTitle {
    font-size:3.5em;
  }
  
  /*threads*/
  .thread {
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
    margin:0 auto 2em!important;
    background:#F5F5F5;
    padding:0;
    width:95%;
  }
  
  .replyContainer, .replyContainer>.reply {
    clear:both;
    width:100%;
    background:none;
    border:none;
  }
  
  .replyContainer>.reply {
     clear:both;
    width:100%;
    background:#FFF;
    border:none;
    border-bottom:1px solid #EAEAEA;
    padding:0.5em 2.5em;
    box-sizing:border-box;
  }
  
  .replyContainer > .reply > .file {
    float:left;
    width:100%;
    clear:left;
    padding:1em 0 0 0;
  }
  
  .expandedWebm {
    margin:0;
  }
  
  .op {
   padding:0.5em 1.5em;  
  }
  
  .thread > .replyContainer:last-child >.reply{
    border:none;
    margin:0;
  }
  
  div.post {
    margin:0 0 1px;
  }
  
  .postInfo {
    padding-top:1em;
    padding-bottom:0.4em;
    display:block!important;
    position:relative;
    float:left;
    max-width:80%!important;
    border-bottom:1px dashed #333;
  }
   
    .thread > .replyContainer .postInfo {
    float:left;
    width:100%!important;
    display:block;
  }
  .image-expanded > .postInfo {
    width:100%;
  }
  .postInfo > input {
    display:none;
  }
  
   .postInfo > span {
    font-size:1.3em;
  }
  
  .file {
    float:left;
    width:20%;
    clear:left;
    padding:1em 0 0 0;
  }
  .fileThumb {
  margin-left:0!important;
    margin-bottom:0!important;
  }
  
  .fileThumb img{
    width:100%!important;
    height:auto!important;
    max-width:100%!important;
    float:left;
    margin:0!important;
    display:block;
  }
  
  blockquote {
    display: inline-block;
    float: left;
    margin: 0;
    padding: 1em 1.5em 1.5em 0;
    max-width: 100%;
    width:80%;
  }
  
 .replyContainer > .reply > blockquote {
    width:100%!important;
    float:left;
   padding-top:0.5em;
   display:block
  }
  .postMessage {
    color:#333;
    line-height:1.5;
    font-size:14.5px;
    font-family: 'Open Sans', sans-serif;
  }
  
  .summary {
    display:block;
    clear:left;
    padding:1em 0.8em 1.5em 0.8em;
    font-size:23px;
    max-width:100%;
    float:left;
  }
  
  .summary a, a.replylink:not(:hover), div#absbot a:not(:hover) {
    text-decoration:underline;
    color:#333!important;
  }
  
  .summary a.replylink {
   color:#FFF!important;
  }
  
  .summary > span {
    background:#FF9AC7 no-repeat 10px 8px;
    padding:0.6em 1em 0.6em 1em;
    color:#FFF;
    font-weight:bold;
    display:block;
    max-width:100%;
    box-shadow:0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
  }
  .postContainer {
    display:block;
    position:relative;
  }
  
  .postContainer:before, .postContainer:after {
    display:table;
    content:"";
  }
  
  .postContainer:after{
    clear:both;
  }
 

  video {
    max-width:100%!important;
  }
  .image-expanded {
    width:auto!important;
    max-width:100%!important;
  }
  
  div.pagelist {
    background:#FFF;
    font-family:open sans;
    font-size:18px!important;
    border:none;
    color:#333;
    margin:0 auto;
    display:block;
    padding:0.5em;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
    float:none;
    width:95%;
    box-sizing:border-box;
    padding:1em 1.5em!important;
  }
  
  div.pagelist a {
    color:#333;
    padding:0 0.25em;
  }
  
  div.pages {
    padding:0!important;
    margin:0;
  }
  
  div.cataloglink {
    border:none!important;
  }
  
  .next {
    margin:0 0 0 0.5em;
  }
  
  .prev {
   margin:0 0.5em 0 0;
  }
  
  div.opContainer,div.op {
    display:block;
  }
  
  /*popup menu */
  
  .dd-menu li {
    background:#fff;
    color:#333;
    border-bottom:1px solid #ccc;
    padding:0.5em;
  }

  .dd-menu li:hover {    
   background:#f5f5f5;
  }
  .dd-menu ul {
      border:none;
     box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
   }

  /* catalog view */
  
  .is_catalog #content #threads {
    width:95%;
    margin:0 auto;
  }
  
  .is_catalog #content .thread {
    min-height:350px;
    overflow-y:auto;
    margin:1em!important;
    padding:1em 0.5em;
    background:#fff;
    width:23.12667261%;
  }
  
  .is_catalog #content .thread:nth-of-type(4n) {
    margin-right:0!important;
  }
  
  .is_catalog #content .thread:nth-of-type(4n+1) {
    margin-left:0!important;
  }
  
  .is_catalog #content .thread .meta {
    margin:0.5em 0;
  }
  
  .is_catalog #content .teaser > b {
    clear:both;
    display:block;
    color:#333;
    line-height:1.2;
    font-size:18px;
    margin:0.5em 0 0.25em;;
  }
  
  #ctrl {
    margin: 0 auto;
    width: 95%;
}
}
