/* Scrollbar Chrome*/

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
    display: none ! important;
}

*::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #484848! important;
}

*::-webkit-scrollbar-corner, *::-webkit-scrollbar-track {
    background: #292929 ! important;
}

.ISc, .Pu5TY, .RWdWXd, .Tzc, .yt-spinner-img {
    -webkit-animation: scaleout 1s infinite ease-in
}

@-webkit-keyframes scaleout {
    0% {
        -webkit-transform: scale(0)
    }
    100% {
        -webkit-transform: scale(1);
        opacity: 0
    }
}

@keyframes scaleout {
    0% {
        transform: scale(0);
        -webkit-transform: scale(0)
    }
    100% {
        transform: scale(1);
        -webkit-transform: scale(1);
        opacity: 0
    }
}
/* Background + General (Todo lo que vi en el Inicio) */

body {
    background: #101010;
    color: #D5D5D5;
}

a:link, a:visited {
    color: #0096F4;
}

a:hover {
    color: #0085FA;
}

#container {
    background: #1F1F1F;
    border: 1px solid #1A1A1A;
    color: #D5D5D5;
}

#panel, .menu-cabecera {
    background: #000000;
    color: #D5D5D5;
}

.menu-cabecera, .menu-cabecera tbody tr td div {
    color: #E9E9E9 !important;
}

.menu-cabecera tbody tr td a:link, .menu-cabecera tbody tr td a:visited {
    color: #0096F4;
}

.menu-cabecera tbody tr td a:hover {
    color: #0085FA;
}

.navigation .active {
    color: #EDEDED;
}

.tcat, .tcat a:link, .tcat a:visited {
    background: #1B466F;
    color: #E1E1E1;
}

.trow1, .trow2 {
    background: #101010;
    color: #D5D5D5;
}

.bottommenu {
    background: #101010;
}

.logo {
    content: url("http://puu.sh/pP769/f911d8d3a5.png");
}

/* Temas/Mensajes */

.trow1 table tbody tr td, .trow2 table tbody tr td {
    color: #FFFFFF;
}

hr {
    background: #FFFFFF;
}

blockquote {
    background: #1F1F1F;
    color: #FFFFFF;
}

.codeblock {
    background: #DFDFDF;
    color: #000000;
}

.highlight {
    background: #004789;
}

/* Creacion/Edicion de un Tema/Mensaje/etc */

.messageEditor, #message_new {
    background: #1F1F1F;
    color: #FFFFFF;
}

.editor_dropdown_label, .editor_dropdown_menu {
    color: #000000;
}

.editor_control_bar {
    background: #1F1F1F;
    color: #FFFFFF;
}

textarea {
    background: #1F1F1F;
    color: #FFFFFF;
}

/* Viendo Foros/Categorias */

.trow_sep {
    background: #1F1F1F;
    color: #F2F2F2;
}
}
