css:
@-moz-document domain("gitlab.com") {
html, body {
 	background-color: #28203c !important;
}

i {
 	color: white !important;   
}

.btn {
 	background-color: #4d3f73 !important; 
    border: none !important;
}    

::-webkit-input-placeholder {
 	color: white !important;   
}

::-moz-placeholder {
 	color: white !important;   
}

:-moz-placeholder {
 	color: white !important;   
}

header, .header-content, .title, a:not(.count) {
 	color: white !important;   
}

.search, .search-form, .search-active, input, .form-control, .search form {
    background-color: #4d3f73 !important;
    border: none !important;
}

.navbar-form {
 	border-radius: 5px;   
}

#project-filter-form-field:focus {
    outline-color: transparent !important;
	outline-style: none !important;
    border: 1px solid white !important;
}

.location-badge {
 	color: #28203c !important;   
}

.dropdown-content {
 	background-color: #28203c !important;   
}

.dropdown-content>ul>li>a:hover {
 	background-color: rgba(0, 0, 0, 0) !important;  
    color: orange !important;
}

.form-control, .search form {
 	color: white !important;   
}

.header-collapsed, .navbar, .navbar-fixed-top, .navbar-gitlab {
 	background-color: #28203c !important;
	border-bottom: none !important;
}

.project-home-panel, .cover-block, .clearfix,
.project-stats, .row-content-block, second-block{
 	background-color: #28203c !important;    
}

.page-sidebar-collapsed, .page-with-sidebar {
 	background-color: #28203c !important;       
}

.side-nav-toggle {
 	background-color: #28203c !important;        	   
}

.content-wrapper {
 	background-color: #28203c !important;        	       
}


.container-fluid, .container-limited {
 	background-color: #28203c !important;;
}

ul.content-list>li {
 	color: #a1a1a1 !important;   
}

ul.content-list>li a {
 	color: #e1e1e1 !important   
}

.nav-links li.active a {
 	color: orange !important;
    border-bottom: 2px solid orange;
}

 .top-area {
	border: none !important;              
}

.projects-list, .project-row, .gl-pagination {
    border-color: #7d6bae !important;
}
.pagination>li>a, .pagination>li>span,
.pagination>li>a:hover, .pagination>li>a:focus, .pagination>li>span:hover, .pagination>li>span:focus {
    color: white !important;
    background-color: #584785 !important;
    border: none !important;
}

.light {
 	color: white !important;   
}

/*project page down here */
.btn-default>span {
    color: #28203c !important;
} 

#project-clone {
 	background-color: rgba(0, 0, 0, 0) !important;   
}

.cover-title>h1 {
 	color: white !important;   
}

.time_ago {
 	color: #d1d1d1 !important;   
}

.commit-author-link {
 	margin-left: 0px !important;   
}

.file-content >h1 {
 	color: white !important;   
}

.file-content > ul {
 	color: #d1d1d1;   
}

.missing {
 	border-color: #4d3f73 !important;   
}

.missing:hover {
    border-color: #4d3f73 !important;
 	background-color: #4d3f73 !important;   
}

.diff-content {
 	background: none !important;   
}

/*border stuff */
.layout-nav, .project-stats, .content-block {
 	border-color: #584785 !important;    
}


/*issues */
.nav-links, lsub-nav {
 	background-color: rgba(0, 0, 0, 0) !important;
    border-color: #4d3f73 !important;
}

.badge {
 	color: white !important;
    background-color: #4d3f73 !important;
}

.top-area, .issues-details-filters, .issues-holder {
 	border-color: #4d3f73 !important;
    border-bottom: 1px solid #4d3f73 !important;
}

.builds>tbody>tr>th {
 	background-color: #4d3f73 !important;
    color: white !important;
    border: none !important;
}
tr, .commit:not(:last-child) {
 	border-color: #4d3f73 !important;   
}

.event-item {
	border-bottom: 1px solid #4d3f73 !important;
}

.note-textarea {
    background-color: #4d3f73 !important;
    padding-left: 10px;
    color: white;
}

#notes-list, .note {
 	background-color: #4d3f73 !important;
}

/*commit data */
.event-label, .pushed, .event-title {
 	color: #d1d1d1 !important;   
}

.commit-row-title {
 	color: #a0a0a0;   
}

.commit-actions>.btn {
 	padding-left: 6px !important;
    padding-right: 6px !important;
}

/*repository -> commits */
.dropdown-menu-toggle {
 	background-color: #4d3f73 !important;
    color: white;
    border: none;
}

.commit-header {
   	background-color: #4d3f73;
    color: white;  
    border: none;
}

.commit-row-info {
 	color: gray !important;   
}

.text-expander {
 	background-color: #4d3f73 !important;
    border: none !important;
}

.row-content-block {
 	border-bottom: none;   
}

.dropdown-menu {
 	background-color: #28203c !important;
    border: 1px solid orange;
}

/*file browser*/
.readme-holder, .file-title, .file-content {
  	background-color: #28203c !important;
}

.file-holder {
 	border: 1px solid #4d3f73;
    border-radius: 2px;
}

.file-title {
     border-bottom: 1px solid #4d3f73 !important;   
}

/*file-content*/
.wiki>h1 {
 	border-bottom: 1px solid transparent !important;   
}

.blob-commit-info {
  	background-color: #28203c;
    border: none;
}

.header-content {
     border-bottom: 1px solid #4d3f73 !important;   
}

.input-group-btn>.clone-dropdown-btn, 
.input-group-btn>.btn-clipboard{
    border: 1px solid #4d3f73 !important;
}

.editor-ref, .form-actions, .commit-message-container, .right-sidebar {
  	background-color: #28203c !important; 	   
}

.count-with-arrow>.count>a {
 	color: #28203c !important;    
}

.js-notification-toggle-btns>div>.dropdown-new {
 	color: white !important;   
}

.md-area {
 	border: 1px solid #4d3f73 !important;   
}

.view-file {
 	background-color: #4d3f73 !important;   
}

/*files*/
.is-focused {
    border-color: orange !important;
 	border: 1px solid orange !important;
    box-shadow: none !important;
}

.diff-file, .image {
 	background: none !important;   
}

.view-modes {
 	background: #4d3f73 !important;
}

.active {
 	color: white !important;   
}

.view-modes-menu>li:not(.active){
 	color: lightgray !important;   
}

.commit-title {
 	color: white !important;   
}

.hidden-xs, .hidden-sm, .commit-stat-summary {
 	color: gray;   
}

.dropdown-toggle>.hidden-xs {
 	color: white !important;   
}

/* graphs */
.sub-header-block {
 	background: none;
    border-bottom: 1px solid #4d3f73;
}

.page-title, .person>h4 {
 	color: white;   
}

/*removing background on graphs */
.spark, .tint-box {
 	background: none !important;   
}

/*misc*/
.project-avatar {
 	background-color: #4d3f73 !important;
    color: white !important;
}

.activity-filter-block {
 	border-bottom: 1px solid #4d3f73 !important;   
}

.navbar-fixed-top {
 	border-bottom: 1px solid #4d3f73 !important;   
}

.award-control-text {
 	color: white !important;   
}

.award-control-text:hover {
 	color: orange !important;   
}

.btn-file-option {
 	background: none !important;
    border: 1px solid transparent !important;
}
.btn-file-option:hover {
 	border: 1px solid orange !important;   
}

.form-control:focus {
 	border: 1px solid orange !important;
    box-shadow: none !important;
}

hr, .form-actions {
 	border-top: 1px solid #4d3f73;   
}
}
