css:
@-moz-document domain("roblox.com") {
.content, body{
	background: linear-gradient(#F5F5F5, #E3E3E3);	
}
.games-list-container{
	background:none;
}

#header{
	border-radius: 0px 0px 5px 5px;
}
@keyframes spin {
    100%{
    	transform: rotate(360deg);
    }
}
@keyframes enlarge {
    98%{
    	transform:scale(1);
	}
    99%{
    	transform: scale(100);
    }
}
.rbx-header .logo-transitional:Hover {
	animation: spin 1s 1 !important;
}
.icon-nav-settings:Hover {
    animation-timing-function: linear;
	animation: spin 0.5s infinite linear !important;
}
.game-card-container {
    background-color: #fff;
    position: relative;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,0.3);
    border-radius: 10px;
    float: left;
    height: 100%;
    margin: 0 auto;
    padding: 0;
    text-align: left;
    width: 100%;
}
.container-footer {
    background: linear-gradient(#E3E3E3, #FFFFFF);	
    position: absolute;
    bottom: 0;
    right: 0;
    width: 100%;
}
a.ad img {
    display: block;
    border-radius: 10px;
}
}
