css:
@-moz-document domain("xda-developers.com") {

/* Remove ADs */
.purch-box, .purchad, #advertisement_RightPanel, #adContent, [id^="crt-"], .VibrantAd, .vm-hook-icon, .vm-adunit-outer, [href*="/ad-free"], #block_message_wrapper, .xdaswappa, .xda_swappa_widget, .sidebar-labs-badge, #hosted-by, .hosted_by, .xda-box, #gleam_container, iframe[src*="gleam.io"], #thread-header-social, [href*="xda.tv"] img, [href*="honor-hub"], .stackcommerce-widget, .et_social_inline, .postbit-social, .et_social_sidebar_networks, #xdadepot-container, #sidepanel [class$="-banner"], #sidepanel [id$="-banner"], .postbit-wrapper, [title="Submit to XDA Portal"], #header_social_wrapper, #honor-launch-banner, .twig, #countdown-timer, [id$="launch-video"], .youtube-container, .postbit-signature, .devdb-tag, .tagbar, .nav, .hamburger, .below-nav-ad, .bravetwig-top {
  display: none !important;
}
.vm-hook[style*=" "] {
  color: inherit !important;
  border-style: none !important;
  text-decoration: none !important;
  cursor: text !important;
}
.postbit-wrapper[id^="edit"] {
  display: block !important;
}
#header, #footer-nav .navbits {
  top: 0px !important;
}
.navbits {
  top: 61px !important;
}
body {
  margin-top: 0px !important;
}

/* Hide ignored users' posts */
#posts TABLE[id^="post"][class="tborder"][cellspacing="0"][cellpadding="5"][border="0"][align="center"] {
  display: none !important;
}

/* More visible search result text */
a.post-result_text {
  color: #000 !important;
}
body.algoliasearch a:hover, body.algoliasearchome a:hover {
  color: #DE7300 !important;
}

/* Always visible bottom image bar */
#swipebox-bottom-bar {
  transform: translate3d(0px, -50px, 0px) !important;
  opacity: .75 !important;
  height: 40px !important;
}

}
