css:
@-moz-document domain("duolingo.com") {
ul.dropdown-menu.languages {
  max-height: 70vh;
  overflow-y: auto;
}
ul.dropdown-menu.languages::-webkit-scrollbar {
  width: 3px;
  background: transparent;
}
ul.dropdown-menu.languages::-webkit-scrollbar-thumb {
  background: rgba(28, 175, 246, 0.5);
}
}
