css:
@-moz-document domain("www.mtime.com") {
.tophead {
    background: none !important;
}
#beaderbar .headbar dl dd:nth-child(3),
[id^=ad-normal],
.cinemaregion,
.mallregion,
.headcart,
.searchlink.cur{
    display: none !important;
}

a {
  color: inherit;
}
.headslider {
    box-shadow: 0 0 15px rgba(0, 0, 0, .5) !important;
}
.headbar {
    padding: 0 0 0 20px !important;
    height: 44px !important;
}
.headbarout,
.headbg {
    height: 44px !important;
}
.headbar h1 a {
    background-size: 75% !important;
    background-position: 0 -264px !important;
    width: 226px !important;
    height: 106px !important;
}
.headtool .line,
.headqr,
.headunlogin a, .headbarnav dd a, .headzoom, .headlogin a, .headmessage{
    height: 44px !important;
    line-height: 44px !important;
}
#searchtooldiv {
	display: none !important;
}
body {
    background-color: #272727 !important;
    color: #ababab !important;
}
#homeContentRegion {
    background-color: #272727 !important;
/* 	background: url("https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/YWAdzU2/video-background-1585-old-film-dust-and-scratches-effect-loop_v1ymu9tz__S0002.jpg");     */
    color: #ababab !important;
}
.customnav dd,
.customnav dl {
    background: none;
}
.customnav i {
    display: none;
}
.customtitle {
    color: #00d0c8 !important;
}
.mlist h4,
h4.indextitle {
    color: white !important;
    border-left: 3px solid #00d0c8;
    padding-left: .5em;
}
.viewmore a {
    border: 1px solid #fff !important;
    color: #fff !important;
    transition: all .5s;
}
#community-acts {
    color: black !important;
}

