.headbarout.fixed {
    background: rgb(22, 49, 53) !important;
    box-shadow: 0 1px 10px black !important;
}
.mboxofficerank dt {
    font-size: 20px !important;
}
.moviegrade {
    border-radius: 5px;
}
div#searchbox {
    position: fixed !important;
    top: 0 !important;
    left: 50% !important;
    width: 438.5px !important;
	transform: translateX(-50%);    
    z-index: 6 !important;
    height: 44px;
    box-sizing: border-box;
    border-radius: 0;
    border: none;
    display: block !important;
}
.tophead {
    z-index: 6 !important;
    height: 0 !important;
}

.headslider dd {
	border: 3px solid white;	
}

h4, .hotcomments .commentsbox h3 {
	color: white;
}

.showsearch {
	width: 100% !important;
}

.selectionregion {
	background-color: #f7f7f7;
}

.selectionregion h4 {
	color: black;
}

.selectionregion a {
	color: #272727;
}

.seltrailer dt p {
    color: #4e4e4e;
}

.seltrailer .gt {
	color: #272727;
}

.newsitem .imgbox {
	transition: all .3s;
}

.newsitem .imgbox:hover {
    transform: scale(0.95);
}

.seltrailer .videobox strong {
	opacity: 0;
}

.seltrailer .videobox:after {
	content: attr(title);
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    color: white;
    font-size: 16px;
    padding: .3em;
    box-sizing: border-box;
    opacity: 0;
    transition: opacity .3s;
}

.seltrailer .videobox:hover:after {
	opacity: 1;
}

.seltrailer .videobox img {
	transition: all .5s;
}

.seltrailer .videobox:hover img {
	filter: brightness(0.7);
    -webkit-filter: brightness(0.5);
}

.funnews h4 {
	padding-bottom: 0;
    margin-bottom: 22px;
}

.mboxofficerank dd.moviepic .moviegrade {
	right: -40px;
}

.headtip {
	top: 44px;
}

.headtip a {
	color: #383838;
}

.my-business {
	display: none;
}

.my-app li {
	border-top: none;
}

}
