css:
/**
 * DuckDuckGo Zenburn theme
 *
 * Inspired by the DuckDuckGo Solarized theme, by Gen2
 *
 * See:
 * - http://kippura.org/zenburnpage/
 * - https://userstyles.org/styles/111106/duckduckgo-solarized-dark
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {

  body, .body--home, .site-wrapper {
    background-color: #1f1f1f;
    background-size: cover;
  }

  .header-wrap, .has-active-zci .header-wrap, .has-active-zci .header-wrap .zcm__link:before, .has-active-zci .header-wrap .zcm__link:after, .has-zcm .header-wrap:before, .has-zcm .header-wrap:after, .has-zcm .header-wrap .header:after {
     background-color: #2f2f2f;
  }

  .region-switch__label, .tag-home__link, .tag-home__link:hover, .tag-home__link:visited, .tag-home__link:active, .results--message, .results--didyas, .results {
     color: #839496;
  }

  .result.highlight,.cw .highlight.results--didya,.cw--c .highlight.results--didya {
     background-color: #2e3330;
  }

  #powered_by {
    display: none;
  }

  .search--home, .search--header {
    background-color: #292929;
    border-color: #242424;
  }

  .search__button:hover, .search__button--active, .search:hover .search__button, .search__input:focus ~ .search__button, .search--header.has-text.search--hover .search__button, .search--header.has-text.search--focus .search__button, .search--home.has-text .search__button, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover {
    background-color: #1F1F1F;
  }

  .result__snippet {
    color: #586e75;
  }

  .result__a, .result__menu__a {
    color: #dcdccc;
   }

  .result__a:visited, .result__menu__a:visited {
    color: #bc8cbc;
   }

  .result__check {
    color: #1f1f1f;
  }

  .tag-home {
    display: none;
  }

  .zci-wrap, .zci, .zci:before, .tileview .zcm--fixed, .tileview--grid .zcm--fixed, .tileview--grid .zcm--fixed.is-stuck, .mapview.is-expanded .zci {
    background-color: #2f2f2f;
  }

  .no-touch .zcm__link:hover, .no-touch .zcm__link:focus {
    background-color: transparent;
  }
}
