css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"), domain("wiktionary.org") {
body{
background-color: #fff !important;
font-family: serif !important;
font-size: /*[[font-size]]*/;
}

.mw-editsection {
display:none !important;
}
/*Inhaltsverzeichnis*/
#toc {
width: 100%;
}

#coordinates {
top: 25px !important;
}


#p-search{
float: none !important;
margin: 0 1em !important;
}


#mw-panel, #p-personal, #left-navigation, #p-views, #p-actions, #p-cactions {
display: none !important;
}

#simpleSearch{
width: 900px !important;
max-width: 100% !important;
margin: 0 auto !important;
padding: 0 !important;
height: auto !important;
border: 0 !important;

}

#simpleSearch input[type=search] {
font-size: 1em !important;
padding: 10px !important;
font-family: serif;
background-color: #eee;
}

#simpleSearch input[type=search]:focus {
background-color: #ddd;
}

#simpleSearch input[type=submit] {
padding: 0 20px;
}

.imeselector.imeselector-toggle {
display: none !important;
}

#right-navigation {
float: none !important;
margin: 0 !important;
}

#content {
margin: 40px auto 0 auto;
border: none;
max-width: 900px;
width: calc(100% - 3em);
padding: 1.5em;
}

#mw-page-base {
display: none;
}

#mw-panel  {
display: none;
}

table{
width: 100% !important;
}

.thumb {
clear:none !important;
}

#footer {
display:none;
}
}
