css:
@-moz-document domain("ratotv.site") {
body {
    background: url('https://images7.alphacoders.com/325/325096.jpg');
    background-repeat: no-repeat;
    background-attachment: fixed;
}
.headertop {
    background-color: rgba(0, 0, 0, 0) !important;
}
.header {
    background: rgba(0, 0, 0, 0) !important;
    -webkit-box-shadow: 0 0px 0 rgba(0, 0, 0, 0.09);
    -moz-box-shadow: 0 0px 0 rgba(0, 0, 0, 0.09);
    box-shadow: 0 0px 0 rgba(0, 0, 0, 0.09);
}
.header .links ul li a {
    font-size: medium;
    color: white;
    font-weight: 500;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 1);
}
.left-content .idTabs li a {
    font-size: small;
}
.main-menu .menu ul li a {
    font-size: 14px !important;
}
div#tvdasogra {
    display: none !important;
}
div#gearbest {
    display: none !important;
}
.no {
    display: none !important;
}
.footer ul li a {
    color: white !important;
}

}
