css:
@-moz-document domain(pikabu.ru) {

body {
background: #333 url(http://totty.ru/181.jpg) center center fixed;
/* Есть еще несколько фоновых картинок 170.jpg 171.jpg 172.jpg */
}

.header-cont {
    width: 100%;
    background: none;
}

.story__rating-count {
color: #fff;
}

.story__title-link_visited_yes {
    color: #fff;
}

.story__header-additional a {
    color: #fff;
}

.story__header-additional {
    color: #fff;
}

.b-user-menu__avatar {
    border: none;
}

.b-user-menu__main {
    border: none;
    background: rgba(0,0,0,.33);
}

.b-feed-panel {
    color: #8f8f91;
    background: rgba(0,0,0,.33);
}

.b-feed-panel a {
    color: #fff;
    border: none;
}

.b-feed-panel {
    color: #fff;
    margin-left: 27px;
    padding: 20px;
}

.b-user-menu-list {
    color: #fff;
}

.b-user-menu__header a {
    color: #fff;
}

.header_links ul li a {
    color: #fff !important;
}

.search_block {
    background: rgba(0,0,0,.33);
}

table#header_t .menu li a {
    color: #fff;
    background: rgba(0,0,0,.33);
}

table#header_t .menu li a {
    color: #fff;
    background: rgba(0,0,0,.33);
}
table#header_t .menu li a:hover {
    color: #fff;
    background: rgba(0,0,0,.55);
}

table#header_t .menu .active a, table#header_t .menu .active a:hover {
    color: #ffffff;
    background: #5190b8;
    border: none;
}

.story__title-link {
    color: #fff;
}

.story__tag {
    background: rgba(0,0,0,.33);
    border: none;
    border-radius: 0px;
}

.b-story__content_type_text {
    border: 1px solid #222;
    background: rgba(0,0,0,.55);
    color: #f1f1f1;
    font-size: 14px; 
}

.stories {
    float: right;
    right: 27px;
    background: rgba(0,0,0,.33);
    padding: 30px 10px;
}

.story__left {
    margin: 0;
    width: auto;
}

.b-story-blocks {
    border: 1px solid #222;
    background: rgba(0,0,0,.55);
    color: #f1f1f1;
    border-radius: 0px;
    font-size: 14px;
}

div.img-viewer>div.iv-frame>div.iv-bg {
    background: rgba(0,0,0,.55);
    box-shadow: none;
}

.b-comment {
    background: rgba(0,0,0,.33);
    color: #f1f1f1;
    padding: 10px;
}

.b-comment__rating-count {
	color: #a7d437 !important;
}

.b-comments {
    border-left: dotted 1px #000;
    background: none;
}

.story__scroll-wrapper_enabled:before {
    background: none;
}

.b-comment-toggle_type_expand {
    color: #999;
    background: rgba(0,0,0,.55);
    padding: 10px ;
    border-radius: 0px;
    left: 10px;
    margin-top: 10px;
}

.b-comment-toggle__count {
	margin: 0px 10px;
}

.b-comments-reply__editable {
    background: rgba(0,0,0,.55);
    border: 2px solid #222;
    border-radius: 0px;
   	color: #fff;
}

.b-comments-reply__attach-files {
    color: #fff;
}

.b-comments-reply__attach-files:hover {
    color: #fff;
    background: rgba(0,0,0,.55);
}

.b-button_type_default {
    color: #fff;
    border:none;
    border-radius: 0px;
    background: rgba(0,0,0,.55);
}
.b-comment__body_highlight_newest:before {
    background: rgba(0,0,0,.77);
}

.b-related-stories>.rs-title {
    color: #333;
    border-bottom: 1px solid #333;
}

.comment_msg {
    color: #fff;
}

.b-community-line__name {
    color: #fff;
}

.b-story__show-all {
    background: rgba(0,0,0,.33);
    box-shadow: none;
}
.b-story__show-all:hover {
    background: rgba(0,0,0,.11);
}

.b-story__show-all span {
    border-bottom: 1px dashed #ccc;
    color: #f1f1f1;
}

.b-story-blocks_with-border .b-story-blocks__wrapper:before {
    background: none;
}
.b-story-blocks__wrapper_slice_yes:before {
    background: none;
}

.story__authors {
    color: #79c36c;
}

td.menu-block-title {
    background: rgba(0,0,0,.33);
    border-radius: 0px;
}

div.menu-block, table.menu-block {
    border: 0px;
    border-radius: 0px;
    background: rgba(0,0,0,.33);
}

.rating_bl {
    font: 14px Tahoma, Arial, sans-serif;
    color: #fff;
    text-shadow: none;
    border: none;
}

.profile_wrap {
    background: rgba(0,0,0,.55);
    border-radius: 0px;
    color: #fff;
}

.profile_wrap > table tr td:nth-child(2) > div > a {
    color: #79c36c;
}

.b-user-profile__label {
    color: #fff;
}

.b-user-profile__value {
    color: #79c36c;
}

.b-user-profile__community span, .b-user-profile__community_more {
    color: #fff
}

.info {
    color: #fff;
}

.tag {
    background: rgba(0,0,0,.33);
    color: #fff;
    text-shadow: none;
    border: none;
    border-radius: 0px;
    padding: 5px;
}

.tag:hover {
    background: rgba(0,0,0,.55);
    color: #fff;
    text-shadow: none;
    border: none;
    border-radius: 0px;
}

.tag_count {
    color: #fff;
}

.tag_all a {
    color: #79c36c;
}

.b-profile {
    color: #fff;
}

#blk-upcoming .a-selected {
    border-bottom: 3px solid #79c36c;
    background: rgba(0,0,0,.55);
    color: #fff;
    padding: 5px;
    height: auto;
}

#blk-upcoming a {
    color: #fff;
}

#blk-upcoming {
    border-bottom: none;
}

#blk-upcoming .a-selected a {
    color: #79c36c;
}

.b-user-menu-list__count {
    color: #79c36c !important;
    background: rgba(0,0,0,.33);
    border-radius: 0px;
}

.b-button_type_green {
    border-radius: 0px;
}

.b-feed-panel a:hover {
    color: #79c36c;
}

.story-search {
    background: rgba(0,0,0,.33);
    margin: 20px 27px 20px 27px;
}

.story-search .story-search-controls>a.story-search-reset {
    color: #79c36c;
}

.story-search table.search-tags {
    color: #fff;
}

.story-search table.search-tags a {
    color: #fff;
    border-bottom: none;
}

.story-search table.search-tags a:hover {
    color: #79c36c;
}

.story-search div.story-adv-search>a {
    color: #fff !important;
    border-bottom: none;
}
.story-search div.story-adv-search>a:hover {
    color: #79c36c !important;
    border-bottom: none;
}

.story-search div.story-adv-search {
    color: #ccc;
}

.story-search div.search-text-field {
    background: rgba(0,0,0,.33);
    border: 1px solid #222;
    border-radius: 0px;
}

.story-search div.search-text-field.ss-selected {
    border: 1px solid transparent;
    border-radius: 0px !important;
    box-shadow: none;
}

.story-search div.search-text-field.ss-selected>input {
    border: none;
    border-radius: 0px !important;
    box-shadow: none;
}

}
