css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("coolapk.com"){body{background-color:#111;color:#999}
a{transition:all .2s ease!important}
.ex-card-app-list li .media-heading a:hover,.ex-card-header .media-body a:hover,a:focus,a:hover{color:#50A6FA;text-decoration:none!important;text-shadow:1px 1px 3px #000}
.ex-page-header{border:0;background-color:#1e1e1e;box-shadow:0 1px 3px #000}
.ex-page-topbar h1{text-shadow:1px 1px 2px #111}
.navbar-inverse{border:0;background-color:#262626;box-shadow:0 1px 3px #111}
.ex-card.panel-default,.ex-well{padding:0 0 1px;border:0;background-color:#1e1e1e;box-shadow:1px 1px 3px #000}
.ex-card-album-app-list,.ex-card-body.row.ex-flat-list,.ex-card-quick-comment .ex-card-comment-more,.ex-card.panel-default>.panel-footer,.ex-card.panel-default>.panel-heading,.panel.panel-default.ex-card.ex-card-btns{border:0;background-color:transparent;box-shadow:none}
.ex-card-app-list{padding:8px 0}
.ex-card-album-list .media,.ex-card-app-list li,.ex-card-category-list .media{margin:8px 10px;padding:10px;border:0;border-radius:3px;background-color:#262626;box-shadow:1px 1px 3px #060606;transition:all .25s ease}
.ex-welcome{padding:10px;border:0;border-radius:3px;background-color:#262626;box-shadow:1px 1px 3px #060606;transition:all .25s ease}
.ex-card-album-list .media:hover,.ex-card-app-list li:hover,.ex-card-category-list .media:hover{background-color:#333}
.ex-card-album-list>.row{margin:8px 10px;border:0;border-radius:3px;background-color:#262626;box-shadow:1px 1px 3px #060606;transition:all .25s ease}
.ex-card-album-list .media .media-heading a,.ex-card-app-list li .media-heading a,.ex-card-category-list .media .media-heading a,.ex-nav-tabs>li.active>a,.ex-page-infobar div>a,.ex-page-infobar div>a>span>strong,.ex-page-topbar h1.ex-apk-view-title,.ex-side-album-list .media .media-heading a{color:#ccc}
.ex-page-infobar div>a:hover{color:#eee}
.ex-card-header .media-heading a.username{color:#eee;text-shadow:1px 1px 2px #111}
.ex-card-mediaView{border:0;border-radius:3px;background-color:#262626;box-shadow:1px 1px 3px #060606}
.ex-flat-list{background-color:#1e1e1e}
.ex-flat-list>.list-group-item{border:0;background-color:transparent;color:#999}
.ex-flat-list>.list-group-item:focus,.ex-flat-list>.list-group-item:hover{background-color:#2e2e2e;color:#ccc}
.dropdown-menu{padding:0;border:0;background-color:#262626;box-shadow:0 3px 3px #111}
.dropdown-menu .divider,.pager{display:none!important}
.dropdown-menu>li:last-child>a{border-radius:0 0 4px 4px}
.dropdown-menu>li>a{padding:4px 6px;color:#999;transition:all .25s ease}
.dropdown-menu>li>a:focus,.dropdown-menu>li>a:hover{background-color:#333;color:#eee}
.ex-apk-vote,.ex-card-comment,.ex-card-comment .ex-card-comment-title,.ex-card-quick-comment,.ex-card-quick-comment .ex-card-comment-title,.ex-flat-list>.form-group{border:0;background-color:transparent}
.ex-card-comment .ex-card-comment-list li,.ex-card-quick-comment .ex-card-comment-list li,.ex-card-small-list li{margin:5px 0;padding:6px 6px;border:0;border-radius:3px;background-color:#262626;box-shadow:1px 1px 3px #111;transition:all .25s ease}
.ex-card-small-list{padding:0 6px}
.ex-card-small-list li:hover{background-color:#333}
.ex-card-dashboard .ex-card-backdrop{background-color:#161616}
.form-control{border:0;background-color:#0e0e0e;box-shadow:inset 1px 1px 3px #000;color:#999}
.btn-default,.btn-success,.ex-flat-list>.form-group>.btn-default,.ex-reply-form>.input-group>.input-group-addon.btn-success,.input-group-addon,.pagination>.disabled>a,.pagination>.disabled>span{border:0;background-color:/*[[btn]]*/;box-shadow:1px 1px 2px #111;color:#aaa!important;transition:all .25s ease}
.btn-default.active,.btn-default:active,.btn-default:focus,.btn-default:hover,.btn-success.active,.btn-success:active,.btn-success:focus,.btn-success:hover,.ex-flat-list>.form-group>.btn-default:hover,.ex-reply-form>.input-group>.input-group-addon.btn-success:hover,.open>.dropdown-toggle.btn-default,.open>.dropdown-toggle.btn-success,.pagination>.active>a,.pagination>.active>a:focus,.pagination>.active>a:hover,.pagination>.active>span,.pagination>.active>span:focus,.pagination>.active>span:hover,.pagination>.disabled>a:focus,.pagination>.disabled>a:hover,.pagination>.disabled>span:focus,.pagination>.disabled>span:hover{border:0;background-color:/*[[btnh]]*/;color:#fff!important}
.navbar-inverse .navbar-nav>.open>a,.navbar-inverse .navbar-nav>.open>a:focus,.navbar-inverse .navbar-nav>.open>a:hover{background-color:transparent}
.ex-nav-tabs>li>a,.nav-tabs>li>a{border:0!important;background-color:#161616}
.ex-nav-tabs>li.active>a:hover,.ex-nav-tabs>li>a:hover,.nav-tabs>li.active>a:hover,.nav-tabs>li>a:hover{background-color:#1e1e1e;color:#fff}
.ex-nav-tabs>li.active>a,.ex-nav-tabs>li.active>a:focus,.ex-nav-tabs>li>a:focus,.nav-tabs>li.active>a,.nav-tabs>li.active>a:focus,.nav-tabs>li>a:focus{background-color:#1e1e1e;color:#bbb}
.col-sm-6{margin:0!important;background:0 0!important;box-shadow:none!important}
.ex-apk-rank .ex-apk-rank-intro .ex-apk-rank-score{color:#999;text-shadow:1px 1px 2px #111}
}
