css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('.*\.pretendyoure\.xyz\/zy\/game.jsp.*') {
  body {
    background-color: #232323 !important;
    color: white !important;
  }

  .scorecard:nth-child(odd) {
    background-color: #333 !important;
  }
  .scorecard:nth-child(even) {
    background-color: #444 !important;
  }

  .game_options {
    background-color: #444 !important;
  }

  #tabs {
    background-image: none !important;
    background-color: #333 !important;
  }

  .ui-tabs-nav {
    background-image: none !important;
    background-color: #333 !important;
    border: none !important;
  }

  .ui-state-default {
    background: none !important;
    background-color: #222 !important;
    border: 3px white !important;
  }

  .ui-state-active {
    background-color: #444 !important;
  }

  .log {
    background-color: #444 !important;
  }

  .log span {
    color: #eee !important;
  }

  .log span:nth-of-type(even) {
    background-color: #222;
  }

  .chat {
    background-color: #555 !important;
    color: white !important;
  }

  .ui-tabs-panel {
    background-color: #444 !important;
    color: white !important;
  }

  .ui-tabs-panel select, .ui-tabs-panel textarea {
    background-color: #333 !important;
    color: white !important;
  }

  .game_hand_filter_text {
    background-color: #333 !important;
  }

  .whitecard {
    background: none;
    background-color: #444 !important;
    color: white;
  }

  .whitecard.selected {
    background-color: #3C7FB1 !important;
  }
  
  .gamelist_lobby {
    background: none;
    background-color: #222 !important;
  }
}

@-moz-document domain("pretendyoure.xyz") {
  body {
  background: #333 !important;
  color: white !important;
  }

  a {
    color: #337ab7 !important;
  }

  a:visited {
    color: #CCFFFF !important;
  }
}
