css:
@-moz-document url-prefix("https://neixin.cn") {
::-webkit-scrollbar-track {
    background-color: initial;
    -webkit-border-radius: initial;
    border-radius: initial;
}
::-webkit-scrollbar {
    width: 2px;
    height: initial;
}
::-webkit-scrollbar-thumb {
    background-color: #8e8f90;
    -webkit-border-radius: initial;
    border-radius: initial;
    outline: initial;
    outline-offset: initial;
    border: initial;
}
::-webkit-scrollbar-thumb:hover {
    background-color: initial;
    -webkit-border-radius: initial;
    border-radius: initial;
}
.comp-session {
    background-color: rgb(54, 61, 74);
}
.comp-session .notice {
    color: #cae5f9;
}
.comp-session:hover {
    background-color: rgb(98, 107, 125);
}
.wrapper-main .wrapper-navbar {
    border-right: 1px solid #8e8f90;
}
.comp-session.selected {
    background: rgb(79, 88, 105);
}
.comp-session .name .content {
    color: #e8ad56;
}
.wrapper-left-chat .wrapper-suggest {
    border-bottom: 1px solid rgb(85, 94, 111);
    background-color: #363d4a;
}
.titlebar {
    background-color: hsl(219, 16%, 25%);
    color: #f4f4f4;
    border-bottom: 1px solid rgb(85, 94, 111);
}
.wrapper-msglist-inner .bubble_list_message {
    background-color: #353c4a;
}
.comp-suggest .ctn-ipt>input {
    background-color: rgb(79, 88, 105);
    color:#fff;
}
.medium-size .wrapper-msglist-inner .time-line .line {
    border-top: 1px solid rgb(76, 86, 107);
}

.wrapper-right-chat .wrapper-right-outer .wrapper-right-background .wrapper-right-inner .wrapper-ipt {
    background-color: #353c4a;
}
.wrapper-msglist {
    color: #c3c5c9;
}
.medium-size .message-text {
    background-color: rgba(17,139,250,.05);
    border:none;
}
.text-input textarea {
    color: #353c4a;
}
.medium-size .me .message-text {
    float: right;
    background-color: #334053;
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right.rfc {
    color: #334053;
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left.lfc {
    color: #334053;
}
.comp-dynamic-loader {
    background-color: #353c4a;
}
.wrapper-right-chat .wrapper-right-outer .wrapper-right-background .wrapper-right-inner.wrapper-chat-inner.showslidebar .slidebar {
    background-color: #353c4a;
    border-left: 1px solid rgb(85, 94, 111);
}
.group-members-list .member .name {
    color: #8d8e90;
}
.bubble-groupinfo .bubble-groupinfo-name {
    color: #e3e3e5;
}
.bubble-groupinfo .bubble-groupinfo-managers .bubble-groupinfo-manager span {
    color: #e3e3e5;
}
.comp-suggest .ctn-ipt {
	background-color: #353c4a;
}
.titlebar .title .title-menu-panel .title-menu .title-menu-item {
    color: #353c4a;
}
.titlebar .title .title-menu-panel .title-menu .title-menu-item {
    color: #353c4a;
}
.medium-size .you .message-long-text {
    background-color: #334053;
    color: #bbbec2;
}
.text-input textarea {
    color: #b5b9be;
}
.medium-size .you .message-video {
    background-color: #334053;
}
}
