css:
@-moz-document domain("speedrun.com") {

body {
    font-family: tahoma;
}
#miscbutton {
    padding-right: 55px!important;
}
.navbar.navbar-fixed-top.navbar-inverse {
    position: static;
    margin-top: -133px;
}
#sidebar li a {
    height: 27px;
    padding-top: 3px;
    background: none;
}
.panel.panel-body {
    padding-bottom: 27px!important;
}
.panel-body table {
    margin-top: 15px!important;
}
.nobr.center.hidden-xs {
    font-family: segoe ui semibold!important;
}
.table.margintop.striped tr td {
    background-color: rgba(0, 0, 0, 0.6);
    border: 1px solid #fff!important;
}
.blackoutline.username.color7 {
    color: #3369F9!important;
}
.blackoutline.username.color6 {
    color: #54B4E4!important;
}
.blackoutline.username.color12 {
    color: #954CD4!important;
}
.blackoutline.username.color9 {
    color: #FC83D0!important;
}
.blackoutline.username.color5 {
    color: #378C48!important;
}
.blackoutline.username.color4 {
    color: #A5F64E!important;
}
.blackoutline.username {
    text-shadow: #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px;
}
.panel.panel-body.panel-tabbed .row button,
.panel.panel-body.panel-tabbed .row a,
.category a {
    background: rgba(0, 0, 0, 0.6);
    color: #fff!important;
    border: 2px solid #000!important;
    text-shadow: #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px, #000 0px 0px 2.6px;
}
.dropdown-menu.dropdown-menu-right a,
.dropdown-submenu,
.dropdown-menu {
    background-color: rgba(0, 0, 0, 0.8);
    border: none!important;
    text-shadow: #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px!important;
    outline-color: #111!important;
}
.dropdown-menu.dropdown-menu-right a:hover {
    background-color: rgba(35, 35, 35, 0.8)!important;
}
.navbar.navbar-fixed-top.navbar-inverse a {
    color: #fff!important;
}
.navbar.navbar-fixed-top.navbar-inverse a:hover {
    background: rgba(55, 55, 55, 0.8)!important;
}
.dropdown-header {
    color: #fff!important;
    font-size: 125%!important;
}
.followbutton button,
.btn.btn-primary button {
    background: rgba(0, 0, 0, 0.6)!important;
    border: 2px solid #000!important;
    text-shadow: #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px!important;
    outline-color: #111!important;
}
.modal-content {
    background: rgba(0, 0, 0, 0.8)!important;
    color: #fff!important;
    border: 2px solid #222!important;
}
.modal-content a {
    background: #212121!important;
    color: #fff!important;
    border: 2px solid #1a1a1a!important;
    text-shadow: #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px, #000 0px 0px 1px!important;
}
}
