css:
@-moz-document domain("feedly.com") {
/*Old Version - with drop shadow effect - will make the browser very slow*/

#feedlyPage {
    width: 100%!important;
}
#mainBar {
    width: 100%!important;
    background: #DDD;
}
#feedlyPart0.area {
    background: #DDD;
}
.sliderContainer {
    width: 800px;
}
/* list mode */
.u0Entry{border:none;}

/*card mode*/

.u5Entry {
    margin: 5px;
    position: relative;
    border-radius: 0;
    border: none!important;
    background: none;
}
.u5Entry * {
    transition: 0.2s;
}
.u5Entry >div {
    background: #FFF;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2);
    border-radius: 4px;
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
}
.u5Entry:hover >div {
    top: -3px;
    box-shadow: 0 5px 8px rgba(0, 0, 0, .4)!important;
}
.u5Entry.selectedEntry >div {
    box-shadow: 0 0 2px #F69;
}
.u5Entry .visual {
    width: 100%;
    border-bottom: none;
    box-shadow: 0 -1px 0 rgba(0, 0, 0, .05) inset;
}
@media screen and (max-width: 1080px) {
    .u5Entry {
        width: 225px;
        height: 300px;
    }
    .u5Entry .visual {
        height: 225px;
    }
}
@media screen and (min-width:1081px) and (max-width: 1366px) {
    .u5Entry {
        width: 198px;
        height: 273px;
    }
    .u5Entry .visual {
        height: 198px;
    }
}
@media screen and (min-width:1367px) and (max-width: 1920px) {
    .u5Entry {
        width: 213px;
        height: 289px;
    }
    .u5Entry .visual {
        height: 213px;
    }
}
/*title*/

.u5Entry * {
    line-height: 20px!important;
    margin: 0!important;
}
.u5Entry .info {
    height: 40px;
    padding: 5px 10px;
}
.u5Entry .title {
    height: 40px;
}
/*text*/

.u5Entry >div >div:last-child {
    padding: 0 10px!important;
}
.u5Entry .summary {
    display: none;
}
/*hot post*/

.u5Entry .metadata span {
    display: block;
    float: left
}
.nbrRecommendations {
    padding-right: 10px;
    background-position: right 3px!important
}
.u5Entry .sourceTitle {
    display: inline-block;
    max-width: 150px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.metadata a:hover {
    text-decoration: none;
    color: #F69
}
.u5Entry .noiseMarker, .u5Entry .wikiBar {
    display: none!important
}
.unread {
    color: #F69;
}
/*search*/

#herculeFrame {
    background-color: rgba(0, 0, 0, .05);
}
/*full articles mode*/

.u100Frame {
    background: #FFF;
    border: none!important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .2);
    border-radius: 4px;
}
/*bottom - mark all as read*/

.secondaryCallForAction {
    background-color: #CCC;
    border: 1px solid #BBB;
}
.secondaryCallForAction:hover {
    background-color: #FFF;
    color: #F69;
}
}
