css:
@-moz-document domain("icloud.com") {
/* 适用于 75% 缩放 for Chrome*/
.atv4.cw-picker.sc-pane.sc-picker.sc-panel{zoom:1.333333;}


/* =====Calendar===== */
/* 左上角下拉箭头 */
.atv4.icloud-controls .sc-button-view .chevron{zoom:1.333333;}

/* 顶部日期左右箭头 */
.atv4.calendar.navigation-control .arrow{zoom:1.333333;top:-3px!important;}

/* 右上角帮助按钮 */
.account-menu.icloud-controls .sc-button-view .help-icon{zoom:1.333333;}


/* sidebar checkmark */
.atv4.calendar.sc-list-item-view.calendar-item .sc-checkbox-view{zoom:1.333333;left:0;}
/* sidebar right icon */
.atv4.calendar.sc-source-list .sc-list-item-view .icon-container img.right-icon{zoom:1.333333;}
/* sidebar birthday icon */
.atv4.calendar.sc-list-item-view.calendar-item .birthday-icon{zoom:1.333333;margin-top:-6px;}
/* middle area birthday icon */
.event-canvas .event .event-birthday-icon, .event-birthday-icon{zoom:1.333333;}
.atv4.calendar.allday-events div.event.birthday .title{margin-left:16px;}

/* bottom left setting */
.atv4.calendar.left .sidebar-footer >div:first-child{top:4px!important;}
.sidebar-footer .atv4.sc-regular-size.borderless.button,.main-footer .first-section .atv4.sc-regular-size.borderless.button{zoom:1.333333;width:44px!important;height:44px!important;top:0!important;left:0!important;}
.atv4.sc-regular-size.borderless.button .sc-button-label{top:0;margin-top:-2px;}

/* bottom middle */
/* calendar icon */
.atv4.calendar.right.main-content .main-footer .middle-section >div:first-child{zoom:1.333333;top:-4px!important;}
/* download icon */
.atv4.calendar.right.main-content .main-footer .middle-section >div:last-child{zoom:1.333333;top:-3px!important}
/* plus icon */
.atv4.calendar.button .add-event-icon{zoom:1.333333;top:6px!important;}
}
