css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kakuyomu.jp") {

/* 全体 */
  main#contentMain{max-width:1000px;margin:0px auto;}
  #page-works-episodes-episode #contentMain-header-cover {
    padding: 20px 0px;
    overflow: hidden;border-radius:5px;
}
  
/* タイトル */
  #page-works-episodes-episode #contentMain-header-cover #contentMain-header-workTitle {
    font-weight: bold;
    font-size: 1.75em;
    padding: 20px 10px;
}
  
/* 作者 */ 
  #page-works-episodes-episode #contentMain-header-cover #contentMain-header-author {
    font-size: 1em;
    color: #888;
}
  #contentMain-header-arrow{display:none;}
/* サブタイトル */
  #page-works-episodes-episode #contentMain-header .chapterTitle.level1 {
    font-size: 1.7em;
    color: #0990CC!important;
    font-weight: bold;
    padding:5px;padding-top:30px;
    margin-top:20px;margin-bottom:0px;
}
  #page-works-episodes-episode #contentMain-header .chapterTitle {
    max-width: 100%;margin:0px auto;
}

  .widget-episodeTitle {
    font-size: 1.5em;
    color:#0990CC!important;
    vertical-align: top;
    line-height: 1.4em;
    font-weight: bold;
    background: #fff;
    margin: 0 0 1em;
    padding:20px;
    max-width:100%;
}
  
#page-works-episodes-episode #contentMain-header .widget-episodeTitle {
    margin: 0px 0 30px;
    color: #222;
}

/* 本文 */  
  .widget-episode-inner {
    text-align: left;
    display: inline-block;
    max-width: 98%;
}
  .widget-episodeBody {
    line-height: 1.9em;
    font-size: 0.9em;
}
.widget-episodeBody p {
    margin: 0.5em;
}

/* 本文フォント設定（メイリオとかの方が良い場合は消してください） */
  .isPC .widget-catchphrase .widget-catchphrase-title, .isTouch .widget-catchphrase .widget-catchphrase-title, .widget-episodeBody {
    font-family: "ＭＳ ゴシック",sans-serif;
}
  
/*フッター*/  
  #page-works-episodes-episode #contentMain-nextEpisode a {
    padding: 10px 10px 0px;margin:0px;
    line-height: 1.4;
}
  
}
