@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?:\\/\\/(www\\.)?minecraft.fr(?!\\/forum).*") {
  
  /* Body */
  html {
    background-attachment: fixed;
    background-image: url("https://minecraft.fr/wp-content/themes/sage/dist/images/bg.png");
    background-repeat: repeat;
    overflow-y: scroll !important;
  }
  body {
    background: none;
  }
  .content-info, .sidebar {
    background: none !important;
  }
  
  /* Header */
  .userbar {
    background-color: #111 !important;
    border-bottom: 1px solid #000;
  }
  .ubermenu-nav {
    background: #111 !important;
    border-bottom: 4px solid rgb(84, 149, 169) !important;
  }
  
  /* Home */
  .wrap.container {
    background-color: #111;
    border-bottom: 1px solid #000;
    border-left:   1px solid #000;
    border-right:  1px solid #000;
    padding-right: 14px; /* To avoid overflow */
    border-radius: 0 0 3px 3px;
  }
  .articles {
    background-color: #282828;
    border-color: #000;
    margin-bottom: 32px;
  }
  .articles article {
    background-color: #111 !important;
    border: 1px solid #000;
  }
  .articles article .right {
    border-bottom: 4px solid rgb(84, 149, 169) !important;
    background-color: #222 !important;
  }
  .articles article .categories {
    background-color: #181818 !important;
    border-top: 1px solid #000;
  }
  .fa-star {
    background-color: rgb(84, 149, 169) !important;
    border-bottom-color:  rgb(14, 129, 149) !important;
  }
  
  /* Article */
  .single-post #videoDailymotion {
    display: none; /* pwn3d */
  }
  .single-post article, .page article, .single-post .row, .single-post #comments {
    background-color: #222 !important;
    border: 1px solid #000;
    border-bottom: 4px solid rgb(84, 149, 169) !important;
  }
  .single-post header:not(.banner) div {
    background-color: #181818 !important;
  }
  .single-post figure, .page figure {
    background-color: #181818;
    border: 1px solid #000;
  }
  .content .main .row .correcteur {
    background-color: #181818 !important;
    border-color: #000;
  }
  .comment article {
    border: 1px solid #000 !important;
    border-radius: 4px;
  }
  .comment-metadata, .comment-author {
    background-color: #111 !important;
    border: none !important;
  }
  .comment-content {
    background-color: #181818;
    border-top: 1px solid #000;
  }
  .links i {
    background-color: rgb(84, 149, 169) !important;
  }
  .comment-reply-login {
    background-color: #a11 !important;
    border-color: #811 !important;
  }
  
  /* Static */
  .content .page-header {
    border-color: #000;
  }
  .alert {
    background-color: #a11 !important;
    border-color: #811 !important;
    color: #fff;
  }
  .partenaire {
    background-color: #222 !important;
    border: 1px solid #000;
  }
  .partenaire img {
    background-color: #181818 !important;
    border: 1px solid #000;
  }
  
  /* Sidebar */
  .search-field, .search-submit, .search-submit:hover, .search-submit:focus {
    background-color: #333;
    border-color: #000;
  }
  .search-submit {
    color: rgb(84, 149, 169);
  }
  .search-submit:hover, .search-submit:focus {
    background-color: #444;
    color: #ccc;
  }
  .social { /* Hey fuck off m8 */
    display: none;
  }
  
  /* Menus */
  .ubermenu-submenu-drop {
    background-color: #222 !important;
    border-color: #000 !important;
    box-shadow:         0px 5px 10px 0px #000 !important;
    -webkit-box-shadow: 0px 5px 10px 0px #000 !important;
  }
  .ubermenu-item-level-1 {
    background-color: #222 !important;
    color: #aaa !important;
  }
  .ubermenu-item-level-1 a {
    color: #aaa !important;
  }
  
  /* Boutons */
  .reply a {
    background-color: rgb(84, 149, 169) !important;
    border-color: #000 !important;
  }
  
  /* Pagination */
  .page-numbers:not(.dots) {
    background-color: #181818;
    border-color: rgb(84, 149, 169);
  }
  .page-numbers:not(.dots).current {
    background-color: rgb(84, 149, 169);
    border-color: rgb(84, 149, 169);
  }
  
  /* Text */
  a {
    color: rgb(84, 149, 169);
  }
  a:hover, a:focus {
    color: #aaa;
  }
  .brand, .entry-title a {
    color: rgb(84, 149, 169) !important;
  }
  .entry-title a:hover, .entry-title a:focus {
    color: #aaa !important;
  }
  .description, .userbar, .published, .content article, .content p, .content ol, .content figcaption, .row, .partenaire h3 {
    color: #aaa !important;
  }
  .entry-title, .sidebar h3, .content h1, .content h2, .comment-reply-title {
    color: #ccc !important;
  }
}

@-moz-document regexp("https?:\\/\\/(www\\.)?minecraft.fr\\/forum.*") {
  
  /* Header */
  .pageContent {
    background-color: #111 !important;
    border-color: #000 !important;
  }
  #logoBlock .pageContent{
    background: transparent linear-gradient(to bottom, #181818 0px, #111 100%) repeat scroll 0 0;
  }
  #searchBar .textCtrl { /* Search */
    background-color: #333;
    border-color: #555;
  }
  
  /* General sections */
  .sectionMain {
    background-color: #222 !important;
    border-color: #000 !important;
  }
  .mainContent, .mainContainer_noSidebar {
    background: none !important;
    border: none !important;
  }
  .secondaryContent, .primaryContent {
    background-color: #222;
    border-color: #000 !important;
  }
  .sectionHeaders, .sectionFooter {
    background-color: #141414 !important;
    border-color: #111 !important;
  }
  
  /* Tabs */
  .tabs {
    border-color: #000;
  }
  .tabs a{
    background-color: #181818 !important;
    border-color: #000 !important;
  }
  .tabs .active a{
    background-color: #111 !important;
    border-color: #000 !important;
  }
  
  /* ForumsList */
  .category .nodeList {
    border-color: #000 !important;
    background-color: #111 !important;
  }
  .nodeInfo {
    background-color: #222 !important;
    border-color: #000 !important;
    border-top: 1px solid #000 !important;
    padding: 8px 8px 0 8px !important;
  }
  .categoryNodeInfo {
    background-color: rgb(84, 149, 169) !important;
    border: 1px solid #000 !important;
    height: 25px !important;
  }
  .categoryNodeInfo .categoryText {
    height: 20px !important;
  }
  .nodeDescription {
    display: none;
  }
  
  /* ThreadsList */
  .threadListSeparator {
    background-color: #181818;
    border-color: #111;
  }
  .discussionListItem {
    background-color: #222 !important;
    border-color: #111;
  }
  .discussionListItem:nth-child(2n) {
    background-color: #282828 !important;
  }
  .discussionListItem .stats .major, .discussionListItem .stats .minor{
    border-left:  1px solid #111;
    border-right: 1px solid #111;
  }
  .DiscussionListOptions {
    background-color: #333;
    border-color: #111;
  }
  #DiscussionListOptionsHandle a{
    background-color: #222;
    border-color: #000;
  }
  .nodeList, .nodeList.section {
    border-color: #000 !important;
    background: none !important;
  }
  
  /* Thread */
  .tag {
    background-color: #222 !important;
    border-color: #000 !important;
  }
  .tag:after {
    border-color: #000 !important;
    background-color: #222 !important;
  }
  .tag .arrow, .tag .arrow:after {
    border-color: transparent #000 transparent transparent !important;
  }
  .threadAlerts {
    background-color: #222 !important;
    border-color: #000 !important;
  }
  .messageUserBlock {
    border-color: #000;
    background-color: #222;
  }
  .messageUserBlock .arrow {
    border-color: transparent;
    -moz-border-left-colors: #000;
  }
  .messageUserBlock .arrow span{
    border-color: transparent;
    -moz-border-left-colors: #181818;
  }
  .bbCodeQuote, .attribution, .quoteContainer, .bbCodeCode, .bbCodeCode .type, .bbCodeCode pre, .bbCodeSpoilerText, .attachedFiles, .attachedFilesHeader, .attachmentList, .messageInfo .likesSummary {
    background-color: #181818 !important;
    border-color: #000 !important;
  }
  .quoteExpand {
    background: transparent linear-gradient(to bottom, transparent 0%, rgba(0, 0, 0, 0.4) 80%) repeat scroll 0 0 !important;
  }
  .attribution, .bbCodeCode .type, .attachedFilesHeader {
    color: rgb(84, 149, 169) !important;
  }
  .bbCodeCode pre {
    background-image: -moz-repeating-linear-gradient(center top, transparent 0px, transparent 26px, rgba(0, 0, 0, 0.15) 26px, rgba(0, 0, 0, 0.15) 52px) !important;
  }
  .attachment .boxModelFixer {
    background-color: #222;
  }
  .attachment .thumbnail {
    border-color: #000;
  }
  .signature {
    border-color: #000 !important;
  }
  .newIndicator {
    background-color: rgb(84, 149, 169) !important;
  }
  .avatarHolder, .extraUserInfo, .messageUserInfo {
    background-color: #181818 !important;
    border-color: #000;
  }
  .userText {
    background-color: #111 !important;
    border-color: #000 !important;
  }
  .onlineMarker { /* Needs negation from .Tooltip if enabled*/
    background-color: rgb(127, 185, 0);
  }
  
  /* NewMessage */
  .redactor_box, .redactor_toolbar {
    border-color: #000;
  }
  .redactor_toolbar {
    background-color: #111;
  }
  .redactor_toolbar li {
    background-color: #333 !important;
  }
  .redactor_toolbar > li {
    background-color: #000 !important;
  }
  .redactor_toolbar ul {
    background-color: #222 !important;
    border-color: #000 !important;
  }
  .redactor_toolbar a {
    filter: brightness(4);
    -webkit-filter: brightness(4);
  }
  /* #ctrl_message_html, .editor_contents, .BbCodeWysiwygEditor, .placeholder, html { //TODO
    background-color: #0d0 !important;
    display: block !important;
  } */
  
  /* MessagePreview */
  .PreviewContainer .messageText {
    border-left:  1px solid #000;
    border-right: 1px solid #000;
  }
  
  /* MessagesList */
  .messageSimpleList {
    border-color: #000 !important;
  }
  .messageSimple {
    border: 1px solid #000;
    border-radius: 3px;
    padding: 10px;
  }
  .comment, .commentMore, .likesSummary {
    border-color: #111 !important;
  }
  
  /* MemberList */
  .memberListItem {
    border: 1px solid #000;
    border-radius: 3px;
    margin-bottom: 4px;
  }
  
  /* Alerts */
  .alertGroup h2 {
    border-color: #000;
  }
  
  /* MainProfile */
  .profilePage .mainText{
    background: #181818;
    border-top:   1px solid #000 !important;
    border-right: 1px solid #000 !important;
  }
  .profilePage .lastActivity {
    border-color: #000 !important;
  }
  .mainProfileColumn {
    border-color: #000 !important;
  }
  .profilePage .infoBlock {
    border-color: #000 !important;
  }
  .profilePage .avatarScaler img{
    border-radius: 5px;
    border: 1px solid #000;
    background-color: inherit;
    width: 99%;
  }
  .profilePage .infoBlock h3 {
    background-color: #111 !important;
    border-color: #000 !important;
  }
  
  /* Navig */
  .mainTabs {
    background-color: inherit !important;
  }
  .navTabs {
    background-color: #111 !important;
  }
  .tabLinks {
    background-color: #222 !important;
  }
  .navTab.selected .navLink {
    background-color: #222 !important;
    border: none !important;
  }
  .PageNav a:not(.currentPage) {
    background-color: #111;
    border-color: #000;
  }
  .currentPage {
    background-color: rgb(84, 149, 169) !important;
    border-color: rgb(84, 149, 169) !important;
  }
  #uix_jumpToFixed {
    background-color: #222;
    border: 1px solid #000;
  }
  
  /* Breadcrumb */
  .breadcrumb {
    background-color: #222;
    border-color: #000;
    bottom: 10px;
  }
  .crumb {
    background-color: #333 !important;
    border-color: #000 !important; 
  }
  .breadcrumb .arrow span {
    border-color: transparent #333 transparent #333 !important;
  }
  .breadcrumb .arrow {
    border-color: transparent #000 transparent #000 !important;
  }
  
  /* Sidebar */
  .sidebarToggle {
    background-color: #222 !important;
    border-color: #000 !important;
  }
  .sidebar .secondaryContent h3 {
    background: #111 !important;
    border-color: #000 !important;
  }
  .sidebar .profilePostListItem, .sidebar .threadListItem {
    border-color: #000;
  }
  .secondaryContent h3 blockquote { /*Removing ugly message*/
    display: none;
  }
  
  /* AccountSidebar */
  .navigationSideBar ul{
    background-color: #181818;
    border-color: #000;
  }
  .navigationSideBar .LogOut {
    border-top: 1px solid #000;
  }
  
  /* Shoutbox */
  .taigachat_motd {
    display: none;
  }
  #taigachat_box, #taigachat_message {
    background-color: #181818;
    border-color: #000;
  }
  #taigachat_send { /* Fix shoutbox button height */
    height: 100% !important;
  }
  .taigachat_messagetext {
    color: #ccc;
  }
  
  /* Buttons */
  .button:not(#taigachat_usercolorpicker), .choosers dd a { /* Not targeting .Tooltip */
    background-color: #222 !important;
    border: 1px solid #000 !important;
  }
  #taigachat_usercolorpicker { /*Fix for Diagamma's userscript */
    background-color: #222;
    border: 1px solid #000 !important;
  }
  .bbCodeSpoilerButton, .privateControls a:not([href*=profile-posts]), .publicControls a:not([href*=profile-posts]) { /* Trick for not targeting profile-posts */
    background-color: #181818 !important;
    border-color: #000 !important;
    color: #aaa !important;
  }
  .uix_socialLink a, #SignupButton a, .fbLogin {
    background-color: rgb(84, 149, 169) !important;
  }
  .PopupControl {
    color: #aaa;
  }
  
  /* Fields */
  .textCtrl {
    background-color: #222;
    color: #aaa;
    border-color: #000;
  }
  .progress-bar {
    background-color: rgb(84, 149, 169) !important;
  }
  .ProgressMeter {
    background-color: #181818 !important;
    border-color: #000 !important;
  }
  .ProgressGraphic {
    background-color: rgb(84, 149, 169) !important;
  }
  .AttachedFile .Thumbnail {
    background-color: #181818 !important;
    border-color: #000 !important;
    border-radius: 3px;
  }
  .questionMark {
    background-color: #181818 !important;
    border: 1px solid #000;
    color: rgb(84, 149, 169) !important;
  }
  .pollOptions, .pollResults {
    border-color: #000 !important;
  }
  .pollOptions label:hover, .pollOptions label:focus {
    background-color: #111 !important;
  }
  .votedIconCell {
    color: rgb(84, 149, 169);
  }
  .barContainer {
    border-color: rgb(84, 149, 169) !important;
  }
  .bar {
    background-color: rgb(84, 149, 169) !important;
  }
  fieldset {
    border-color: #000 !important;
  }
  
  /* Overlay */
  #exposeMask {
    background-color: #000 !important;
  }
  .styleChooser {
    border-color: #000 !important;
  }
  .subHeading {
    background-color: #111;
    border-color: #000;
  }
  .xenOverlay form {
    border-color: #000 !important;
  }
  .xenPreviewTooltip {
    background-color: #181818;
    border-color: #000;
    box-shadow:         0px 5px 10px 0px #000;
    -webkit-box-shadow: 0px 5px 10px 0px #000;
  }
  .xenPreviewTooltip .posterDate {
    border-color: #000;
  }
  .xenPreviewTooltip .arrow {
    border-color: #000 transparent #000;
  }
  .xenPreviewTooltip .arrow span {
    border-color: #181818 transparent #000;
  }
  .formOverlay { /*Login*/
    background-color: #181818 !important;
  }
  .formOverlay h2, .formOverlay .uix_loginOptions {
    border-color: #000 !important;
  }
  
  /* Menus */
  .Menu {
    border-color: #000;
  }
  .menuHeader {
    background-color: #111 !important;
  }
  .menuColumns {
    background-color: #111 !important;
  }
  .menuColumns ul{ /* Inner borders to avoid overflow */
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    border: 1px solid #000;
  }
  .menuColumns ul:first-child{ /* Ugly thing for alignement... */
    left: 1px;
    position: relative;
  }
  .menuColumns .LogOut { /* Ugly thing for alignement again... */
    height: 16px;
  }
  .blockLinksList {
    padding-bottom: 0;
  }
  .Menu .listPlaceholder ol, .blockLinksList li {
    background-color: #222 !important;
  }
  .PopupItemLink[href*=conversations] {
    background-color: transparent !important;
  }
  .Menu .Alert {
    border-color: #000;
  }
  .Menu .listItem {
    border-color: #000;
    border: 1px solid transparent !important; /* To keep size constant */
  }
  .Menu .listItem:hover {
    background-color: #181818;
    border: 1px solid #000 !important;
    transition: all 0.2s;
  }
  
  /* Texts */
  .titleBar, .threadListSeparator, .mainText h1 {
    color: #eee;
  }
  .title, .secondaryContent h3, .secondaryContent h3 a, .tabs a, .textHeading, .menuHeader h3, .pollContent h2, .threadAlerts dt {
    color: #ccc !important;
  }
  .stats .major, .stats .minor, .statsList, .stats dd , .stats dt, .pollOptions label, .pollResult div, .messageContent, .messageText, .commentContent, .userStats, .userStatus, .userBlurb, .lastActivity dd, form dt, .ctrlUnit label, .alertText h3, .pageNavHeader, .lastThreadTitle, .snippet, .OptOut, .nodeText, .controlGroup, .infoBlock dd, .previewTooltip, .Filename {
    color: #aaa;
  }
  .CurrentStatus, .tag, .tagBlock, .threadAlerts dd, .messageContent span[style*="color: #2c2c2c"], .messageContent span[style*="color: #000"], .messageContent span[style*="color: rgb(0, 0, 0)"] { /* Message color correction */
    color: #aaa !important;
  }
  
  /* Links */
  a:hover, a:focus {
    color: #eee !important;
  }
  
  /* Credits */
  .thCopyrightNoticeStyle:after {
    content: " | Thème DarkMCFR par Le Minaw.";
  }
  
  /* NoUglyThingsHack */
  .bigFooter, .sharePage {
    display: none;
  }
}
