css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.4gamer.net") {

.unit_utility,.V2_global_navi,.left_contents,.right_contents,.footer_index,.footer_middle,.footer_bottom{display: none !important;}
.contents_header,#SOCIALBOOKMARK_BAR,#SOCIALBOOKMARK_BOTTOM_BAR,
  ul.title_info,.keyword,.contents_bottom_pr,#MEDIANEWS_box,.whats_new,blockquote.copyright,.article_eof{display: none !important;}
  .header{height: auto !important;}
  ul.related_link,.header{background: none !important;}
  .V2_utility{height: 0 !important;
  overflow: hidden !important;}
  
  .cap{padding: 0.4em !important;}
  
  /* AD */
  .adsbygoogle,.ad_header_top,.digest_4gamer{display: none !important;}
  
  /* 1カラム化 */
    body{font-size: small !important;
      margin: 0 !important;
}
  body,.contents{background: #f5f5f5 !important;}
  .contents, .unit_contents{background-image: none !important;
  width: 100% !important;}

  .main_contents{
    float: none !important;
   box-shadow: 0 1px 2px 1px #eee !important;
    width: 80% !important;
        margin: 0.5em auto !important;
}
    .main_contents .container{
   padding: 0 1em !important;
      box-sizing: border-box;
  }
  .main_contents .container,.main_contents .container h1,.main_contents .container .author,.main_contents div.finding{width: 100% !important;}
 .main_contents .container .author,.main_contents div.finding{background-image:none;}
   .main_contents div.finding{
    background:  -moz-linear-gradient(
      top
    , #888888 0%
    , #505050 19%
    , #333333 20%
    , #343434);
}
  
  /* レビュー タイトルテーブル */
  .review_header{width: 100% !important;}
  .review_header p.catch{width: 100% !important;
  box-sizing: border-box !important;}
  .review_header  .author,.review_header  h1{box-sizing: border-box !important;}

  /* 画像ページ */
  .big_main_contents{width: 100% !important;}
   
}
