css:
@-moz-document domain("badoo.com") {
.page {
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background: url(http://cdn.wonderfulengineering.com/wp-content/uploads/2016/01/nature-wallpapers-6.jpg);
}
:root {
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background: url(http://cdn.wonderfulengineering.com/wp-content/uploads/2016/01/nature-wallpapers-6.jpg);
}
nav#sidebar.sidebar {
    background: transparent !important;
}
div.js-scroller {
    background: rgba(0, 0, 0, 0.79);
    color: white;
}
a.app.js-user-name {
    color: white !important;
}
span.b-link.js-vip-button {
    color: white;
}
.sidebar-menu__item-txt {
    color: white;
}
b {
    color: #2C6F0B;
}
.spotlight__title {
    color: black;
}
div.profile__header {
    background: transparent !important;
}
div.profile__header.js-profile-header-container.js-core-events-container {
    background: transparent !important;
}
body {
    background: transparent !important;
    color: white;
}
.page-right-side {
    background: rgba(0, 0, 0, 0.79);
}
a.b-link.user-card__link.app.js-folders-user-profile-link {
    color: white;
}
.common-info > span {
    color: white;
}
.photo-gallery {
    background: transparent;
}
.page-right-footer {
    background: rgba(0, 0, 0, 0.86);
    color: white;
}
span.b-link.js-banner-action {
    color: black;
}

.big-photo__footer .banner {
    color: black;
}

div.scroll.scroll--top.scroll--bottom {
    background: transparent !important;
}

div.contacts-wrap.js-contacts-wrap {
    background: transparent;
}
section.section-chat-cnt {
    background: transparent !important;
    border-right-width: 1px;
    border-style: ridge;
    border-bottom-width: 1px;
}
span.b-link.js-im-promo {
    border-style: ridge;
    border-top-width: 1px;
}
span.b-link.im_user {
    border-style: ridge;
    border-top-width: 1px;
}
div.messenger-banner-wrap.js-messages-banner {
    border-style: ridge;
    border-top-width: 1px;
}
.section-chat-left {
    background: transparent !important;
}
.section-flexible {
    background: transparent !important;
}
.messenger_ {
    background: rgba(0, 0, 0, 1);
    border-width: 1px;
    border-style: ridge;
}
.message-match-txt:last-child {
    color: black;
}
.message-match-title {
    color: black;
}
.lgrey {
    color: rgb(99, 150, 162);
}
div.b {
    color: rgba(0, 0, 0, 0.72);
}
.messages {
    background: transparent !important;
}
div#messages.unread.js-scroller {
    background: transparent !important;
}
.section-messages {
    background: transparent !important;
}
.st_normal .messenger-banner-wrap, .st_msgs_disable .messenger-banner-wrap {
    background: transparent;
}
.messenger-close {
    background: #5A4E4E;
}
.contacts__item {
    background: rgba(2, 14, 14, 0.7);
}
.section-chat-left .section-header--small {
    background: rgba(2, 14, 14, 0.7);
}
.section-chat-left .tabs__item {
    background: rgba(2, 14, 14, 0.7);
}
.section-chat-left .section-chat-footer {
    border-style: ridge;
    border-right-width: 1px;
    background: rgba(2, 14, 14, 0.7);
}
.message-tools {
    background: black;
}
div#t.message-tools__textarea.js-scroller {
    background: white;
    color: black !important;
}
.tabs__item {
    color: white;
}
}
