css:
@-moz-document domain("realitygaming.fr") {
#content{
    background: url("http://image.noelshack.com/fichiers/2016/27/1467886097-2-earth-2049-copie.jpg") top center fixed;
}
#content .pageContent {
    background: none;
}
#header {
    background-image: url("http://image.noelshack.com/fichiers/2016/27/1467886097-2-earth-2049-copie.jpg");
}
#content {
    border-top: 2px solid rgba(252, 252, 255, 0.35);
    box-shadow: none;
}
#taigachat_full #taigachat_box {
    background-image: url('http://image.noelshack.com/fichiers/2016/27/1467886806-background-rg.png') !important;
    background-repeat: no-repeat !important;
    background-position: 100% 100% !important;
    background-color: rgba(112, 159, 255, 0.2) !important;
}
    .secondaryContent {
        background-color: rgba(112, 159, 255, 0.2) !important;
        
    }
    
    .ugc a:hover, .ugc a:focus{
        background: rgba(23, 230, 193, 0.4) repeat-x top;
    }
    
    a[href]{
        color: #0096FF !important;
    }
    
    .sidebar .visitorPanel{
        background: rgba(0, 0, 0, 0);
    }
    
    .sidebar .avatarList .userTitle{
        color: rgb(0, 0, 0) !important;
    }
        
    .profilePage .tabs.mainTabs {
        background: rgba(0, 21, 255, 0);
    }

    .mast h3.subHeading, .mast h3.textWithCount.subHeading .text{
        background-color: rgb(69, 0, 77) !important;
    }
    
    .profilePage .mast .sectionFooter{
        border-right: none !important;
        border-left: none !important;
    }
    
    .profilePage .mast .followBlocks .primaryContent.avatarHeap{
        border: none !important;
    }
    
    .primaryContent{
        background-color: rgba(251, 250, 249, 0.2) !important;
    }
    
    .followedBlock{
        border-bottom: none !important;
    }
    
    .followingBlock{
        border-bottom: none !important;
    }
    

    
    .sectionFooter{
        background: rgb(68, 2, 67) repeat-x bottom;
    }
    
   .linkActu:hover {
    color: #B408F5 !important;
    font-weight: bold !important;
    font-size: larger !important;
    }
    .sectionMain{
        background-color: rgba(252, 242, 255, 0.2) !important;
        border: none;
    }
    ol#forums.nodeList.sectionMain{
        background: rgba(251, 250, 249, 0.29) !important;
    }
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo{
        background: rgba(251, 250, 249, 0) !important;
    }
    .nodeList h3.nodeTitle a{
        color: rgb(21, 153, 243) !important;
    }
    .username .style2{
        color: #009AFF !important;
    }
    #taigachat_box{
        border: 1px solid #d091d4;
    }
    .nodeList .categoryStrip{
        border-bottom: none !important;
    }
.menuVisiteur {
    box-shadow: 0px 2px 5px #283E52;
    background: #3F6685 url("https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png") repeat-x bottom!important;
}
.Menu div.primaryContent.menuHeader {
    background: url("https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png") repeat-x scroll center bottom rgb(71, 113, 137);
}
.Menu .sectionFooter {
    background-color: rgb(64, 103, 133);
}
.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background: linear-gradient(to right,rgb(70, 112, 136) 0%,rgb(69, 111, 136) 100%);
}
.breadcrumb {
	background: rgba(69, 110, 135, 0);
    border: none;
}
.breadcrumb .jumpMenuTrigger{
    -webkit-filter: brightness(800%); /* Chrome, Safari, Opera */
    filter: brightness(800%);
}
.PageNav a.currentPage {
    background: rgb(67, 107, 135);
    border-color: #283F53;
}
.PageNav a:hover, .PageNav a:focus {
    color: rgb(233, 237, 238);
    background: rgb(87, 137, 146);
    border-color: rgb(233, 237, 238);
}
.active .formPopup {
    background: rgb(255, 255, 255);
}
#QuickSearch.active {
    box-shadow: 3px 3px 25px rgb(62, 62, 62);
}
#QuickSearch {
    padding-top: 0;
}
.formPopup .controlsWrapper {
    background: #EBEFEF;
}
.navPopup .PopupItemLinkActive:hover {
    background: linear-gradient(to right,rgb(106, 141, 179) 0%,rgb(97, 148, 208) 100%)!important;
}
.muted a {
    color: #222!important;
}
.button, .callToAction span {
    background: url("https://realitygaming.fr/styles/realitygaming/images/gaming/ps3-r.png") no-repeat 2% 98%,url("https://realitygaming.fr/styles/realitygaming/images/gaming/ps3-r.png") no-repeat 50% 2%,url("https://realitygaming.fr/styles/realitygaming/images/gaming/ps4.png") no-repeat 98% 2%;
    background-color: rgb(63, 102, 133);
}
.footer .pageContent {
    background-color: rgba(23, 96, 147, 0.5);
    border-top: 4px solid rgba(109, 164, 191, 0.5);
}
.nodeList .categoryStrip {
    background: rgb(75, 24, 77) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern4-ts.png') repeat-x bottom;
    border: none !important;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {

    background: rgb(75, 24, 77) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png') repeat-x bottom;
    border-bottom: none !important;
}
    
    .xenOverlay.memberCard{
        background-image: url("http://image.noelshack.com/fichiers/2016/27/1467894017-cm-rg-5.png"), url("http://image.noelshack.com/fichiers/2016/27/1467893992-cm-rg-4.png"), url("http://image.noelshack.com/fichiers/2016/27/1467892776-rg-membercard.png") !important;
    }
#exposeMask {
	background-color: rgba(255, 255, 255, 0)!important;
}
.PageNav .pageNavHeader{
    color: #fff;
}
.bbCodeQuote .attribution {
    background-color: #78B8DC;
    border-bottom: 1px solid #78B8DC;
}
.bbCodeBlock {
    border-radius: 4px;
    border: 1px solid #78B8DC;
}
.bbCodeQuote {
    border-color: #78B8DC;
}
.bbCodeBlock .type {
    background-color: #78B8DC;
    border-bottom: 1px solid #78B8DC;
}
.titleBar h1 {
    color: rgb(241, 241, 241);
}
}
