.XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto', sans-serif, Helvetica, Arial, sans-serif;
    color: rgb(255, 255, 255);
    background-color: rgb(25, 25, 25);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(25, 25, 25);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 0px;
}
.secondaryContent {
    background-color: rgba(101, 101, 101, 0.08);
    padding: 10px;
    border-bottom: 1px solid rgb(26, 26, 26);
}
.XenBase .amsArticleViewItem .main .featured,
.XenBase .sidebar .featuredNotice {
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #5a9d32), color-stop(100%, #527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: linear-gradient(to bottom, #565656 0%, #565656 100%);
    color: #fff;
    border: 1px solid rgb(86, 86, 86);
    font-size: 12px;
    line-height: 1.4;
    border-radius: 0px;
}
.XenBase #ProfilePostList li.messageSimple {
    background-color: rgba(101, 101, 101, 0.08);
    border: 1px solid rgba(101, 101, 101, 0);
}
.XenBase .messageSimple,
.XenBase .profilePage .eventList li,
.XenBase .searchResult {
    background-color: rgba(101, 101, 101, 0.08);
    padding: 7px;
    margin-bottom: 5px;
    border: 1px solid rgb(26, 26, 26);
}
.amsCategoryList .count {
    color: rgb(197, 197, 197);
    float: right;
    margin-left: 5px;
}
.amsMostRecentComments .commentList .extraData,
.amsMostRecentComments .commentList .extraData a {
    color: rgb(175, 175, 175);
}
.XenBase .profilePage .mast .infoBlock .secondaryContent.pairsJustified > dl,
.XenBase .amsArticle .likesSummary,
.XenBase .innerContent .rateBlock {
    border: 1px solid rgba(101, 101, 101, 0);
    background-color: rgba(101, 101, 101, 0.08);
    background-image: none;
    border-radius: 0px;
}
.node .forumNodeInfo.unread .nodeIcon,
.node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://i.imgur.com/hnDimcK.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
}
.node .forumNodeInfo .nodeIcon,
.node .categoryForumNodeInfo .nodeIcon {
    background-image: url('http://i.imgur.com/7ymrq14.png');
    background-repeat: no-repeat;
    background-position: 0 0;
}
.mediaLabel.labelStandard {
    color: rgb(37, 37, 37);
    background-color: rgb(58, 58, 58) !important;
    border-color: rgb(58, 58, 58) !important;
    border-radius: 0px;
}
.XenBase .mediaContainer .statsSection,
.XenBase .mediaContainer .statsSection i,
.XenBase .mediaLabel.labelStandard {
    color: rgb(197, 197, 197);
}
.mediaContainer .mediaControls {
    padding: 0 5px 5px 5px;
    text-align: right;
}
.mediaLabels .mediaLabel {
    font-size: 10px;
    background: none;
    padding: 1px 5px;
    -webkit-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.25);
    -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.25);
    -khtml-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.25);
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.25);
    text-align: center;
    display: inline-block;
    margin-top: 5px;
    min-width: 12px;
    border-radius: 0px;
}
.XenBase .messageSimple .messageInfo {
    background-color: rgba(243, 144, 144, 0);
    border: 1px solid rgb(31, 31, 31);
    border-radius: 0px;
}
.profilePage .tabs.mainTabs {
    padding: 0 20px;
    margin-top: -25px;
    min-height: 25px;
    height: auto;
    position: relative;
    background: rgb(25, 25, 25);
}
.discussionListItem .stats dl {
    padding: 0 10px;
    border-left: 1px solid rgb(31, 31, 31);
    border-right: 1px solid rgb(31, 31, 31);
}
.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
