#InlineModOverlay {
    display: none;
    z-index: 10000;
    border: 1px solid rgb(43, 43, 43);
    -webkit-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    -moz-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    -khtml-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    box-shadow: 0px 25px 50px rgb(45, 45, 45);
    max-width: 401px;
    width: 99%;
    margin-left: 300px;
}
#InlineModOverlay .selectionControl {
    display: block;
    overflow: hidden;
    zoom: 1;
    font-size: 11px;
    padding: 1px 10px;
    line-height: 23px;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #3a3a3a;
}
::-webkit-scrollbar {
    width: 10px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
    background-color: #232323;
    background-image: -webkit-linear-gradient(45deg, rgba(255, 255, 255, .2) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, .2) 50%, rgba(255, 255, 255, .2) 75%, transparent 75%, transparent);
}
.subHeading {
    font-size: 14px;
    font-family: 'Roboto', sans-serif, Helvetica, Arial, sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #5a9d32), color-stop(100%, #527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: linear-gradient(to bottom, #191919 0%, #191919 100%);
    border-radius: 0px;
}
.xenOverlay .formOverlay .heading {
    font-weight: bold;
    font-size: 14px;
    color: rgb(247, 247, 247);
    background-color: rgb(104, 159, 16);
    padding: 7px 10px;
    margin-bottom: 10px;
    border: 1px solid rgb(31, 31, 31);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #5a9d32), color-stop(100%, #527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: linear-gradient(to bottom, #191919 0%, #191919 100%);
    border-radius: 0px;
}
.xenOverlay .formOverlay {
    color: #eee;
    /* background: url(http://www.se7ensins.com/rgba.php?r=0&g=0&b=0&a=191); */
    
    background: #1f1f1f;
    padding: 1px 1px;
    border: 20px solid rgb(0, 0, 0);
    border: 0px solid rgba(0, 0, 0, 0);
    _border: 20px solid rgb(0, 0, 0);
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    -khtml-border-radius: 20px;
    border-radius: 0px;
    -webkit-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    -moz-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    -khtml-box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    box-shadow: 0px 25px 50px rgba(0, 0, 0, 0.5);
    _zoom: 1;
    margin: 0;
}
#logoBlock {
    background: none;
}
.pageContent .breadcrumb .crust .arrow,
.pageContent .breadcrumb .crust .arrow span {
    border-top-width: 17.5px;
    border-bottom-width: 17.5px;
    border-left-width: 0px;
}
.message .editDate {
    text-align: right;
    margin-top: 5px;
    font-size: 11px;
    color: rgb(67, 156, 54);
}
.dark_postrating_member th {
    color: rgb(255, 255, 255);
    font-size: 11px;
}
.dark_postrating_member {
    border-spacing: 0;
    border-collapse: separate;
    border-width: 0 1px 0px 1px;
    border-style: solid;
    border-color: rgb(26, 26, 26);
    color: rgb(255, 255, 255);
    width: 100%;
    padding: 5px 25px;
}
.breadcrumb .crust a.crumb {
    cursor: pointer;
    text-decoration: none;
    background-color: rgb(26, 26, 26);
    padding: 0 15px 0 23px;
    margin-bottom: -1px;
    border-bottom: 1px solid;
    outline: 0 none;
    -moz-outline-style: 0 none;
    display: block;
    line-height: 24px;
}
.xbScrollUpButton,
.xbScrollDownButton {
    color: rgb(255, 255, 255);
    background-color: rgb(35, 152, 255);
    padding: 10px;
    border: 1px solid rgb(35, 152, 255);
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    -khtml-border-radius: 2px;
    border-radius: 2px;
    white-space: nowrap;
    cursor: pointer;
    text-align: center;
    opacity: 0.3;
    transition: opacity 0.3s ease-in-out;
}
.xbScrollUpButton:hover,
.xbScrollDownButton:hover {
    color: rgb(255, 255, 255);
    border-color: rgb(35, 152, 255);
    opacity: 1;
}
.member_view .profilePage .infoBlock dd {
    font-size: 11px;
    padding-top: 2px;
    color: #929292;
}
@media (min-width: 481px) {
    .signature img {
        max-height: 500px !important;
        max-width: 100% !important;
    }
}
