css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.unity.com") {

/* 
 * Name:    Unity Forum Dark
 * Author:  Zoran Ravic
 * Version: 0.5.1
 */
	
	/* Classes that can be used in signatures
		for example you can use
			<div class="dark-theme-hide">______________________________________________________________________________</div>
		to only show a line to users that don't have the dark theme
	*/
	.dark-theme-hide
	{
		display: none;
	}
	
	/* Background */
	body
	{
		background-color: #222 !important;
	}
	
	/* Background of main groups */
	  .lia-list-wide /* Board list */
	, .thread-list tr /* Thread list */
	, .lia-panel /* Generic table */
	, .lia-panel tr
	, .lia-welcome /* Welcome text at the board list */
	{
		background-color: #333 !important;
	}
	
	/* Board titles */
	.board-title a
	{
		color: #DDD !important;
	}
	
	/* Breadcrumbs */
	.lia-breadcrumb-node.final-crumb
	{
		color: #DDD !important;
	}
	
	/* Replu and View Count */
	.lia-component-messages-column-message-replies-count,
	.lia-component-messages-column-message-views-count
	{
		color: #DDD !important;
	}
	
	/* Threads with unread posts stand out */
	.ForumPage .lia-list-row-thread-unread
	{
		background-color: #444 !important;
	}
	/* Highlight this thread when Unread - As a reminder to Update */
	.ForumPage .lia-list-row-thread-unread a[href*="td-p/2647011/jump-to/first-unread-message"]
	{
		color: green !important;
	}
	
	/* Remove the bullshit properties on edit post page */
	.EditPage .lia-form-custom-unity-fix-version-entry,
	.EditPage .lia-form-custom-unity-issue-id-entry,
	.EditPage .lia-form-custom-unity-in-version-entry,
	.EditPage .lia-form-custom-unity-regression-entry,
	.EditPage .lia-form-custom-unity-duplicate-id-entry,
	.EditPage .lia-form-custom-unity-fogbugz-id-entry,
	.EditPage .lia-form-custom-unity-priority-entry,
	.EditPage .lia-form-custom-unity-severity-entry
	{
		display: none;
	}
	
	/* Date Time on the Posts */
	.lia-message-post-date.lia-component-post-date /* Not sure what I set this for */
	{
		color: #BBB !important;
	}
	.DateTime
	{
		color: #BBB !important;
	}
	
	/* Info area on the left */
	.lia-info-area
	{
		color: #BBB !important;
	}
	
	/* Color of links to user profiles */
	.lia-user-name-link
	{
		color: white !important;
	}

	/* Page navigation */
	.lia-component-pagesnumbered span.lia-link-navigation.lia-link-disabled,
	.lia-component-pagesnumbered a.lia-link-navigation.lia-custom-event
	{
		color: #CCC !important;
		border-color: rgba(0,0,0,0);
		margin: 0;
	}
	.lia-component-pagesnumbered span.lia-link-navigation.lia-link-disabled
	{
		background-color: #444 !important;
	}
	.lia-component-pagesnumbered a.lia-link-navigation.lia-custom-event
	{
		background-color: #333 !important;
	}
	.lia-component-pagesnumbered a.lia-link-navigation.lia-custom-event:hover
	{
		border-color: white;
	}
	/* Next and Previous page links */
	.lia-paging-full-wrapper .lia-paging-page-next *.lia-custom-event ,
	.lia-paging-full-wrapper .lia-paging-page-previous *.lia-custom-event
	{
		color: white !important;
		background-color: #333 !important;
	}
	/* All, Previous, Next thread */
	.lia-paging-full a
	{
		color: #CCC !important;
	}
	.lia-paging-full a:hover
	{
		background-color: #333 !important;
	}

	/* Headers of the parts to the left of the thread list (in sub-forum view) */
	.lia-panel-heading-bar-title
	{
		color: #BBB !important;
	}
	
	/* Search Bar */
	.lia-search-input-wrapper *
	{
		background-color: #333 !important;
	}
	.lia-search-input-wrapper > * ,
	.lia-search-input-wrapper .lia-search-match-lithium
	{
		color: white !important;
	}
	
	/* Tread Post Colors */
	.lia-message-board.lia-panel-message .lia-quilt-row,
	.lia-user-messages > div /* Posts by user (on profile) */
	{
		background-color: #333 !important;
		color: #DDD !important;
	}
	/* Quote colors */
	.lia-message-board.lia-panel-message .lia-quilt-row blockquote
	{
		color: gray !important;
	}
	/* Thread name that is included in every post */
	.lia-message-subject.lia-component-subject h1,
	.lia-message-subject.lia-component-subject h2,
	.lia-message-subject.lia-component-subject h3,
	.lia-message-subject.lia-component-subject h4,
	.lia-message-subject.lia-component-subject h5,
	.lia-message-subject.lia-component-subject h6,
	.lia-message-subject.lia-component-subject h7
	{
		color: gray !important;
	}
	/* Remove border around quoted text */
	.lia-message-body-content table,
	.lia-message-body-content th,
	.lia-message-body-content td
	{
		border-color: transparent;
	}
	
	/* Buttons for Replying and creating a new Post */
	.message-post a,
	.message-reply a,
	.reply-action-link
	{
		background-color: #6699cc !important;
	}
	
	/* Like count color */
	.lia-button-image-kudos-count *
	{
		color: white !important;
	}
	
	/* Button for Forum, Thread and Post Options */
	.dropdown-default-item > a
	{
		background-color: #444 !important;
		color: white !important;
	}
	/* Forum, Thread and Post Options dropdown menu */
	.dropdown-default-item ul,
	.dropdown-default-item ul *
	{
		background-color: #444 !important;
		color: white !important;
	}
	
	/* Accepted answer solution line */
	.lia-panel-feedback-banner-safe
	{
		background-color: #292929;
		max-width: 40%;
	}
	
	/* Link, button, breadcrumb colours hover */
	.lia-button-primary:hover,
	.lia-button-secondary.reply-action-link.lia-action-quick-reply.lia-custom-event:hover,
	.lia-button-secondary.reply-action-link.lia-action-reply:hover,
	.lia-button-secondary.quote-message-link:hover,
	.lia-button-primary.lia-link-disabled:hover
	{
		background: #00cccc !important;
	}
	
	/* Thread list - Sidabar (Like count, Colution count) */
	.kudosCountColumn,
	.acceptedSolutionsCountColumn 
	{
		color: white !important;
	}
	
	/* Link color (in Posts) */
	.lia-message-body-content a
	{
		color: #00bcd4 !important;
	}
	.lia-message-body-content a:hover
	{
		color: #008fa1 !important;
	}
	
	/* Signatures */
	.UserSignature.lia-message-signature
	{
		border-top: 1px solid gray;
		margin-top: 10px !important;
		padding-top: 5px !important;
		color: gray !important;
		max-height: 100px !important;
	}
	.UserSignature.lia-message-signature a
	{
		color: #005e6a !important;
	}
	.UserSignature.lia-message-signature a:hover
	{
		color: #00bcd4 !important;
	}
	
	/* Spoilers */
	.lia-spoiler-container,
	.lia-spoiler-link
	{
		background-color: #292929 !important;
		border-color: #444 !important;
	}
	/* Spoiler content */
	/*.lia-spoiler-content 
	{
		background-color: #333 !important;
	}*/
	
	/* Code text */
	.lia-message-body-content pre
	{
		background-color: #222 !important;
		color: #CCC !important;
		border: none;
	}
	
	/* Avatar image size */
	.lia-panel-message-content .UserAvatar > img
	{
		width: 100% !important;
		height: auto !important;
		border-radius: 10%;
	}
	/*.lia-panel-message-content .lia-message-author-avatar.lia-component-user-avatar
	{
		width: 100% !important;
	}*/
	/* Don't use name as placeholder if user has no avatar */
	.lia-panel-message-content .UserAvatar > a,
	a.UserAvatar
	{
		color: rgba(0,0,0,0) !important;
	}
	
	/* Reduction of Post's spacing */
	.lia-quilt-row.lia-quilt-row-header /* post time and options */
	{
		padding-top: 2px;
		padding-bottom: 0;
	}
	.lia-quilt-row.lia-quilt-row-header .lia-quilt-column-header-left p /* post time */
	{
		padding-left: 38%;
	}
	.lia-quilt-row.lia-quilt-row-header .lia-quilt-column-header-right a /* post options */
	{
		padding: 5px 15px;
		margin-top: 10px;
	}
	.lia-panel-message-content .lia-quilt-row.lia-quilt-row-header .lia-quilt-column-header-right a:hover /* post options */
	{
		background-color: #00cccc !important;
	}
	.lia-quilt-row.lia-quilt-row-footer /* reply and like */
	{
		padding-top: 0;
		padding-bottom: 2px;
	}
	.lia-quilt-row.lia-quilt-row-main .lia-quilt-column,
	.lia-quilt-forum-message .lia-quilt-row-header p
	{
		margin-bottom: 0;
	}
	.lia-quilt.lia-quilt-header.lia-quilt-layout-header.lia-component-quilt-header,
	.lia-quilt-column-common-header,
	.lia-quilt-row-sub-header
	{
		border: none !important;
	}
	.lia-quilt-row-sub-header
	{
		background-color: #222 !important;
	}
	.lia-menu-navigation li /* options list */
	{
		padding: 0;
	}
	a.lia-action-reply
	{
		padding: 7px 20px !important;
	}
	a.lia-action-quick-reply
	{
		padding: 7px 10px !important;
	}
	.lia-message-body-content
	{
		margin-bottom: 5px;
	}
	.lia-panel-message-content .Attachments
	{
		margin-bottom: 0;
	}
	/* Remove empty lines that users put after quotes from some reason */
	.lia-message-body-content blockquote ~ br
	{
		display: none;
	}
	/* Profile, My settings */
	.user-navigation-settings-drop-down li
	{
		padding: 0;
	}
	
	/* Remove a random "empty" iframe hidden in the port options dropdown menu */
	.dropdown-positioning-static iframe
	{
		display: none !important;
	}
	
	/* Insert link modification (there might be other in the style) */
	.mce-container-body div:last-child > button
	{
		background-color: #333 !important;
	}
	.mce-container-body div:last-child > button:hover
	{
		background-color: #FFF !important;
	}
	
	/* Border around posts */
	.lia-panel-message, .lia-panel-standard, .lia-tkb-article-summary-view, .lia-message-view-display, .lia-note-view-display, .lia-spoiler-container
	{
		/*border-color: black;*/
		border: none;
	}
	/* Borders between posts */
	.lia-panel-message-content
	{
		border-color: white;
	}
	.lia-linear-display-message-view:first-child .lia-panel-message-content
	{
		border-color: #333;
	}
	
	/* User solutions rank list */
	.UserList .UserProfileSummary 
	{
		margin: 5px 0;
	}
	.UserList td 
	{
		vertical-align: middle;
	}
	
	/* Footer colors */
	footer > div
	{
		background-color: #222 !important;
	}
	footer > div .unity-logo,
	footer > div .social
	{
		filter: invert(100%) !important;
	}
	
	/* User navigation menu */
	#userNavDropdown
	{
		background-color: #444 !important;
	}
	#userNavDropdown a
	{
		color: #00bcd4 !important;
	}
	#userNavDropdown a:hover
	{
		background-color: #333 !important;
	}
	#userNavDropdown .dropdown-separator
	{
		background-color: #222 !important;
	}
	.UserNavigation:hover .user-navigation-settings-drop-down
	{
		display: block !important;
	}
	
	/* Notifications section */
	.lia-quilt-notification-feed-page .lia-quilt-row-main .lia-quilt-column-alley
	{
		background-color: #333 !important;
		color: white !important;
	}
	
	/* Likes section */
	.lia-quilt-kudos-message-page .lia-quilt-row-main .lia-quilt-column-alley
	{
		background-color: #333 !important;
		color: #999 !important;
	}
	
	/* New Thread, New Post form */
	.lia-quilt-post-page .lia-quilt-column.lia-quilt-column-24.lia-quilt-column-single.lia-quilt-column-main-content > div,
	.lia-quilt-reply-page .lia-quilt-column.lia-quilt-column-24.lia-quilt-column-single.lia-quilt-column-main-content > div,
	.lia-quilt-edit-page .lia-quilt-column.lia-quilt-column-24.lia-quilt-column-single.lia-quilt-column-main-content > div
	{
		background-color: #333 !important;
	}
	.lia-form.lia-form-vertical
	{
		color: #BBB !important;
	}
	/* Options for text editor */
	.mce-panel.mce-panel
	{
		background-color: #333 !important;
	}
	.mce-panel.mce-panel .mce-text,
	.mce-panel.mce-panel button
	{
		color: gray !important;
	}
	.mce-panel.mce-panel button:hover
	{
		color: #333 !important;
	}
	i.mce-i-resize
	{
		color: white !important;
	}
	.message-editor-controls .quote-message-link
	{
		padding: 7px 20px;
		background-color: #555 !important;
		color: white !important;
	}
	.lia-tabs-standard
	{
		border: none;
	}
	.lia-button-wrapper-Submit-action input
	{
		background-color: #6699cc !important;
	}
	.lia-button-wrapper-Cancel-action input
	{
		background-color: #555 !important;
		color: white !important;
	}
	/* Text input fields */
	.lia-form .lia-form-type-text,
	.lia-form #lia-subject
	{
		background-color: #222 !important;
		color: #DDD !important;
	}
	/* Inside of the iframe */
	.lia-form #mceu_26 /* Remove text editor's border */
	{
		border: none;
	}
	.lia-form #tabgroup *
	{
		color: white !important;
	}
	/* new post text's iframe */
	#tinymce
	{
		color: #DDD !important;
	}
	#tinymce a
	{
		color: #00bcd4 !important;
	}
	.mce-window-head *,
	.mce-container-body label
	{
		color: #DDD !important;
	}
	
	/* Upload form */
	.lia-media-uploader-dialog
	{
		max-height: 90%;
    	overflow-y: scroll !important;
	}
	.lia-media-uploader-dialog div
	{
		background-color: #333 !important;
		color: #DDD !important;
	}
	
	/* Info messages for forms */
	.InfoMessage
	{
		background-color: #555 !important;
		border-color: #555 !important;
		color: #DDD !important;
	}
	/* For alert shown after posting */
	.InfoMessage a
	{
		color: #00bcd4 !important;
	}
	
	/* User profile statisics */
	.lia-component-quilt-user-profile-statistics
	{
		background-color: #333 !important;
		color: white !important;
	}
	
	/* Saved drafts */
	.lia-message-view-auto-saved-draft
	{
		background-color: #333 !important;
	}
	.lia-message-view-auto-saved-draft a
	{
		color: #00bcd4 !important;
	}
	/* Delete draft confirmation */
	.lia-message-view-auto-saved-draft .lia-inline-confirm
	{
		background-color: #222 !important;
	}
	
	/* My settings */
	.MyProfilePage .lia-quilt-row-main .lia-quilt-column-alley.lia-quilt-column-alley-single
	{
		background-color: #333 !important;
	}
	.lia-tabs-standard *
	{
		color: #00bcd4 !important;
	}
	.MyProfilePage .lia-tabs-secondary *
	{
		color: #CCC !important;
	}
	.MyProfilePage .lia-tabs-secondary span a
	{
		background-color: #333 !important;
	}
	.MyProfilePage .lia-tabs-secondary span:hover a,
	.MyProfilePage .lia-tabs-secondary .lia-tabs-active span a
	{
		background-color: #222 !important;
	}
	.MyProfilePage #fieldset legend
	{
		color: #999 !important;
	}
	.MyProfilePage #fieldset div,
	.MyProfilePage .lia-list-wide
	{
		color: #CCC !important;
	}
	/* Hide search form in my settings */
	.MyProfilePage #lia-searchform
	{
		display: none;
	}
	
	/* Private messaging */
	.PrivateNotesPage .lia-quilt-row-main .lia-quilt-column-alley.lia-quilt-column-alley-single,
	.PrivateNotesPage .lia-panel-message
	{
		background-color: #333 !important;
	}
	.PrivateNotesPage .lia-panel-message-content
	{
		border: none;
	}
	.PrivateNotesPage .lia-message-subject
	{
		font-size: 30px;
	}
	.PrivateNotesPage .lia-message-subject,
	.PrivateNotesPage .lia-note-received-date-wrapper,
	.PrivateNotesPage .lia-message-body
	{
		color: white;
	}
	.PrivateNotesPage .lia-message-body
	{
		padding-top: 30px;
	}
	.PrivateNotesPage .lia-message-body *
	{
		font-size: 17px;
	}
	.PrivateNotesPage #tabgroup .lia-button.lia-button-secondary
	{
		background-color: #6699cc !important;
		color: #333 !important;
		padding: 5px 10px;
	}
	.PrivateNotesPage #tabgroup .lia-button.lia-button-secondary:hover
	{
		background: #00cccc !important;
	}
	
	.KudosUserPage .lia-quilt-column-alley.lia-quilt-column-alley-single
	{
		background-color: #333 !important;
		color: white;
	}
	
	/* Tag people in your post */
	.lia-mentions-block,
	.lia-mentions-block > div > div,
	.lia-mentions-block li
	{
		background-color: #444 !important;
		color: white;
	}
	.lia-mentions-block li:hover
	{
		background-color: #222 !important;
		color: white;
	}
	
	/* View image */
	.modal-imagelightbox .modal-content,
	.modal-imagelightbox .modal-content .lia-lightbox-info
	{
		background-color: #333;
		color: white;
	}
	
	/* Insert video */
	.lia-body .mce-window .mce-window-head + .mce-container-body
	{
		min-height: 300px !important;
	}
	
	/* By default: Big yellow banner note*/
	.lia-panel-status-banner-note
	{
		background-color: #252525 !important;
	}
	
	/* Remove their random floating text that was suppose to be a menu select */
	.fancy-select
	{
		display: none;
	}
	
	/* Move new message and notification number outside of the dropdown menu */
	.UserNavigation .user-navigation-settings-drop-down
	{
		display: block;
	}
	.UserNavigation ul,
	.UserNavigation ul li :not(.lia-header-nav-component-unread-count)
	{
		visibility: hidden;
	}
	.UserNavigation:hover ul,
	.UserNavigation:hover ul li *,
	.UserNavigation ul li .lia-header-nav-component-unread-count
	{
		visibility: visible;
	}
	.UserNavigation .lia-header-nav-component-unread-count
	{
		position: relative !important;
	}
	.UserNavigation .lia-component-notificationfeed-widget-indicator .lia-header-nav-component-unread-count
	{
		top: -221px !important;
		right: -200px !important;
	}
	.UserNavigation .lia-component-notes-widget-summary-modern .lia-header-nav-component-unread-count
	{
		top: -280px !important;
		right: -160px !important;
	}
}
