css:
@-moz-document url-prefix("http://vjudge.net/") {



:root {
	--background-color:		#fafafa;
	--accent-color:			#FF9800;
	--primary-color:		#009688;
    --light-primary-color:	#B2DFDB;
    --light-light-primary-color:	#D2EFEB;
    
    --secondary-text-color:	#727272;
    --text-color:			#FFFFFF;
    --primary-text:			#212121;
    --divider-color:		#B6B6B6;
    
    
    --ac-color:#C8E6C9;
    --first-ac-color:#4CAF50;
    --wrong-color:#FFCCBC;
    --practice-color:#F0F4C3;

}



/* 页面背景 */
body {
	background-image: none;
    background-color: var(--background-color);
    margin:0;
}


/* 菜单美化 */
#cssmenu>ul {
	border: none;
	border-radius: 0;
    height: 54px;
    background-color: var(--primary-color);
    box-shadow: 0 2px 5px rgba(0,0,0,0.26);
}

#cssmenu>ul>li>a {
    font-size: 16px;
	padding: 19px 20px;
    color: var(--text-color);
}

#cssmenu>ul>li.has-sub>a::after {
	top: 23px;
    border-bottom: 1px solid white;
    border-right: 1px solid white;
}

#cssmenu>ul>li:hover>a, #cssmenu>ul>li.active>a {
    color: var(--text-color);
}

#cssmenu>ul>li:hover>a {
    background-color: rgba(0,0,0,0.1);
}

#menu-line {
	background: var(--text-color);
    visibility: hidden;
}



/* 数据表格 */
.dataTables_wrapper {
	min-width: 600px;
    max-width: 1100px;
    width: 90%;
    margin: 0px auto 0px auto;
}

.ui-widget-header {
	border: none;
    background: none;
}

table.dataTable thead th.ui-state-default {
    border: none;
    background: none;
    
    box-shadow: none;
}

.dataTables_wrapper {
	box-shadow: 0 0 6px rgba(0,0,0,.16),0 6px 12px rgba(0,0,0,.32);
    background-color: #fff;
}

table.dataTable {
	width: 100% !important;
    font-size: 12px;
    
}

table#table_status td  {
	text-overflow: ellipsis;  
    white-space: nowrap;  
    overflow: hidden;  
}

.dataTable thead {
	margin-bottom: 20px;
}

table.dataTable tbody tr {
	border-width: 0 0 1px 0;
    border-color: var(--divider-color);
}

table.dataTable tbody tr td {
/* 	line-height: 30px; */
/*     background-color: white !important; */
/*     border-bottom: 1px solid var(--divider-color); */
}

/* table.dataTable tr.even td.sorting_1, table.dataTable tr.odd td.sorting_1 {
    background-color: white !important;
}
 */
.dataTable a {
	color: black;
    
} 


/* 按钮 */
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
	background: white;
    border: none;
    
    box-shadow: 0 0 2px rgba(0,0,0,.12),0 2px 4px rgba(0,0,0,.24);

}

.dataTables_wrapper .dataTables_paginate .fg-button {
	border: none;
    font: 13.3333px Arial;
    margin: 0 2px;
}

.dataTables_wrapper .dataTables_paginate .fg-button:hover {
	background: none;
    border: none;
    color: #333 !important;
}

.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
	background: var(--accent-color);
    border: none;
    color: var(--text-color) !important;
}

.ui-corner-all, .ui-corner-bottom, .ui-corner-left, .ui-corner-bl {
    border-bottom-left-radius: 0; 
}

.ui-corner-all, .ui-corner-top, .ui-corner-left, .ui-corner-tl {
    border-top-left-radius: 0;
}

.ui-corner-all, .ui-corner-bottom, .ui-corner-right, .ui-corner-br {
    border-bottom-right-radius: 0;
}
.ui-corner-all, .ui-corner-top, .ui-corner-right, .ui-corner-tr {
    border-top-right-radius: 0;
}

.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
	background: var(--accent-color);
    border: none;
    color: var(--text-color) !important;
}

.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
	color: var(--text-color);
}

a:hover {
	TEXT-DECORATION: none;
}




.MathJax nobr>span.math>span{border-left-width:0 !important}








}

@-moz-document url-prefix("http://vjudge.net/") {
/* latin */
@font-face {
  font-family: 'Volkhov';
  font-style: normal;
  font-weight: 400;
  src: local('Volkhov Regular'), local('Volkhov-Regular'), url(https://fonts.gstatic.com/s/volkhov/v8/7YA5GpHg48S818VCt31NAA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* latin-ext */
@font-face {
  font-family: 'Yrsa';
  font-style: normal;
  font-weight: 400;
  src: local('Yrsa'), local('Yrsa-Regular'), url(https://fonts.gstatic.com/s/yrsa/v1/EB591sVHP0lgbCVRKQWiLw.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Yrsa';
  font-style: normal;
  font-weight: 400;
  src: local('Yrsa'), local('Yrsa-Regular'), url(https://fonts.gstatic.com/s/yrsa/v1/HvXbkSMs7hgg2r-HiCNOmA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
}

@-moz-document url-prefix("http://vjudge.net/contest/") {
#contest_title {
    font-size:32pt;
    color: var(--primary-text);
	font-family: 'Volkhov', serif; 
}


#problem_title {
    
	font-size:30pt;
    color: var(--primary-text);
	font-family: 'Volkhov', serif; 
}

#problem_title span {
	color: var(--primary-text) !important;
}

#problem>div {
	min-width: 600px;
    max-width: 1100px;
    width: 90% !important;
    margin: 0px auto 0px auto;
}

#problem>div:nth-child(3)>div:nth-child(1) {
	border-bottom: none;
    box-shadow: 0 0 2px rgba(0,0,0,0.12), 0 2px 4px rgba(0,0,0,0.24);
    background-color: #fff;
}

#problem_number_container {
	margin-bottom: 30px !important;
}

#problem_number_container label {
	padding: 3px 6px;
    margin: 1px;
}

#contest_tabs {
	border: none;
}

/* Rank */

div.solvedfirst {
	background-color: var(--first-ac-color);
}
.green {
    background-color: var(--ac-color);
}

.red {
	background-color: var(--wrong-color);
}

div.solved_practice {
	background-color: var(--practice-color);
}



.ui-tabs .ui-tabs-nav {
	width: fit-content;
    margin: auto;
}

#rank {
	margin: auto;
}

#rank_header {
/* 	margin: auto; */
    left: auto !important;
}

#rank_foot {
	left: auto !important;
}

#time_container {
	width: 83% !important;
}

}

@-moz-document url-prefix("http://vjudge.net/contest/"), url-prefix("http://vjudge.net/problem") {
p {
    display: block;
    -webkit-margin-before: 0em;
    -webkit-margin-after: 0em;
    -webkit-margin-start: 0px;
    -webkit-margin-end: 0px;
}

pre, .input, .output, pre>div, .vj_sampleInput .textBG, .vj_sampleOutput .textBG{
	font-family: consolas, "Times New Roman",Times,serif  !important;
    font-size: 20px  !important;
}

.plm {
	padding: 30px 0 20px 0;
}

 .opt_btn, .hiddable, .plm, .vj_description, .vj_input, .vj_output, .vj_sampleInput, .vj_sampleOutput, .vj_hint {
	border-bottom: none;
    box-shadow: 0 0 2px rgba(0,0,0,0.12), 0 2px 4px rgba(0,0,0,0.24);
    background-color: #fff;
}

.vj_description, .vj_input, .vj_output, .vj_sampleInput, .vj_sampleOutput, .vj_hint {
	padding: 0 20px;
}

.pst {
	margin: 0;
    padding-left: 10px;
    color: var(--primary-color) !important;
    font-family: 'Yrsa', "Times New Roman",Times,serif !important;
    font-size: 30px !important;
}

.textBG, .ptx {
    background-color: transparent;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    padding: 10px;
    border: none;
    font-family: 'Yrsa', "Times New Roman",Times,serif !important;
    font-size: 24px !important;
    color: var(--primary-text);
}
}

@-moz-document url-prefix("http://vjudge.net/problem") {
.ptt{
	border-bottom: none;
    box-shadow: 0 0 2px rgba(0,0,0,0.12), 0 2px 4px rgba(0,0,0,0.24);
    background-color: #fff;
}

div.ptt a {
	font-size:30pt !important;
    color: var(--primary-text) !important;
	font-family: 'Volkhov', serif !important; 
}

#mid_view, #right_view {
    width: 0px !important;
	display: none !important;
}

#left_view {
	min-width: 600px;
    max-width: 1100px;
    width: 90%;
    margin: 0px auto 0px auto;
    display: block;
}

.ptx {
    background-color: transparent;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    padding: 0px;
    border: none;
    font-family: 'Yrsa', "Times New Roman",Times,serif !important;
    font-size: 24px !important;
    color: var(--primary-text);
}

#left_view>div, #left_view>.pst {
	border-bottom: none;
    box-shadow: 0 0 2px rgba(0,0,0,0.12), 0 2px 4px rgba(0,0,0,0.24);
    background-color: #fff;
}
}
