css:
@-moz-document url-prefix("https://docs.python.org/"), url-prefix("http://docs.python.org/") {
body{font:100%/1.5 Calibri, "Lucida Grande", Verdana, Arial, sans !important; }
  div.body h1, div.body h2, div.body h3, div.body h4, div.body h5, div.body h6{
    font:120%/1.5 "Bookman Old Style", "Gerogia", "Times New Roman", sans-serif !important;
  }

  /* Code */
  tt{font-family:consolas, "Courier New", monospace !important;}
  tt.descname { font-size: 1em !important;}
  pre{font:12px/1.5 Anonymous, consolas, monaco, "Courier New", monospace !important;}
  div.sphinxsidebar h4,div.sphinxsidebar h3{ 
    font-family: "Bookman Old Style", "Gerogia", "Times New Roman", sans-serif !important;
  }

.function, .method {background-color:#BBDEFB;}
.exception {background-color:#FFF9C4;}
.class {background-color:#C8E6C9;}
.attribute {background-color:#D7CCC8;}
.property {background-color:#E1BEE7;}

code.descname {
    font-size: 1.8em;
}

code.descclassname {
    font-size: 1.4em;
}
}
