css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.unity.com")
{
  body
  {
   background: #222b37
  }
  
  /* Forum subforums list */
  tbody
  {
   background: #F0F0F0
  }
  
  /* Right sidebar box 'Our community...' */
  span.lia-welcome.lia-component-common-widget-welcome
  {
    background: #F0F0F0
  }
  
 
  /* Message header and page header bars */
  div.lia-quilt-row-header 
  {
   background:#d0d0d0;
   font-weight: bold;
  }
  
  /* Message header height */
  div.lia-quilt-row.lia-quilt-row-header
  {
    padding-top: 5px;
    padding-bottom: 4px;
  }
  

  /*Main content box is white. Also add a box shadow to the content because why not */
  .lia-content{
  box-shadow: 0px 0px 15px #000;
    background-color: white;
}
  
  /* Message background */
  div.lia-panel-message-content
  {
     color: black;
    background: #d0d0d0;
  }
  
  /*signature background */
  .UserSignature.lia-message-signature
  {
    border-top: solid 1px #eee;
    margin-top: 5px;
  }
  
  /* Message footer */
  div.lia-quilt-row.lia-quilt-row-footer
  {
    background: #ddd;
    padding-top: 5px;
    height: 52px; /* Note: This element seems to be overlayed on top of other box element, so this cant be made much less tall than this */
  }

/* hide signature bar */
  .dark-theme-hide
  {
    display: none;
  }
  
  /* 'Recommended' table at bottom of page */
  div.lia-panel-content
  {
   background: #F3F7F9;
  }
  
  /* Avatars are square and larger -------- */

  img.lia-user-avatar-message 
  {
    border-radius: 0;
    height: 40px;
    width: 40px;
    
  }
  
  img.lia-user-avatar-message:hover
  {
  box-shadow: 0px 0px 5px #000;
  }

  /*  Link/button/breadcrumb colours */
.lia-button.lia-button-primary, .lia-button.lia-button-secondary.reply-action-link.lia-action-quick-reply.lia-custom-event, .lia-button.lia-button-secondary.reply-action-link.lia-action-reply, .lia-button.lia-button-primary.lia-link-disabled {
	background: #ddd;
	color: white;
}
  /*  Link/button/breadcrumb colours hover */
.lia-button.lia-button-primary:hover, .lia-button.lia-button-secondary.reply-action-link.lia-action-quick-reply.lia-custom-event:hover, .lia-button.lia-button-secondary.reply-action-link.lia-action-reply:hover, .lia-button.lia-button-primary.lia-link-disabled:hover {
	background: #00cccc;
	color: white;
}
  
  /* Sticky styles only work on larger screens */
  @media screen and (min-width: 1495px) {
    
     /* Avatars are square and larger -------- */

  img.lia-user-avatar-message 
  {
    border-radius: 0;
    height: 70px;
    width: 70px;
    
    margin-top: 15px;
  }
  
  /* New thread button floating to the bottom right */
span#link_9.lia-button.lia-button-primary.lia-link-disabled {
	position: fixed;
	bottom: 10px;
	right: 20px;
	z-index: 10;
	padding-left: 10px;
	padding-right: 10px;
}
/* Options floating to the top left */
a#dropDownLink.lia-js-menu-opener.default-menu-option.lia-js-click-menu.lia-link-navigation {
	position: fixed;
	top: 20px;
	left: 20px;
	z-index: 10;
	padding-left: 10px;
	padding-right: 10px;
}
/* Float main reply button to the bottom right */
a#link_9.lia-button.lia-button-primary {
	position: fixed;
	bottom: 10px;
	right: 20px;
	z-index: 10;
	padding-left: 10px;
	padding-right: 10px;
}
/* Move the user functions to a top right floater */
li.user-navigation-parent-dropdown {
	position: fixed;
	top: 100px;
	right: 10px;
	z-index: 10;
	padding-right: 0 !important;

  
  
}
  /* Profile sticky Avatar style */
  .user-navigation-parent-dropdown .UserAvatar.lia-user-avatar img
  {
    border: solid white 5px;
    width: 120px;
    height: 120px;
  }
  
  /* Make the username sitcky to the bottom of the avatar*/
a#viewUserProfile.lia-link-navigation.view-profile-link.lia-component-users-action-view-user-profile {
position: absolute;
  top: 130px;
 left: 10px;
  background-color: white;
  padding: 10px;
  width: 120px;
}
    .user-navigation-parent-dropdown .UserAvatar.lia-user-avatar img:hover
    {
      box-shadow: 0px 0px 15px #000;
    }
    
  /* Options menu sticks to the options button */
 .dropdownHover .lia-menu-dropdown-items, .dropdownHover .user-navigation-settings-drop-down-inner > .lia-list-standard, .dropdownHover .lia-form-fieldset-content-wrapper.lia-form-fieldset-content-wrapper
  {
    position: fixed;
	top: 60px;
	left: 20px;
  }
  
    
    
 
  
  /* Move user menu inside the window */
ul#userNavDropdown.lia-list-standard {left: -100px;}
/* Less space wasted inside posts */
div.lia-quilt-column.lia-quilt-column-18.lia-quilt-column-right.lia-quilt-column-main-right {margin-bottom: 0;}
  
 /* Quick reply buttons are not as huge */
  .lia-panel-message:not(.lia-panel-message-root) .lia-button-secondary, .lia-panel-message:not(.lia-panel-message-root) .default-menu-option, .lia-panel-message:not(.lia-panel-message-root) .lia-common-dropdown-toggle, .lia-panel-message:not(.lia-panel-message-root) .lia-content .ContactMeTaplet .MyStatisticsBeanDisplay + ul > li > a, .lia-content .ContactMeTaplet .lia-panel-message:not(.lia-panel-message-root) .MyStatisticsBeanDisplay + ul > li > a, .lia-panel-message:not(.lia-panel-message-root) .lia-component-quilt-header .lia-component-common-widget-search-form .lia-search-toggle, .lia-component-quilt-header .lia-component-common-widget-search-form .lia-panel-message:not(.lia-panel-message-root) .lia-search-toggle, .lia-panel-message:not(.lia-panel-message-root) .lia-action-reply.reply-action-link {
    padding: 10px 15px;
}
  }
}
