css:
@-moz-document domain(cityoftitans.com) {

  /* 
   * Name:    CoT Website
   * Author:  Izzy, izzysoft.com
   * Version: v0.1
   */

  #block-textsize-form {
      clear: both;
  }

  #block-textsize-form > .block-title {
      float: left;
      width: 80px;
  }

  #block-textsize-form > .content {
      float: left;
      margin: 2px 0px -5px -20px;
      width: 460px;
  }

  #block-textsize-form > .content > .textsize_list, #block-textsize-form > .content > .textsize_list > li {
      float: left;
  }

  #block-textsize-form > div.content > .textsize_current {
      float: right;
      left: -50px;
      position: relative;
      width: 120px;
  }

  #contentWrapper {
      background-color: #dedede;
      font-family: Helvetica;
      font-size: 10pt;
  }

  #header-images {
      height: 128px;
  }

  #pageBorder {
      left: 0px;
      position: absolute;
      top: 0px;
      width: 1018px;
  }

  .container_12 .grid_4 {
      width: 218px;
  }

  .container_12 .grid_8 {
      width: 750px;
  }

  .container_12 .pull_8 {
      left: -770px;
  }

  .container_12 .push_4 {
      left: 240px;
      top: 3px;
  }

  .forum-post-links {
      margin-right: -5px;
  }

  .forum-post-title {
      display: none;
  }

  .node-type-forum .clear {
      color: #C3C1C1;
      font-size: 8pt;
  }

  .page-tracker h1#page-title {
      display: none;
  }

  .sidebar ul.menu {
      margin-left: -30px;
  }

  .topic-reply-locked {
      float: left;
  }

  a.active {
      text-decoration: none;
  }

  a.bb-url {
      color: #5873fc;
      text-decoration: none;
  }

  a.permalink {
      text-decoration: none;
  }

  a.username {
      color: #c277a8;
      word-wrap: break-word;
      font-size: 14pt;
  }

  blockquote.quote-msg.quote-nest-1.odd {
      margin-bottom: 5px;
      margin-left: 4px;
      margin-right: 15px;
      margin-top: 10px;
  }

  body.node-type-forum .forum-post .forum-post-panel-main img {
      max-width: 812px;
  }

  body.node-type-forum blockquote.quote-msg p {
      margin-bottom: 0px;
  }

  body.node-type-forum div.author-signature {
      background-color: #edeeff;
      color: #c2c0c2;
      font-size: 8pt;
      line-height: inherit;
  }

  body.node-type-forum div.author-signature span, .node-type-forum .clear img.bb-image {
      max-height: 32px;
  }

  body.node-type-forum div.forum-post-content {
      font-family: Verdana;
      font-size: 10pt;
      line-height: inherit;
  }

  body.node-type-forum div.forum-post-wrapper {
      overflow: hidden;
  }

  body.node-type-forum embed[type="application/x-shockwave-flash"] {
      height: 360px;
      width: 640px;
  }

  body.node-type-forum h2.title.comment-form {
      clear: both;
  }

  body.node-type-news div.field-item.even {
      font-size: 11pt;
      line-height: inherit;
  }

  body.page-forum a.feed-icon, body.page-node a.feed-icon {
      float: right;
  }

  body.page-forum th.forum-last-post {
      width: 300px;
  }

  body.page-forum th.forum-topics, body.page-forum th.forum-posts {
      width: 50px;
  }

  body.page-node .teaser-content img {
      max-width: 652px;
  }

  div#block-privatemsg-privatemsg-new {
      margin-left: 20px;
      position: relative;
      z-index: 20;
  }

  div.field-item.even p {
      margin-bottom: 15px;
      margin-left: 6px;
      margin-right: 10px;
      margin-top: 15px;
      font-size: 11pt;
  }

  div.field-item.even p a {
      color: #5873fc;
      text-decoration: none;
  }

  div.forum-name a {
      text-decoration: none;
  }

  div.forum-post-content {
      color: #616161;
  }

  div.forum-post-footer.clearfix {
      background-color: #f0f0f0;
  }

  div.forum-post-panel-main.clearfix {
      background-color: #ebebeb;
  }

  div.forum-post-panel-sub {
      background-color: #f0f0f0;
  }

  div.forum-subforums a {
      text-decoration: none;
  }

  div.forum-table-description {
      font-family: Helvetica;
      font-size: 16px;
  }

  div.forum-table-name a {
      text-decoration: none;
  }

  img.slide {
      height: 128px;
  }

  img[src~="http://missingworldsmedia.com/images/favicon.ico"] {
      -moz-box-sizing: border-box;
      background: url(http://www.ascendantonline.com/files/Old_MWM_logo.png) no-repeat;
      box-sizing: border-box;
      display: block;
      padding-left: 32px;
      width: 32px;
  }

  li.comment-add.last {
      padding-left: 22px;
  }

  li.comment-delete.first a {
      text-decoration: none;
  }

  li.comment-edit a {
      text-decoration: none;
  }

  li.comment-reply a {
      text-decoration: none;
  }

  li.comment-reply.first a {
      text-decoration: none;
  }

  td.forum-last-reply a {
      font-size: 14pt;
      letter-spacing: 0;
      text-decoration: none;
  }

  td.forum-list-icon.forum-list-icon-default {
      background-color: #f0f0f0;
  }

  td.forum-list-icon.forum-list-icon-new-posts {
      background-color: #f6ffb5;
  }

  td.views-field.views-field-topic-icon {
      background-color: #f2f2f2;
  }

  textarea.text-full.form-textarea.required {
      font-family: Helvetica;
      font-size: 14px;
  }

  th.forum-name {
      width: 280px;
  }

  th.forum-posts {
      width: 38px;
  }

  th.forum-topics {
      width: 38px;
  }

  ul.pager {
      float: right;
      text-align: right;
      width: 560px;
  }

  span.forum-topic-title {
      font-size: 14pt;
  }

  .forum-table-superheader .forum-table-description {
      font-size: 11pt;
  }

  ul.pager > li.pager-current {
      padding: 4px 4px !important;
  }

  #page div.content-inner {
      background-color: #cccccc;
  }

  div#breadcrumb {
      background-color: #cccccc !important;
  }

  div#content {
      background: #dcdcdc !important;
  }

  div#forum-topic-header {padding: 4px;}


  ol#crumbs {
      font-size: 110%;
      padding: 5px 0px;
  }
  
  @media only screen and (max-width: 768px) and (min-width: 581px)
  {
    .container {
      width: 95%;
    }
  }
  
  @media only screen and (max-width: 1024px) and (min-width: 769px)
  {
    .container {
      width: 95%;
    }
  }
  
  @media only screen and (max-width: 580px) and (min-width: 321px)
  {
    .at-mt .at-menu-toggle ul.menu li a {
        background: #3270c5;
        border-bottom: 0px solid #fff;
    }
  }
  
}
