css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("experts-exchange.com") {

.allZones,.startFreeTrial, [id^="relatedSolutions"],.ontopBanner, #pageRight,
[class^="bbg bbgQuestion"] .answers, [id^="compSignUpNow"],.signUpCorp,
[class="c cnh"], table.h,[class^="s sectionFour"],table.f,.adSense {display:none!important}

div.bbg, div.bc,div.bl,div.br {padding:0!important;margin:0!important;background:none!important}

div.bbg div.bbg {
border:1px dashed #999!important;width:914px!important;padding:10px!important;margin:0!important;
font-family:Verdana!important;font-size:12px!important
}

div[class="bc bcQuestion"] {
width:904px; padding:15px!important;margin:5px 0 0 0!important;border:1px dashed #999!important;
font-family:Verdana!important;
}

div[class="bc bcQuestion"] > h3+h3 {display:none!important}
h1.titleHeader {color:#7D868F!important;font-weight:normal!important;font-size:22px!important}

#pageHeader {height:17px!important;overflow:hidden!important;width:17px;opacity:0!important}
#pageHeader:hover {height:auto!important;width:auto!important;opacity:1!important;border-bottom:1px solid #aaa}
#breadCrumb {width:100%!important;float:right!important;margin:-16px 0 3px 0!important}
#breadCrumb::before {content: "\25BC ";font-size:11px;font-weight:bold;
padding:2px;color:#666;-moz-border-radius:2px;}
}
