@-moz-document domain(mail.yahoo.co.jp) {


body {margin-top: 10px !important;}

/* Remove Text Underline */
a:focus {outline: none !important;}
a:link, a:visited, a:active, a:hover, #checkall, #clearall {text-decoration: none !important;}

/* Smart Gray Colorã€€*/
#contentbuttonbartop.contentbuttonbar {background: none !important;}
#globalbuttonbartop.globalbuttonbar {background: #CCC !important;}
#first {background: #EEE !important;}

/* Dark Text Color */
.content a, #first a, #addfoldercontrol, #personalfoldersdisplaytoggle, #ygma a, #topnav a, #checkall, #clearall
{color: #3F3F3F !important;} .switchbox a, #mailtab a {color: #FFF !important;}
.switchbox a{color: #FFF !important;}

/* List Header*/
#subjectheader {width: 42% !important;}
#recipientheader {width: 18% !important;}
th[width="15%"] {width: 13% !important;}
#senderheader, #recipientheader, #subjectheader, th[width="15%"]
{font-size: 10px !important;}

/* Bevel Button OS Default */
button {border: 0px !important;
background: #FFF !important;}

/* Remove Element */
#uhdMain, /*Universal Header */
object, embed, /* Flash */
table[id="jpwelcomeyminfo"],
div[id="nwbox"],
