css:
@-moz-document domain("disqus.com"), domain("www.gossipcop.com"), domain("blog.disqus.com"), domain("twitchy.com") {
a.card-comment-truncated.truncate-line {
    color: #1f59d0;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
    border-color: darkred  !important;
}

p {
    color: #7f919e;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bold;
}

.card-wrap {
    border: 5px  solid #494C4F;
    border-radius: 10px;
    background-color: black !important;

}

.nav__spacing {
    border-bottom: 0px solid #e0e3e9;
}

ul.nav--no-aside {
    border-top: 0px solid #e0e3e9;
}
.card__reason:first-child {
    border-bottom: 5px  solid #494C4F;
}

.curtain-truncate {
    border-top: 5px  solid #494C4F;
}

.aside__wrap {
    background: #000;
}

div.comment-body {
    border-left: 0px solid #2018BE !important;
}

a.view-comment.link-gray {
    box-shadow: 0 0 0 0px #ebeef2 inset !important;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bolder;
   
}

a.reply.link-gray {
    box-shadow: 0 0 0 0px #ebeef2 inset !important;
    font-family: "Comic Sans MS", cursive, sans-serif;
    font-weight: bolder;
}

a.nav-lnk.-color-muted.truncate-line, #container-page, #container-main, .card__content.-notification, .compensation, .nav-lnk.-color-muted, .form__header, .form__content, .form__footer, .panel:after, .row-fluid, div#container-main div.section-expanded.sidebar-fixed > div > div, .card, header.module-header.text-center, div.empty-message, .card__content.-empty, .card__content.-network, .text-medium {
    background-color: black !important;
}

.nav__item {
    border: 0px solid #e0e3e9;
}

.button-inverted {
    border-color: #000;
    }

.textarea-wrapper .placeholder {
        color: #0726C8;
}

.postbox .post-actions {
        background: #2F3132 !important;
    }

.postbox .post-actions .btn {
        background-color: #242121;
}

.no-touch .postbox .post-actions .btn:hover, .no-touchevents .postbox .post-actions .btn:hover {
    background: #242121;
    }

a:hover, a:hover *:not(img) {
    color: #FFE900 !important;
        }

#disqus_thread {
       padding-right: 0px;
    padding-left: 0px;
}

.text-gray, .postbox .textarea-wrapper .textarea {
    color: gold;
    font-weight: bolder;
    font-size: 16px !important;
}

.postbox .textarea-wrapper .textarea {
    color:Darkred !important;
}

.card-comment .comment-header a.time, a.bullet.link-discussion {
    color: gold !important;
}

.card--notification .card-comment .reply-disabled, .card__reason, .card__inner, .card__additional, .empty-message, .module-header {
    background-color: #151313 !important;
}

body.dark .input-wrapper input, body.dark .textarea-wrapper .textarea {
    border-color: #b92507 !important;
    border-radius: 4px;
    background-color: black !important;
    color: #7f919e !important;
}

.border-bottom {
    border-bottom: 1px solid #ebeef2;
    background-color: black;
}

.card {
    position: relative;
    border-bottom: 1px solid #ad1901;
    background-color: black;
}

.module__wrapper.is-disabled:before {
    opacity: 0;
    visibility: visible;
}

.card--notification .card-comment .view-comment, .card--notification .card-comment .reply, .card__reason:first-child {
    background-color: #151313 !important;
}

.cover {
    background: #fff;
    color: #546673;
    position: relative;
    border-bottom: 7px solid #ebeef2;
}

a.link-gray-darker.truncate {
    Color: Darkred !important;
}
    
div.card.card-profile.card-profile-post, .cover {
        border: 5px solid #494C4F;
}

.card-comment .comment-message, .icon-flag, .collapse span, .expand span, .nav-primary>ul>li.notification-menu a .notification-count, .alert, .alert a:active:hover, .alert a:hover, .alert a:visited:hover  {
    color: Darkred !important;
}

span.text-medium.spacing-left-small.hidden-lg {
    Background-color: transparent  !important;
}

.padding-default {
    padding: 10px;
    background-color: #1f1d1d;
}

.dark .load-more__button {
    background: rgba(41, 41, 41, 0.85) !important;
}

.post-content .post-message p, .post-message  {
    user-select: all !important;
}

.truncate-line, .truncate-line>p {
    display: block;
    white-space: normal;
    overflow: visible;
    text-overflow: ellipsis;
}


ul#post-list textarea:nth-child(n) {
    resize: visible !important;
    height: 364px !important;
}
}

@-moz-document domain("disqus.com"), regexp("www.aintitcool.com") {
h3, h4, strong, strong a {
    font-weight: 700;
    color: #bf0a0a;
}

.post-content header .parent-link, .post-content header .state-byline, .post-content header .time-ago {
    color: #095ac3 !important;
}

.nav-primary>ul>li.user-menu .dropdown-toggle .dropdown-toggle-wrapper .username {
    display: inline;
    color: #ecdc04;
}

.nav .dropdown-toggle {
    color: rgba(247, 16, 16, 0.5)!important;
}

span.text {
    color: #e4d509!important;
}

span.vote-down.count-0 {
    color: red !important;
}

.post-content footer .voting .vote-up {
    padding-left: 0;
    color: #23e60d;
}

.post-content .post-menu>li a {
    color: rgba(251, 251, 251, 0.5)!important;
}

.post-content .post-menu .dropdown-toggle {
    background-color: #191818;
}

.icon-comment:before {
   color: #dad7d7;
}

.nav-secondary>ul>li.recommend .thread-likes a .label-count {
   color: #23e60d;
}

*, .btn-block, .input-block-level {
    box-sizing: border-box;
    color: #8a8a8a;
}

#footer .logo a {
        background-color: #100f0f;
}

.discussion-page {
    background-color: #000;
    }

*, .btn-block, .input-block-level {
    color: #ab0000;
    
    
}

.header-bottom {
    background: #696799!important;
    border-bottom: 2px solid #000!important;
    border-bottom: 2px solid rgba(0,0,0,.8)!important;
    border-top: 0px solid #000!important;
}

#talkback div.box-content {
    padding: 5px 5px;
    background: black !important;
}

.article-content {
    font-size: 100%!important;
    line-height: 1.5;
    background-color: black;
    padding-left: 10px;
    padding-right: 10px;
}

.article-heading-left, .textarea-wrapper {
    background: black !important;
}

.box-content, .article-list .spoiler small {
    background-color: #000000 !important;
}

.article-heading {
    background-color: black !important;
}

.article-title, .article-metainfo {
    padding: 5px 5px;
}

ul#post-list textarea:nth-child(n) {
    resize: visible !important;
    height: 364px !important;
}
}
