css:
@-moz-document domain("zdfans.com") {
body{
    background: #161616;
    color: #838a9a;
  }
  .footer a, .meta-tit{
    color: #bbb;
  }
  .excerpt .note, .widget_bulletin li a, .widget_recently_viewed li a, .widget_links li a, .widget_archive li a, .widget_categories li a, .widget_pages li a, .widget_recent_entries li a, .info, .info a, .sidebar, .paging a, .paging .current, .pagenav a, .pagenav .current, .paging .dots, .pagenav .page-numbers, .meta-info,.friendlink a,.paging li a, .paging li span, .pagenav li a, .pagenav li span,#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-main .area-textarea-e .textarea-fw,#SOHUCS, #SOHUCS #SOHU_MAIN,#SOHUCS #SOHU_MAIN .section-newslist-w .title-name-w,#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-user-gw .user-time-gw,#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-user-gw .user-address-gw{
    color: #838a9a;
    transition: all 0.2s ease;
  }
	#SOHUCS #SOHU_MAIN .section-list-w .wrap-user-gw .user-1-4-gw i{
    color: #376a9d;
    transition: all 0.2s ease;
  }
  a{
    color: #27c;
    text-decoration: none !important;
  }
  a:hover,
  .info a:hover{
    color: #29f;
    text-shadow: 1px 1px 2px #000;
  }
  .widget_bulletin li a:hover, .widget_recently_viewed li a:hover, .widget_links li a:hover, .widget_archive li a:hover, .widget_categories li a:hover, .widget_pages li a:hover, .widget_recent_entries li a:hover{
    color: #eee;
  }
  p[style*="color: rgb(51, 56, 61)"],div[style*="color: rgb(51, 56, 61)"]{
    color:#838a9a !important
  }
  span[style*="color: rgb(0, 0, 128)"]{
    color:#79a !important
  }
	span[style*="color:#000000"]{
    color:#eef !important
  }
  .header{
    margin: 0;
    background-color: #244e99;
    box-shadow: 0 2px 4px #000;
  }
  .menu-item:hover a,
  .menu-item a:hover,
  .current-menu-item a,
  .current_page_item a,
  .current-post-ancestor a,
  .sub-menu{
    background-color: #244e99;
  }
  .sub-menu{
    padding: 0;
    box-shadow: 1px 1px 3px #000;
  }
  .sub-menu li a{
    transition: all .3s ease;
		border-color:transparent !important;
  }
  .sub-menu li a:hover{
		border: 0;
		padding-left: 18px;
		background-color: #29f;
  }
  #hasfixed{
    margin: 0;
  }
  .wrapper{
    background-color: #222;
    border: 0;
    box-shadow: 1px 1px 4px #000;
    padding: 60px 8px 16px;
  }
  .content-wrap,
  .content,
  .footer{
    background: none !important;
    color: #838a9a;
    border: 0;
    box-shadow: none;
  }
  .content{
    margin-right: 400px;
  }
  .column2{
    padding: 0;
  }
  .content .excerpt li{
    background-color: #2e2e2e;
    border: 0;
    margin: 10px 5px;
    padding: 5px 8px 10px;
    box-shadow: 1px 1px 3px #111;
    border-radius: 3px;
  }
  .excerpt li:hover{
    background-color: #2e2e2e;
  }
  .excerpt .pic{
    padding: 0;
    border: 0;
    margin: 7px 12px 7px 5px;
    box-shadow: 1px 1px 2px #111;
  }
  .widget_bulletin, .widget_d_theme, .widget_d_comment, .widget_recently_viewed, .widget_links, .widget_meta, .widget_tag_cloud, .widget_archive, .widget_categories, .widget_pages, .widget_d_hotpost, .widget_calendar, .widget_recent_entries, .widget_train_get{
    background-color: #2e2e2e;
    border: 0;
    margin: 7px;
    box-shadow: 1px 1px 2px #111;
    border-radius: 3px;
  }
  .widget_tit{
    border: 0;
    color: #bbb;
    padding: 0;
    font-size: 17px;
  }
  .meta{
    border: 0;
    padding: 0;
  }
  .search-submit{
    background-color: #245ea9;
    box-shadow: 0 0 2px #111,inset 0 0 0 #000;
    transition: all 0.2s ease;
  }
  .search-submit:hover{
    background-color: #1a4e8f;
    box-shadow: 0 0 0 #000,inset 0 0 2px #111;
  }
  .search-input,.search-input:focus,
  #comment-author-info input,
  #comment{
    background-color: #161616;
    color: #838a9a;
    box-shadow: inset 1px 1px 4px #000;
  }
  #comment-author-info p,#comment-author-info p:hover, #comment-author-info p.on{
    color: #838a9a;
    box-shadow: none;
    border: 0;
  }
  #comment-author-info label,
  .post-area,
  .comment-editor,
  #smileys{
    border: 0;
    box-shadow: none;
    background: none;
  }
  .post-related{
    background: none;
    border: 0;
  }
  .post-related a{
    width: 150px;
  }
  .post-related a img{
    border: 0;
    padding: 0;
    box-shadow: 1px 1px 2px #111;
  }
  #postcomments{
    margin: 16px 0;
  }
  .c-main p{
    color: #838a9a;
  }
  .comment{
    margin: 10px;
    padding: 15px 20px 15px 56px;
    border-radius: 3px 3px 6px 3px;
  }
  .base-tit, .quicker h3{
    border: 0;
    color: #838a9a;
  }
  .depth-1.odd,
  .depth-1{
    border: 0 !important;
    background-color: #262626;
    box-shadow: 1px 1px 2px #111;
  }
  .depth-2, .depth-4, .depth-6, .depth-8{
    border: 0 !important;
    background-color: #2e2e2e;
    box-shadow: 1px 1px 2px #111;
  }
  .depth-3, .depth-5, .depth-7, .depth-9{
    border: 0 !important;
    background-color: #262626;
    box-shadow: 1px 1px 2px #111;
  }
  .c-avatar{
    margin-left: -46px; 
  }
  #myrecentcomments .comment{
    background-color: #2e2e2e !important;
    border: 0 !important;
    margin: 10px 0 !important;
    box-shadow: 1px 1px 2px #111 !important;
  }
  #myrecentcomments .comment .comment-content{
    background: none !important;
  }
  .paging a, .paging .current, .pagenav a, .pagenav .current, .paging .dots, .pagenav .page-numbers,.paging li a, .paging li span, .pagenav li a, .pagenav li span{
    border: 0;
    background: #333;
    box-shadow: 1px 1px 2px #111;
    transition: all 0.25s ease;
  }
  .paging .current, .paging a:hover, .pagenav .current, .pagenav a:hover{
    background: #444;
    box-shadow: 1px 1px 1px #000;
    font-weight: normal;
  }
  .btn{
    box-shadow: 1px 1px 3px #111;
    vertical-align: middle;
  }
	#SOHUCS #SOHU_MAIN .module-cmt-list .block-cont-gw{
		border:0;
		background-color:#262626;
		padding:16px 20px;
		margin-bottom:12px;
		border-radius: 0 8px 8px 8px;
		box-shadow: 1px 1px 2px #111;
	}
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw .build-floor-gw{
		border:0;
		background-color:#2e2e2e;
		padding:16px 20px;
		margin-bottom:12px;
		border-radius: 0 8px 8px 8px;
		box-shadow: 1px 1px 2px #111;
	}
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw > .build-floor-gw > .build-floor-gw,
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw,
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw,
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw,
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw > .build-floor-gw{
		background-color:#262626;
	}
	#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-action-gw .action-click-gw .gap-line-gw{
		background-color: #222;
	}
	#SOHUCS #SOHU_MAIN .module-hot-topic{
		display:none	
	}
}
