css:
@-moz-document domain(habitica.com) {

/* make "code" style purple background with regular font */

code {
    font-family: lato, sans-serif;
    color: #fff;
    background-color: #402971;
}
/* make streak text and icon dark purple*/

.glyphicon-forward,
ng-form > div.task-meta-controls > span.ng-binding {
    color: #402971;
}
/* make option buttons on tasks opaque */

.task-meta-controls {
    opacity: 1.0!important;
}
/* change checklist progress background to green if complete */

.badge.ng-binding.ng-scope.badge-success {
    background-color: #47a447;
}
/* change checklist progress background to red if incomplete */

.badge {
    background-color: #da5353;
}
/* hide graph and "send to top" buttons */

.glyphicon-open,
.glyphicon-signal {
    display: none;
}
/* hide party chat icon */

.toolbar-notifs {
    display: none !important;
}
}
