css:
/* i really want this to be global */
body {
    background-color: #fff;
    line-height: 125%;
}
hr {
    height: 0px;
    border: none;
}
strong,
b {
    font-weight: normal;
}
/* ----- OUTSIDE OF FORUM BORDER ----- */

.bodyline {
    background-color: #fff;
    border: none;
}
table {
    display: table;
    border-collapse: collapse;
    border: none;
}
font,
th,
td,
p {
    font-family: Helvetica Neue, Helvetica, sans-serif;
}
.forumline {
    background-color: #fff;
    border: none;
}
/* TEXT */

.gen,
.genmed,
.gensmall {
    color: #666;
}
.gen {
    color: #666;
    font-size: 18px;
    font-weight: 200;
}
.genmed {
    color: #666;
    font-size: 16px;
    font-weight: 200;
}
.gensmall {
    color: #666;
    font-size: 14px;
    font-weight: 100;
}
a.forumlink {
    text-decoration: none;
    color: #333;
}
.forumlink {
    font-weight: normal;
    font-size: 22px;
    color: #333;
}
.nav {
    font-weight: 200;
    font-size: 14px;
    color: #333;
}
.topictitle,
h1,
h2 {
    font-weight: 200;
    font-size: 22px;
    color: #333;
}
a.topictitle:link {
    text-decoration: none;
    color: #333;
}
.postdetails {
    font-size: 14px;
    color: #999;
    font-family: helvetica neue;
    font-weight: normal;
}
.postbody {
    font-size: 18px;
    line-height: 25px !important;
}
.name {
    font-size: 14px;
    color: #666;
}
.quote {
    font-family: Helvetica neue;
    font-size: 14px;
    color: #666;
    background-color: #f9f9f9;
    padding: 20px;
    border: none;
}
/* TABLE HEAD */

th {
    color: #444;
    font-size: 24px;
    font-weight: 200;
    background-image: none;
}
th.thHead,
th.thSides,
th.thTop,
th.thLeft,
th.thRight,
th.thBottom,
th.thCornerL,
th.thCornerR {
    font-weight: 200;
    padding: 10px;
    border-bottom: none;
    background-color: #eee;
    text-align: left;
}
th.thCornerL {
    text-align: left;
}
td.catLeft {
    border: none;
    background-color: #fff;
    background-image: none;
}
.cattitle {
    font-weight: 200;
    font-size: 18px;
    letter-spacing: 1px;
    color: #303030;
}
/* TABLE ROWS */

td.row1,
td.row2,
td.row3,
td.row3Right,
td.spaceRow {
    background-color: #fff;
    padding: 10px;
    border-bottom: 1px dotted #ddd;
}
td.spaceRow {
    border: none;
}
td.row2 {
    padding-left: 10px;
}
td.rowpic {
    background-image: none;
    background-color: #f9f9f9;
    border-bottom: none;
}
td.cat,
td.catHead,
td.catSides,
td.catLeft,
td.catRight,
td.catBottom {
    padding: 5px;
    background-image: none;
    background-color: #f9f9f9;
    border-bottom: none;
}
/* POSTS */

.postbody {
    font-size: 18px;
    line-height: 18px;
}
/* FORMS */

input,
textarea,
select {
    color: #666;
    font: normal 14px Helvetica Neue, sans-serif !important;
    border: 1px solid #ccc !important;
    padding: 10px !important;
    border-color: #ddd !important;
    border-radius: 2px !important;
}
input.mainoption {
    background-color: #f9f9f9;
    font-weight: 200;
}
