body {
    background: url('http://www.easyfreepatterns.com/patterns/208/pixels-background-masterpost-208910.png') rgb(0, 0, 0) fixed center !important;
    font-family:'Segoe Ui Semibold',Open Sans,Lucida Grande, Helvetica Neue !important;
    font-size:10pt;
}

/* author post avatar fixed */
div.author.fixedd {
    top:60px;
}
    

/* arriba mierda */
div.post_header{
    background: rgba(189,238,193,.1) url(http://api.thumbr.it/whitenoise-361x370.png?background=01656300&noise=626262&density=31&opacity=8) !important;
}

/* boton para volver arriba */
#arribamier  {
    margin-top:50px !important;
    background: rgba(1,100,100,0.5) !important;
    box-shadow:0px 2px 0px rgba(1,100,100,1);
    padding:5px;
    border-radius:4px;
}
#arribamier:hover {
    background: rgba(1,100,100,.9) !important;
    box-shadow:0px 2px 0px rgba(1,100,100,0.8);
}

/* fin del boton qliao inutil */

/* responsive mode */
div.warper_menu.opened a{
    font-size:11px !important;
    text-transform:uppercase;
    text-shadow:0px 1px 0px gray !important;
}
/* responsive mode end */

#navigation {
    background-image:none !important;
    border-radius:4px !important;
    background-color: rgba(400,799,350,0.1);
    border:1px solid rgba(0,60,450,0.2);
    box-shadow:0px 0px 10px rgba(0,60,450,0.1);
}
#sidebar {
    background-image:none !important;
    width:355px;
    border-radius:4px;
}

.container {
    border-radius: 2px;
    background: rgba(100,195,224,0.1) !important;
    box-shadow: 0px 0px 5px rgba(153,139,135,0.8);
}

.post_header {
    border-radius:3px !important;
    background: rgba(0,9,0,0.3) !important;
    
}
.post_wrap {
    border-radius: 2px;
    background: rgba(78,70,97,0.3) !important;
    box-shadow: 0px 0px 4px rgba(80,47,59,0.8);
}

p.dsq-widget-meta {
    color: yellow !important;
    margin-left:5px;
}
.dsq-widget-list {
    list-style-image: url('https://cdn1.iconfinder.com/data/icons/silk2/bullet_go.png') !important;
    font-size: 9pt !important;
    margin-left:5px !important;
}
li.dsq-widget-item {
    margin: 0px !important;
    margin-right: 33px !important;
}
#nav_next {
    background: url(http://icons.iconarchive.com/icons/icojam/blue-bits/24/arrow-right-icon.png) no-repeat center black !important;
    box-shadow: 0px 0px 15px #666 !important;
}
#nav_pre {
    background: url(http://icons.iconarchive.com/icons/icojam/blue-bits/24/arrow-left-icon.png) no-repeat center black !important;
    box-shadow: 0px 0px 15px #666 !important;
}

.widget_body .dsq-widget-list {
    padding-left: 15px;

}

.widget_sidebar .widget_title {
	border-radius: 3px !important;
    background: rgba(6,13,63,0.3) url(http://api.thumbr.it/whitenoise-361x370.png?background=01656300&noise=626262&density=31&opacity=8);
}
.widget_title h2 {
    font-size:12px;
    text-shadow:0px 1px 4px #3B8686 !important;
}


/* avatar */
.author_url {
    text-transform:uppercase;
    font-size:8px !important;
    border-radius:2px !important;
    background-color:#3B8686 !important;
    color:#FFF !important;
    text-shadow:0px 1px 0px #000;
    box-shadow:0px 2px 0px #0B486B !important;
    margin-bottom:10px;
}

.author_url:hover {
    text-transform:uppercase;
    font-size:8px !important;
    border-radius:2px !important;
    background-color:#0B486B !important;
    color:#FFF !important;
    text-shadow:0px 1px 0px #000;
    box-shadow:0px 2px 0px #3B8686 !important;
    margin-bottom:10px;
}
.author_img {
    background-image:none;
    
}

.author_img img {
    border-radius:50%;
    border: 3px solid #3B8686;
    box-shadow:0px 2px 0px #0B486B;
    width:70px;
}

header a.logo img {
    margin-top:65px;
    margin-bottom:-4px;
}
/* fondo del menu */
.header_menu {
    text-transform: uppercase !important;
    width: 100% !important;
    height: 50px !important;
    padding: 10px 5px 0px 0px !important;
    font-weight: bold !important;
    color: #FFF !important;
    background-size: 250px;
    position: fixed !important;
    background-color: #111 !important;
    z-index: 9999 !important;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#000000+0,000000+100&0+0,0.65+100 */
    
    background: -moz-linear-gradient(top, rgba(0, 0, 0, 0.8) 15%, rgba(0, 0, 0, 0.7) 100%) !important;
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0.8) 15%, rgba(0, 0, 0, 0.7) 100%) !important;
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0.8) 15%, rgba(0, 0, 0, 0.7) 100%) !important;
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}


/* tamaño del menu superior*/
.header_menu #nav {
    width:100%;
    height:100%;

}
.header_menu #nav:after{
    content : '';
}

/* boton url menu modificado */
.header_menu li {
    color: #A8DBA8 !important;
    text-shadow: 0px 1px 2px #875 !important;
    background: url(http://i.imgur.com/9n7dJIr.png) #3B8686;
    box-shadow: 0px 2px 0px #0B486B;
    margin-right:10px;
    margin-bottom:5px;
    border-radius:3px;
}

.header_menu li:hover {
  color: #CFF09E !important;
    text-shadow: 0px 1px 2px #875 !important;
    background: url(http://i.imgur.com/9n7dJIr.png) #7FC7AF;
    box-shadow: 0px 2px 0px #3B8686;
    margin-right:10px;
    margin-bottom:5px;
    border-radius:3px;
    
}

/* Remove basura de por ahi version 2 */

div#advpini.hidden_tablets,div.site_ads,div.site_ads.hidden_tablets,div.site_ads_bottom.hidden_tablets{
    /* En jaidefiinichon esta como "block !important" por lo tanto el parametro tiene preferencia... lo borramos con un simple !important de vuelta <3 */
    display:none !important;
    visibility:hidden !important;
}
}
