css:
@-moz-document regexp("^https?://www\\.google\\.[a-z\\\\.]+/\\_/chrome/newtab.*") {
html,
#spch {
    background-image: url("https://i.imgur.com/khPLggH.jpg");
    background-repeat: no-repeat;
    background-position: right bottom;
    background-size: cover;
    background-attachment: fixed;
}
._gjb #spchi {
    color: white!important;
    text-shadow: 0 0 10px #000, 0 0 8px #000, 0 0 4px #ddd!important;
}
._gjb #spchta {
    color: #C7DCFC!important;
    text-shadow: 0 0 10px #000, 0 0 8px #000, 0 0 4px #ddd!important;
}
body {
    overflow: hidden !important;
    background: transparent!important;
}
.mv-hide {
    transition-property: background, background-color, transform, padding, padding-top, padding-bottom, padding-left, padding-right, margin, border, border-color, opacity, color, font-size, width, height, max-height, max-width, position, top, bottom, reft, right, padding, margin;
    transition-duration: 0.7s;
    animation: fade 0.5s;
}
form#f {
    animation: openVert 1s !important;
}
#fkbx {
    display: none !important;
    opacity: 0 !important;
}
#fkbx:hover {
    display: none !important;
    transition: all 5s ease;
    opacity: 1 !important;
}
#lga {
    visibility: hidden;
    margin-top: -100px !important;
}
#mv-tiles,
.mv-tiles-old {
    opacity: 0 !important;
    margin-top: 180px !important;
}
#mv-tiles:hover,
.mv-tiles-old:hover {
    transition: all 2s ease;
    opacity: 1 !important;
    margin-top: 180px !important;
}
}
