css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.madmoizelle.com") {
/* 2B2E31 */
  body {
    background: #0a0a0a !important;
    background-repeat: repeat!important;
    background-position: 50% 50%!important;
    height: 100%!important;
  }
  
  /* Header */
  .site-header,
  .head_pc {
    background: #000 !important;
  }
  .site-container,
  div.nav-primary-mini {
    background: #212121 !important;
  }
  
  .site-inner {
    box-shadow: 0px 0px 60px rgba(255, 255, 255, 0.42);
  }
  
  .article_banner,
  .site-inner
  /*, .sidebar, .widget_content_dark */{
    background: #212121 !important;
    color: #ccc !important;
  }
  .content, .article_intro, .hot_txt, blockquote {
    background: #1e1e1e !important;
    color: #ccc !important;
  }
  .content {
    box-shadow: 3px 0px 40px rgba(255, 255, 255, 0.10);
  }
  .hot_txt {
    box-shadow: 3px 0px 40px rgba(255, 255, 255, 0.10);
  }
  
  /* ----- Menu gauche ----- */
  /*.submenus {
    background: #2b2e31 !important;
  }*/
  .menu-item > a {
    color: #ccc !important;
  }
  .sous-menu, .sous-menu a {
    background: #282b2e !important;
    color: #ddd !important;
  }
  .sous-menu li:hover,
  .sous-menu li a:hover {
    background: #62c7c2 !important;
    color: #222 !important;
  }
   
  /* Accueil */
 
  /* ----- Articles ----- */
  h2,
  .home_today_title,
  .article_con h2.chapo,
  .hub_sonar .swiper-slide a {
    color: #c0c5d0 !important;
  }
  /* Bottom */
  img[src="https://static.mmzstatic.com/wp-content/themes/madmoizelle/assets/img/author_mask.png"] {
    display: none !important;
  }
  p.alire {
    background: #353535 !important;
    border-left: 1px solid #e4e6e9;
  }
  .cash-box,
  .article_legend {
    border: 1px solid #101010 !important;
    background: #303030 !important;
  }
  /* Right panel */
  .actu-links,
  .pr30, .p130, .pb10 {
    color: #c0c5d0 !important;
  }
  .quote {
    border-top: 1px solid #b2c3d4 !important;
    border-bottom: 1px solid #b2c3d4 !important;
  }
  .box {
    background: #353535 !important;
  }  
}
