css:
@-moz-document domain("m.weibo.cn") {
.icon,
.home-sub-nav > [data-action="discover"],
.card9 header .media-main:before
{
    display: none;
}
#box
{
    width: 47rem;
    margin: 0 auto;
    padding: 0 3rem;
    background-color: #fafafa;
    border: 1px solid #d8d8d8;
    display: block !important;
}
.card9,
.card25,
.comment-list,
.mod-pagination
{
    background-color: #fafafa !important;
    border-width: 0 0 0.16rem !important;
    border-style: dotted !important;
    box-shadow: none !important;
}
.card9 >:not(header)
{
    width: 41rem;
    margin-left: auto;
}
.size-xs
{
    width: 4rem;
    margin: 2rem 0 0 !important;
}
.card-list > div > footer
{
    margin: 0 0 0 auto;
    width: 18rem;
    border-width: 0;
}
.card-list > div > header > div > a > span
{
    font-size: 1.2rem;
    line-height: 1.4rem;
}
body,
.extend-content
{
    background-color: #eee;
}
.extend-content
{
    border: 1px solid #d8d8d8;
}
.txt-xl
{
    line-height: 1.6rem;
}
/*.topBarWrap
{
    width: 400px;
    top: 10px;
    left: 10px;
}*/

.module-topbar,
.home-sub-nav
{
    left: calc(50% - 25rem - 1.5rem);
    width: 53rem;
}
.home-sub-nav
{
    box-shadow: none;
}
.module-publisher
{
    left: calc(50% - 25rem);
    width: 44rem;
    padding: 0 3rem;
    background-color: #fafafa;
    border: 1px solid #d8d8d8;
}
.J-slider
{
    background-color: rgba(0, 0, 0, 0.6);
    position: fixed !important;
    z-index: 10;
}
#h5_page_wrap
{
    margin: 0 auto;
    width: 53rem;
}
.ux-popmenu > .content.show
{
    left: calc(50% - 5rem);
    top: 6rem;
    width: 10rem;
    box-shadow: none;
    border: none;
    height: 13rem;
    padding: 0.6rem;
    border-radius: 2%;
}
}
