css:
@-moz-document domain("www.corazondemelon.es") {

body {
   font:90% Calibri;
   color:#777777;
}

span {
   color:#000000;
}

span a {
   color:#000000;
}

h1, h2, h3, h4, h5 {
   font-family:"century gothic" !important;
   text-transform:uppercase;
   letter-spacing:0,5px;
   color:black!important;
}

h2 .firstword, .integrated-title2 .firstword {
   font:11px Calibri;
   color:black;
   font-style:normal;
   text-transform:uppercase;
   letter-spacing:1px;
}

.integrated-title1, .integrated-title2, .integrated-title3, #current-episode h2 {
   font-family:Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   font-style:oblique;
}

.integrated-title1 {
   color:black!important;
}

.integrated-title2 {
   color:black!important;
}

.integrated-title3 {
   color:black!important;
}

#right-informations, #counter, .number, .firstword {
   font:14px "century gothic"!important;
   letter-spacing:-0.5px;
   color:black!important;
}

.firstword {
   margin-right:16px; 
}

#container {
    background: url(http://img15.deviantart.net/e9ae/i/2016/193/d/4/amrcdmfondo4_by_chuscaro-da9r6hv.png) no-repeat fixed center top !important;
    background-size: cover!important;
}

#container.connected h2 {
   font-family:"century gothic";
   margin:12px 6px;
   border-bottom:1px dashed WhiteSmoke;
   padding-bottom:4px;
}

#news-list .news .date {
   float:left;
   width:59px;
   height:28px;
   background-color:#000000;
   border-radius:5px;
   background-image:none;
   color:#fff;
   font:bold 14px Calibri;
   padding:6px 0px 0px 10px;
}

.text a {
  color:#000000!important;
}

#avatarGirl {
    padding-left: 55px!important;
}

#container.connected #header { background-image : url(http://orig13.deviantart.net/8894/f/2016/193/b/0/amrcdmbarra_by_chuscaro-da9rlnn.png) !important ;
}

#connexion_landing label {
    display: none;
}

#connexion_landing .connexion_ok {
    opacity: 0;
}

#cadre_ext_body.disconnected {
    background: none;
}

#logged-deco a {
    background-image: url(http://orig04.deviantart.net/30a9/f/2016/193/f/c/amrcdmlogout_by_chuscaro-da9rmo6.png)!important;
}

#logged-money {
    color: Silver;
    left: 175px;
    top: 42px;
    line-height: 15px;
    height: 15px;
    width: 130px;
    text-transform: uppercase;
    font: 11px Serif;
}

#logged-pa, #logged-pa .actionpoints {
    color: Silver;
    left: 175px;
    top: 65px;
    line-height: 15px;
    height: 15px;
    width: 130px;
    text-transform: uppercase;
    font: 11px Serif;
}

#logged-moral {
    color: Silver;
    left: 175px;
    top: 97px;
    line-height: 15px;
    height: 15px;
    width: 130px;
    text-transform: uppercase;
    font: 11px Serif;
}
    
#header a:hover {
    font-style:oblique;
}

#logged-pseudo a {
   color:WhiteSmoke;
   font:bold 14px Serif;
}

#logged-pseudo span {
   float:left;
   color:Silver;
   text-shadow:none;
   font:bold 14px Serif;
}

#logged-messages a {
    color: Silver!important;
    text-shadow: none;
}

#logged-replays a {
    color: Silver!important;
    text-shadow: none;
}
    
#logged-episode {
   padding:2px 4px;
   color:Silver;
   text-shadow:none;
   font:11px Serif;
   text-transform:uppercase;
}

#astrological-sign a {
    color: WhiteSmoke;
    font:oblique 11px calibri, serif;
    text-shadow: 2px 2px 2px #000000;
    letter-spacing:1px;
    text-decoration:none;
}

.idcard-member { background : url(http://orig10.deviantart.net/2f47/f/2016/194/e/4/amrlcdmid2_by_chuscaro-da9v9zc.gif) no-repeat !important ;
}

.idcard-member span {
   color:WhiteSmoke;
}

body {
    cursor: url(http://orig08.deviantart.net/d271/f/2016/193/f/a/amrdmcursor_by_chuscaro-da9rvfi.png), auto !important;
}

a, a:hover {
    cursor: url(http://orig08.deviantart.net/d271/f/2016/193/f/a/amrdmcursor_by_chuscaro-da9rvfi.png), auto !important;
}

#cadre {
    background: rgba(255, 255, 255, 0.6)!important;
    border: none;
    box-shadow: none!important;
}

#body, #cadre-top {
    background: none!important;
}

#body:before, #body:after {
    border-color: transparent!important;
}

#menu_landing {
    display: none
}
    
.sm0,.sm1, .sm2, .sm3, .sm4, .sm5 {
   opacity:0;
}

#sous-menu {
   width:100%;
   height:40px;
   position:relative;
   margin-top:0px;
   background:url(http://orig14.deviantart.net/0003/f/2016/193/f/d/amrcdmopciones_by_chuscaro-da9s0s1.png) no-repeat scroll top center transparent;
}

#discussion table {
    border: 1px solid black;
    background: rgba(255,255,255,0.5)!important;
}

#discussion table .author_avatar {
    border: 1px solid black;
    background: Silver;
}

#discussion table tr {
    border-bottom: 1px solid black;
}

#discussion table .author_pseudo {
    font-weight: bold;
    font-family: "century gothic", serif;
    color: black;
}

#discussion table tr.read_0 {
    border: 1px solid black;
    background: WhiteSmoke!important;
}

#discussion table tr.read_0 .author_avatar {
    background: DimGray;
}

#discussion span {
   color:maroon;
}

#discussion .warning p:nth-of-type(1) {
   text-align:center;
}

#discussion .message_bloc .type-0 {
    background: Gainsboro;
    color: black;
}

#discussion .message_bloc .type-1 {
    background: LightSteelBlue;
    color: black;
}

#discussion .message_bloc .delete-option {
   opacity:0;
   position:absolute;
   bottom:1px;
   right:138px;
   cursor:pointer;
   transition:opacity .3s;
   -webkit-transition:opacity .3s;
}

#discussion .message_bloc .delete-option span, #discussion .message_bloc .report-option span {
   transition:color .3s;
   -webkit-transition:color .3s;
   color:#000;
   font:88% Calibri;
   text-transform:uppercase;
   letter-spacing:1px;
   padding:1px 6px;
}

input.submit-input {
    font-family: "century gothic", serif;
    background: black;
    border: black;
    color: #fff;
}

#profile .player-name .summary {
    text-align: center;
    font-size: 14px;
    text-shadow: none;
    color: black;
    letter-spacing: 1px;
}

#profile div#right {
    background-color: rgba(255,255,255,0.8)!important;
    margin: 60px 0 0 65px;
    padding-left: 260px;
    min-height: 500px;
}

#profile div#actions {
    background: rgba(0, 0, 0, 0.5) top left no-repeat;
    height: 130px;
    margin-top: 20px;
    left: 1px;
}

#profile div#avatarProfil {
    border: 2px whitesmoke solid;
    background-color: rgba(0, 0, 0, 0.5)!important;
}

#profile div#avatarProfil {
    background: none, center bottom no-repeat;
    border: 2px whitesmoke solid;
}

.idcard .value {
    border-radius: 5px
}

#current-episode h2 {
    font: 15px Serif;
    color: black;
}

h3, .integrated-title3 {
    font-family: georgia, sans-serif;
    color: black;
}

#current-episode li.completed {
    font-family: "century gothic", sans-serif;
}

.episode-progression .progress-left {
    position: absolute;
    background: url(http://orig15.deviantart.net/67e7/f/2016/177/e/c/tumblr_inline_o6dpyn9huu1u5w4b5_5402_by_chuscaro-da7rc6a.png)!important;
    width: 5px;
    height: 14px;
    left: -5px;
}

.episode-progression .progress-inner {
    position: absolute;
    background: url(http://orig15.deviantart.net/e409/f/2016/177/7/f/tumblr_inline_o6dpyo5mfw1u5w4b5_5402_by_chuscaro-da7rcan.png)!important;
    height: 14px;
    left: 0px;
    right: 0px;
}

.episode-progression .progress-right {
    position: absolute;
    background: url(http://orig08.deviantart.net/a076/f/2016/177/1/0/tumblr_inline_o6dpyoe1lp1u5w4b5_5402_by_chuscaro-da7rcng.png)!important;
    width: 5px;
    height: 14px;
    right: -5;
    z-index: 9999;
}

.episode-progression .playcount {
    background: black;
    border-radius: 25px;
    width: 23px;
    height: 23px;
    color: #ffffff;
    text-align: center;
    font-weight: bold;
    -webkit-transform: rotate(-7deg);
    float: left;
    padding-top: 3px;
    margin-right: 12px;
}

#objectives .description {
    margin-top: 6px;
    text-align: justify;
    font-family: "century gothic", sans-serif;
    color: black
}

#minigame {
  padding-left:36px;
}

#minigame h1 {
  text-align:center;
}

p.description a.auto-button-pink {
  margin:10px 10px 10px 36px;
}

#challenge-list-body td.center {
    font-style: normal;
    color: whitesmoke;
}

#mortalpillow thead tr * {
	background: whitesmoke!important;
    text-align: center;
}

.list-ranking .bold {
    font-weight: bold;
    color: whitesmoke;
}

center, dl, dt, dd, ol, ul, li, fieldset, form {
    color: white;
}

#mortalpillow table td, #mortalpillow table th {
    color: black;
}

.final_bonus {
    background-color: silver;
}

div#bgames {
    display: none;
}

div#bbflags {
    display: none;
}

#copy1, #copy1 a {
    font-size: 11px;
    color: #696969;
    font-family: arial;
    text-decoration: none;
}

#copy2 {
    background: rgba(255,255,255,0.5)!important;
    border-radius: 5px
}

.boyfriend .lom span {
    background-color: DarkGray;
    color: #000;
    text-shadow: 1px 1px #c0c0c0;
    text-transform: uppercase;
    font: 10px Serif;
    letter-spacing: 1px;
    padding: 4px 6px;
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
}

.boyfriend.crush {
    border: solid 1px firebrick;
    border-radius: 5px;
}

.boyfriend .description {
	color: black
}

.stamp {
    color: #fff!important;
    text-shadow: 1px 1px #a9a9a9;
    border-color: red!important;
}

.friend .player-name a {
    color: black;
    font-family: "century gothic", sans-serif;
    margin-bottom: 2px;
    display: block;
}

.friend .episode-number {
    display: none;
}

.pagination a.page {
    background-color: #000;
    color: white;
}

#forum thead {
    border: none;
    border-bottom: 0;
    background: LightSlateGray;
}

#forum tbody {
    background: DarkGray;
    border: none;
    border-top: 0;
}

#forum .forum_post {
    border: 1px solid DarkGray;
    background: rgba(255,255,255,0.8)!important;
}

.inbox thead tr {
    color: LightSlateGray;
}

legend, table, caption, tbody, tfoot, thead, tr, th, td {
    font-family: "century gothic", sans-serif;
    color: #ffffff;
    font-weight: bold;
}

.inbox thead th {
    color: #ffffff;
    font: serif;
    padding: 2px;
    text-align: center;
    background: LightSlateGray;
}

.pun_topic_page .col2, .pun_topic_page .col3 {
    text-align: center;
    background: DarkGray;
}

.pun_topic_page .col2.sticked_col, .pun_topic_page .col3.sticked_col {
    background: none;
}

.pun_topic_page .col2, .pun_topic_page .col3 {
    background: none;
}

#forum .pun.box.group1>h2, #forum .pun.box>.inbox.group1 .post_right .infos {
    background: black;
}

#forum .pun.box>.inbox.group1 div.author_bottom, #forum .pun.box>.inbox.group1 div.author {
    background: black;
}

#forum .pun.forum_post>h2>span, #forum .pun.forum_post>h2>a {
    color: #ffffff;
    font-family: "century gothic", sans-serif;
}

.pun h2 span.numbering, .pun h2 span.date {
    font-family: "century gothic", sans-serif;
    color: Gainsboro;
}

#forum .pun.box>.inbox .author .author_name .summary, #forum .pun.box>.inbox .author .author_name strong {
    text-align: center;
    color: Gainsboro;
    font-size: 14px;
    font-weight: bold;
}

#forum .pun.box>.inbox .author .author_title strong {
    text-align: center;
    color: Gainsboro;
}

#forum .pun.box>.inbox .author .author_infos div {
