#forum .pun.box>.inbox.group1 .post_left, #forum .pun.box>.inbox.group1 .username, #forum .pun.box>.inbox.group1, #forum .pun.box>.inbox.group1 div.post, #forum .pun.box>.inbox.group1 div.post_bottom {
    background: rgba(255,255,255,0.3)!important;
}

#forum .pun.box>.inbox .post_bottom {
    background-color: SlateGray
}

#forum .pun.box>.inbox .post {
    padding: 2px 5px;
    border-left: solid SlateGray 167px;
}

#forum .pun.box>.inbox .author {
    width: 167px;
    float: left;
    background-color: SlateGray;
    margin-top: -20px;
    padding: 5px;
}

#forum .pun.forum_post>h2 {
    background-color: SlateGray;
}

#forum .pun.box>.inbox .author_bottom p {
    padding-left: 5px;
