    padding-left: 5px;
    font-size: 11px;
    background-color: black;
}

#forum .pun.box>.inbox .author .author_infos {
    margin-top: 5px;
    display: block;
    border-top: solid white;
    font-size: 11px;
}

#forum .pun.box>.inbox.group1 .post {
    padding: 2px 5px;
    border-left: solid black 167px;
}

#forum .pun.box>.inbox.group2 .author_bottom p {
    padding-left: 5px;
    font-size: 11px;
    background-color: CadetBlue;
}

#forum .pun.box>.inbox.group2 .post {
    padding: 2px 5px;
    border-left: solid CadetBlue 167px;
}

#forum .pun.box>.inbox.group2 .post {
    border-left: solid white 167px;
}

#forum .pun.box.group2 {
