    background: rgba(255,255,255,0.3)!important;
}

#forum .pun.box.group2>h2, #forum .pun.box>.inbox.group2 .post_right .infos {
    background: CadetBlue;
}

#forum .pun.box>.inbox.group2 div.author_bottom, #forum .pun.box>.inbox.group2 div.author {
    background: CadetBlue;
}

#bank-container #bank-other-countries {
    background-color: Gainsboro;
}

#bank-container #bank-selection li.active {
    background: Gainsboro;
    border: Gainsboro solid 2px;
    border-right: none;
}

#bank-container #bank-payment div[class^=payment-method] {
    border: solid 2px Gainsboro;
    background: rgba(255, 255, 255, 0.3)!important;
}

#bank-container #bank-payment div[class^=payment-method] table.bank-in-game-values tr.bank-in-game-value.select td:first-child {
    border-left: 2px solid black;
    border-radius: 10px 0 0 10px;
    border-right: hidden;

}

#bank-container #bank-payment div[class^=payment-method] table.bank-in-game-values tr.bank-in-game-value.select td:last-child {
    border-right: 2px solid black;
    border-radius: 0 10px 10px 0;
    border-left: hidden;
}

#bank-container #bank-payment div[class^=payment-method] table.bank-in-game-values tr.bank-in-game-value.select td {
    border-top: 2px solid black;
    border-bottom: 2px solid black;
}

#bank-container #bank-payment div[class^=payment-method] table.bank-in-game-values tr.bank-in-game-value.select {
    border-top: 2px solid Gainsboro;
